new GadgetDescription { Id = Guid.Parse("f94bbdf1-756c-4847-b4ee-9732d33165a5"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Laptop Acer Swift với bộ ba công nghệ tiên tiến bao gồm Acer Purified Voice, Acer TrueHarmony và DTS:X ULTRA, mang đến trải nghiệm âm thanh tuyệt vời cho người dùng. Bạn sẽ được thưởng thức âm nhạc, video và phim ưa thích của mình ở chất lượng tốt nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7ec8dc71-e1e1-4343-b63c-34a1d350a1fa"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Chiếc laptop cao cấp - sang trọng này không chỉ tinh tế, mà còn sở hữu hiệu suất mạnh mẽ nhờ bộ vi xử lý Intel Core Ultra 7 155H tích hợp công nghệ AI mới nhất. Điều này giúp chiếc máy có khả năng xử lý tốt các tác vụ liên quan đến đồ họa, render video 3D, thiết kế banner, poster 2D chuyên sâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("9d08a1f5-c5c9-4439-8dc2-2e4075fef2c8"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Ngoài ra, chip còn đạt chuẩn Intel Evo hỗ trợ máy sạc nhanh, thời gian sử dụng pin lâu, khởi động máy cũng nhanh hơn,... Hơn nữa, card đồ họa Intel Arc Graphics cung cấp sự hỗ trợ đáng tin cậy cho việc học tập và làm việc của bạn, giúp bạn chơi game ổn định hơn với cấu hình cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("3d2e21b7-0bb4-467c-835e-921ff5614611"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Với bộ nhớ RAM 16 GB LPDDR5X (Onboard) có tốc độ Bus lên đến 6400 MHz, chiếc laptop này đáp ứng tốt nhu cầu đa nhiệm, cho phép bạn sử dụng các ứng dụng đòi hỏi nhiều tài nguyên một cách hiệu quả. Bạn sẽ có thể lướt web và nghe nhạc mà không gặp phải tình trạng lag giật, khó chịu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("7e2b1b36-638c-4088-8fbe-3438dd7f4d5a"), GadgetId = Guid.Parse("69627779-0251-4114-ad2c-fdb80af9ccbf"), Value = "Laptop này sử dụng ổ cứng SSD NVMe PCIe dung lượng 512 GB, giúp máy khởi động nhanh chóng và truy cập dữ liệu một cách trơn tru. Bạn sẽ có đủ không gian để lưu trữ các tài liệu cá nhân quan trọng. Ngoài ra, chiếc máy này cũng hỗ trợ nâng cấp tối đa lên 2 TB, sẵn sàng đáp ứng nhu cầu lưu trữ lớn của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("6b7ea601-c8cc-46a6-9893-ab8db646f09f"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Tận hưởng những trải nghiệm giải trí tuyệt vời cùng bạn bè chỉ với một mức giá vô cùng hấp dẫn tại Thế Giới Di Động. Laptop Acer Gaming Nitro V ANV15 51 58AN i5 13420H (NH.QNASV.001) không chỉ hấp dẫn game thủ bởi vẻ ngoài cá tính, mạnh mẽ mà còn bởi bộ cấu hình vượt trội cùng đa dạng tiện ích hỗ trợ cho quá trình chơi game.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("af27b86a-7b23-4afb-9d6f-065e7398f3dc"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Trải nghiệm chơi game đỉnh cao nhờ vào bộ vi xử lý Intel Core i5 13420H mạnh mẽ và card rời NVIDIA GeForce RTX 2050 4 GB, cho phép người chơi thưởng thức hấp dẫn hàng loạt con game Esports nổi tiếng với cài đặt đồ họa từ trung bình đến cao. Cấu hình mạnh mẽ này cũng hỗ trợ xuất sắc cho việc chỉnh sửa hình ảnh, video, render 2D, 3D và lập trình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("2e0bfbb9-d39e-42ad-a05b-a271d7627e11"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7ec147e6-892d-4b1e-9ec5-74ef0c3467da.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("8cd73bc8-315e-41db-b5ec-4c13d79c3b2f"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Với RAM 8 GB DDR5, laptop Acer xử lý đa nhiệm một cách mượt mà và tăng tốc các tác vụ nhanh chóng. Người dùng còn có thể nâng cấp lên đến 32 GB để cải thiện hiệu suất làm việc với phần mềm, game đòi hỏi cấu hình cao. Ổ cứng SSD 512 GB NVMe PCIe giúp tăng tốc độ đọc và ghi dữ liệu, mang lại không gian lưu trữ rộng rãi cho mọi dữ liệu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("8f1a2552-e035-44f6-a6c1-7c313434936e"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Với thiết kế mới lạ, Gaming Nitro V nổi bật với các đường cắt góc cạnh và biểu tượng hoạ tiết kiểu Cyberpunk nổi bật, mang lại vẻ ngoài cá tính và độc đáo hơn hẳn so với phiên bản Acer Gaming Nitro trước đây. Chất liệu nhựa cứng màu đen được chế tác tỉ mỉ, đảm bảo độ bền và đàn hồi cao cho toàn bộ cấu trúc của máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("42239042-b772-4f8c-8c6d-28857fad5ded"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Ngoài ra, khối lượng 2.1 kg cũng không quá nặng so với các mẫu máy gaming kích thước 15.6 inch hiện tại, dễ dàng bỏ gọn trong túi chống sốc và mang theo bên mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
