new GadgetDescription { Id = Guid.Parse("9dedb5fe-f20e-4dc6-883b-12aab1232501"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Màn hình laptop gaming kích thước 15.6 inch mang lại tầm nhìn rộng lớn, đồng thời cung cấp không gian làm việc thoải mái khi mở được nhiều cửa sổ cùng lúc. Độ phân giải Full HD (1920 x 1080) đảm bảo hình ảnh hiển thị rõ ràng và màu sắc sống động, ngay cả trong điều kiện ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("67962b13-e0d2-4a32-9af7-dfd351199822"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Tận hưởng trải nghiệm chơi game không gián đoạn với màn hình IPS hỗ trợ tần số quét 144 Hz, giúp đồng bộ hóa tốc độ làm mới màn hình với tốc độ khung hình từ GPU, giảm thiểu độ trễ, tránh tình trạng giật hình, xé hình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("9c036ac3-d99e-448c-b615-20608781f203"), GadgetId = Guid.Parse("ceddf841-fead-4437-b454-66fcbd78bd76"), Value = "Hệ thống âm thanh tiên tiến trên laptop Acer Nitro bao gồm Acer Purified Voice, Acer TrueHarmony và DTS:X ULTRA mang lại chất âm chân thực, sắc nét, tái tạo chính xác các hiệu ứng âm thanh trong game, truyền tải âm nhạc một cách sống động, nâng cao trải nghiệm giải trí của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("6d4f5eba-4daa-4c41-b145-3296ede2ebc8"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Đánh bật nhiều đối thủ trong cùng phân khúc laptop gaming với sự xuất hiện của con chip Intel Gen 12 đầy mạnh mẽ, laptop Acer Nitro 5 Tiger AN515 58 773Y i7 (NH.QFKSV.001) hứa hẹn sẽ là cánh tay phải đắc lực cho các game thủ oanh tạc trên mọi chiến trường.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
