new GadgetDescription { Id = Guid.Parse("c44ee790-73a2-4547-bc92-4b54f5fe5626"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Laptop RTX 30 series với card NVIDIA GeForce RTX 3050Ti 4 GB, khẳng định nguồn sức mạnh vô tận không chỉ đáp ứng tốt nhu cầu chiến game mà còn hoàn thành xuất sắc nhiệm vụ của một chiếc laptop đồ họa - kỹ thuật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
