new GadgetDescription { Id = Guid.Parse("7b288444-5229-4b24-bff9-eec40035dfab"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Tần số quét 144 Hz cho các chuyển động ảnh sắc nét, màu sắc chuẩn xác, chân thật trên từng chi tiết, hạn chế tối đa hiện tượng bị xé hình khi chơi các tựa game có tốc độ nhanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("ea038748-4da5-4222-8cf9-b08ed45df606"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Cho cảm giác chơi game sống động và hấp dẫn hơn bao giờ hết với chất lượng âm thanh to rõ, dải âm rộng, độ khuếch tán cao cùng khả năng tái tạo âm thanh gần giống thực tế nhờ sự kết hợp giữa công nghệ Acer TrueHarmony và DTS X:Ultra Audio.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("79b84973-f782-47d7-9ac5-351d713c6094"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/543c3be7-7022-4a08-80cf-ea2ca728a748.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a927ee83-1f89-460f-b0ca-d84e07a6699e"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Phong cách thiết kế có phần lịch lãm hơn nhờ các chi tiết viền xanh đỏ được cắt xéo trên mặt lưng máy nhưng vẫn không mất chất hầm hố vốn có của một chiếc laptop gaming. Khối lượng 2.5 kg vẫn có thể đặt vào balo và cùng bạn chiến game khắp mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("0b762a4d-0fd7-4fda-90ab-8b2022d1e7fb"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Cổng USB, HDMI, Jack tai nghe 3.5 mm, LAN (RJ45) và USB Type-C được trang bị trên laptop cho phép người dùng dễ dàng kết nối với các thiết bị ngoại vi khác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e8dd0d9a-7b41-4fd5-8ea7-c2ed824725a7"), GadgetId = Guid.Parse("0228bd2b-5b5e-4071-b7b3-061c39e5f008"), Value = "Laptop Acer Nitro sở hữu đèn bàn phím chuyển màu RGB mang đến cảm giác phấn khích hơn mỗi khi lao vào trận chiến. Thao tác gõ phím đã tay hơn nhờ hành trình phím sâu và độ nảy tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("87bd0f32-8597-42bc-9258-1b79f5a0f79c"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Mẫu laptop gaming với mức giá tầm trung đến từ thương hiệu Acer vừa được lên kệ tại Thế Giới Di Động, sở hữu hiệu năng mạnh mẽ với con chip Intel Gen 13 dòng H hiệu năng cao, RAM 16 GB, card rời RTX cùng nhiều tính năng hiện đại. Laptop Acer Aspire 5 Gaming A515 58GM 51LB i5 13420H (NX.KQ4SV.002) chắc chắn sẽ mang đến cho bạn những trải nghiệm sử dụng và chiến game giải trí tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4fc01fc6-e8b9-4279-b8c8-e8b221341aaa"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Cấu hình chiến mọi tựa game \"HOT HIT\" ", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("9b43a8db-7fca-41c9-b0be-0c8aa4e5f7ba"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Những mẫu laptop Aspire Gaming chắc chắn đã quá quen thuộc với anh em nhờ hiệu năng mạnh mẽ nhưng lại được gắn với mác giá vô cùng hợp lý. Với mẫu Acer Aspire 5 Gaming vào năm 2023 này chắc chắn sẽ không làm mọi người thất vọng với cũng giá đó nhưng hiệu năng có phần được nâng lên nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("1daf4177-3ba2-4886-a5b3-0407c96370db"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/237f55c5-245a-47c0-a627-48c376b8550f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("aaeab458-a906-48ef-bf1d-33041519edee"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Laptop được trang bị bộ vi xử lý Intel Core i5 Raptor Lake - 13420H cùng card rời NVIDIA GeForce RTX 2050 4 GB đa nhiệm hiệu quả cho mình mọi công việc trên cơ quan, học tập hay giải trí thường ngày đến việc thực hiện các bản thiết kế trên nền tảng Premiere, Photoshop,... tuy nhiên với các ấn phẩm nghệ thuật, đồ hoạ động quá nhiều layer hay effect thì mình đánh giá máy chưa đáp ứng được nhanh chóng, nếu chỉ sử dụng cho công việc thông thường thôi thì vẫn rất ok nha.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("3afcca7f-5ad0-4838-8c54-8e4efc0ed94f"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Một điểm mà chắc chắn mọi người cũng khá quan tâm trên mẫu máy này là khả năng chơi các tựa game. Theo như mình nhìn nhận thì tốc độ đáp ứng các tựa game Online/Esports như PUBG, LOL, Valorant, FO khá tốt, hầu hết mọi tựa game đều có mức FPS ổn định và chơi trong thời gian dài thì hiệu suất hoạt động vẫn được duy trì bền bỉ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("1fd2111f-642d-4520-bca0-70173b58e7c6"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Bộ nhớ RAM 16 GB cho phép mình mở được nhiều cửa sổ trình duyệt Chrome cùng lúc mà vẫn không có hiện tượng giật lag quá nhiều và em laptop Acer cũng hỗ trợ nâng cấp tối đa 32 GB, hoàn toàn giúp mình xử lý được khối lượng công việc nhiều hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("034588d8-23cc-4ad5-babb-feae7d39cf4b"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Ổ cứng SSD 512 GB NVMe PCIe bên cạnh việc giúp mình có thể dễ dàng truy xuất hay khởi động ứng dụng nhanh chóng thì còn mang đến không gian lưu trữ rộng lớn cho một sinh viên như mình. Đối với những bạn thường làm việc với các bản thiết kế hoặc tải nhiều tựa game thì máy còn hỗ trợ mở rộng không gian lưu trữ lên tối đa 1 TB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("167b9a0d-fd1a-4bbf-82c4-e1e9d09fcc86"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Giải trí đã mắt với không gian lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("7a88e69c-79be-458a-8786-f92589add1fe"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Màn hình cũng là một điểm mà mình cũng rất ưng trên mẫu Aspire 5 Gaming với không gian 15.6 inch rộng lớn cho mình làm việc trên nhiều cửa sổ cũng như thoải mái để chơi game. Độ phân giải Full HD (1920 x 1080) đảm bảo cho mọi hình ảnh đều được tái tạo sắc nét với màu sắc sống động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c00e95af-5f72-49ea-adb2-65f0683d3726"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0a760955-5c01-4883-9cb0-04d1ed6005f4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("116f0309-9af4-482a-b57d-de54931c1ab4"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Hơi tiếc một chút khi một chiếc màn hình trên laptop gaming mà chỉ sở hữu tần số quét 60 Hz nhưng các chuyển động nhân vật trong game đều được tái hiện chính xác, không hề bị nhoè mờ làm mình có cảm giác khó chịu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("912ff85f-4309-469b-8eaf-bd185d830ef1"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Cảm giác khi mình chơi game với chiếc màn hình này cùng khá thoải mái và rõ ràng. Bên cạnh đó, chất lượng âm thanh to rõ, âm trầm cũng khả năng tái tạo sống động của hệ thống loa cũng khiến cảm giác sử dụng của mình sống động và thú vị hơn rất nhiều.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("640d5515-472c-4775-ade7-b4d661a54176"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Cấu trúc mạnh mẽ, vẻ ngoài hiện đại", Type = ParseGadgetDescriptionType("BoldText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("83c1e8df-c1f5-4117-9260-9ae8666fb01d"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Không quá hầm hố hay quá nhiều chi tiết như những mẫu máy chiến game khác, đặc trưng của dòng Aspire Gaming là thiết kế được thể hiện ở mức đơn giản và tinh tế nhất. Logo máy được làm nhỏ lại và được đặt sát bên trên phần bề mặt lưng máy tạo cảm giác nhìn thoáng mắt trên tông màu xám. Phần bản lề máy được gia công khá kĩ nên mình hoàn toàn không nhận thấy có sự lỏng lẻo hay rung lắc khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("10ee15f4-ab4c-4af2-b0fa-246fc64e3ff0"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Phần bàn phím của máy sẽ có bố cục Full size khá phổ biến để mình có thể soạn thảo văn bản, chiến game trơn tru hay nhập các số liệu nhanh chóng hơn khi làm việc với các trang tính. Trải nghiệm gõ phím khá sướng tay với độ phản hồi nhanh, hành trình phím ổn, không tạo tiếng ồn quá lớn đến người xung quanh mỗi khi mình giao tranh. Phím cũng có hỗ trợ đèn nền để mình dễ dàng sử dụng hơn ngay cả khi thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("46741ab6-2214-463c-99ce-fcf894355b1e"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/26afb80b-f77a-4156-a531-b7dc0b65433e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("84c9a1ae-9ca2-4bb4-9f65-c96c22ff304f"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Touchpad của máy được làm tương đối vừa đủ cho mình để tay vào và điều khiển, tốc độ di vuốt cũng rất mượt mà mà không bị khựng lại giữa chừng. Tính năng bảo mật vân tay được tích hợp ngay trên bàn di chuột giúp mình mở máy dễ dàng hơn so với các kiểu mở máy truyền thống đồng thời cũng gia tăng độ bảo mật lên đáng kể.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("e604efe9-4f5c-44b8-98a1-4dfe0a1f75a7"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Một điểm cộng khá lớn là với việc là một mẫu laptop gaming nên máy được trang bị tương đối đầy đủ cổng giao tiếp như: HDMI, Jack tai nghe 3.5 mm, USB Type-A, USB Type-C và LAN vừa cung cấp hiệu suất truyền tải dữ liệu nhanh chóng vừa giúp mình liên kết với các thiết bị khác như chuột, bàn phím, màn hình rời,... thuận tiện mà không phải sắm thêm HUB kết nối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("6f47395f-6d84-4504-bcfd-e5584b3e92ac"), GadgetId = Guid.Parse("7c9a8814-a48c-44ad-9f9c-1228781d6cd4"), Value = "Về tổng thể, laptop Acer Aspire 5 Gaming A515 58GM 51LB i5 13420H (NX.KQ4SV.002) sở hữu hiệu năng được coi như là khá vượt trội trong phân khúc, cấu trúc thiết kế đơn giản, tính đa dụng cao lại đi kèm một mức giá thành ưu đãi. Mẫu laptop Acer Aspire này là sự lựa chọn không thể phù hợp hơn cho anh em học sinh, sinh viên vừa thoả mãn yêu cầu công việc lại vừa đáp ứng nhu cầu giải trí cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
