new GadgetDescription { Id = Guid.Parse("d15c0968-f66f-499f-aa92-4783c697e6dc"), GadgetId = Guid.Parse("db865d07-1b82-483d-81f4-2eb50451f12b"), Value = "Một điểm cộng nữa cho chiếc máy này là phần bàn phím được thiết kế với layout Full size, bố cục phím được sắp xếp hợp lý tạo cho mình cảm giác gõ khá thích tay, hơn nữa với đầy đủ các phím số còn khá thuận tiện với một đứa sinh viên kinh tế như mình bởi nhu cầu nhập số liệu khá cao. Mình hơi tiếc một chút khi máy không có đèn nền, nhưng khi sử dụng lâu dần thì tay cũng đã quen với mặt phím thì các thao tác nhấn, soạn thảo văn bản trong không gian tối với mình cũng đều không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7ba7b085-604d-4e19-9977-fa67089ef7d2"), GadgetId = Guid.Parse("db865d07-1b82-483d-81f4-2eb50451f12b"), Value = "Trái ngược với phần bàn phím thì mình cũng chưa có hài lòng lắm với TouchPad, bàn di chuột có độ lớn vừa phải, độ nhạy tốt cho cảm giác kéo thả tốt nhưng đối với khổ tay của mình thì sẽ hơi khó thao tác một chút, lâu lâu còn bị khựng lại gây nên cảm giác khá khó chịu. Mình vẫn khuyên nếu bạn nam nào có khổ tay hơi quá to như mình hoặc người dùng phải xử lý khối lượng công việc lớn thì vẫn nên sắm thêm chuột bên ngoài để có trải nghiệm tối ưu nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
