new GadgetDescription { Id = Guid.Parse("ffe2e7ea-db94-4729-bd2a-56424b0fa71c"), GadgetId = Guid.Parse("db865d07-1b82-483d-81f4-2eb50451f12b"), Value = "Âm thanh to rõ đến từ công nghệ stereo speakers cho mình trải nghiệm thế giới âm nhạc hay phim ảnh đầy thoải mái và sống động sau những giờ làm việc, học tập căng thẳng mệt mỏi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("76fc9ab1-7a56-4375-ab8e-ca0ae6bc40e0"), GadgetId = Guid.Parse("db865d07-1b82-483d-81f4-2eb50451f12b"), Value = "Với viên pin 50 Wh thì laptop trụ được đâu đó tầm 2 - 3 tiếng hoạt động, phù hợp với một buổi làm việc bên ngoài hay tiết học trên trường. Tuy nhiên thời lượng pin có thể sẽ thay đổi tùy thuộc theo tác vụ bạn sử dụng hay độ sáng màn hình nữa, nên mình khuyên các bạn vẫn nên mang theo sạc bên mình để có thể làm việc hiệu quả và ổn định nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("d6fda972-abc6-4d77-a9bc-5f153fe1dedd"), GadgetId = Guid.Parse("db865d07-1b82-483d-81f4-2eb50451f12b"), Value = "Về tổng thể, laptop Acer Aspire 5 A515 58P 34RJ i3 1315U (NX.KHJSV.003) với hiệu năng ổn định, phục vụ mượt mà mọi tác vụ thường ngày, thiết kế hiện đại đẹp mắt đi cùng với một mức giá phải chăng. Nếu bạn là sinh viên hay người đi làm đang tìm kiếm một thiết bị để đáp ứng mọi nhu cầu học tập và công việc thì đây chính xác là sự lựa chọn tuyệt vời dành cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("ad6f1166-28ff-49ad-aba7-8b92d801df66"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Bạn đang tìm kiếm một chiếc laptop đáp ứng tốt nhu cầu học tập, văn phòng và giải trí nhẹ nhàng? Laptop Acer Aspire Lite AL16 51P 596H i5 1235U (NX.KWZSV.002) là lựa chọn hoàn hảo dành cho bạn với hiệu năng mạnh mẽ, màn hình đẹp, thiết kế thanh lịch và giá cả hợp lý. Sự cân bằng hoàn hảo nhưng hiện tại đang có một mức giá vô cùng tốt tại Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1ae0a3a6-cf19-4786-96ca-b50ac479a08f"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Laptop Acer Aspire sử dụng vi xử lý Intel Core i5 1235U thế hệ 12 mạnh mẽ, xung nhịp tối đa lên đến 4.4 GHz, cùng card đồ họa Intel UHD Graphics, giúp bạn xử lý mượt mà mọi tác vụ từ cơ bản đến nâng cao như: soạn thảo văn bản, tính toán Excel, lập trình, chỉnh sửa ảnh, video đơn giản, chơi game nhẹ nhàng,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("5d679f1c-7c44-44b8-b928-41fd7735a2f7"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/d1c00f03-cf64-4edc-889d-f80912ff8e5d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("5340e65c-168e-4dc7-942d-cafba41acfa8"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Hơn nữa, với hiệu năng trên bạn hoàn toàn có thể chơi được nhiều các tựa game hot như Đấu Trường Chân Lý, Valorant, Minecraft,... với mức thiết lập cấu hình trung bình hoặc thấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("007126f7-bae2-4951-963f-e148f358614f"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "RAM 8 GB có hỗ trợ nâng cấp lên được 64 GB theo nhu cầu cho phép người dùng xử lý công việc thêm linh động, vừa nghe nhạc giải trí, vừa xử lý công việc với nhiều cửa sổ khác nhau. Ổ cứng SSD 512 GB dung lượng lớn giúp khởi động máy và truy xuất dữ liệu nhanh chóng, để lưu trữ hình ảnh, file sách nặng thì bạn hoàn toàn có thể tháo lắp và thay thế ổ khác có dung lượng đến 2 TB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("298d7978-6151-4ca2-ba62-7bc8206fa5ed"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a9a07ad9-9228-42b9-a47d-786b63885886.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("d8604d6d-1494-48f7-87a5-ce57d1d13214"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Màn hình 16 inch với độ phân giải Full HD (1920 x 1080) cho hình ảnh sắc nét, màu sắc chân thực. Công nghệ Acer ComfyView giúp giảm thiểu độ chói và ánh sáng xanh, bảo vệ mắt của bạn khi sử dụng máy trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("e7006b7e-5d2f-489f-b833-78d68d7013ec"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Laptop Acer có thiết kế thanh lịch với tông màu xám trung tính, vỏ ngoài bằng nhựa được hoàn thiện tốt. Cân nặng của thiết bị chỉ có 1.7 kg, mỏng 18.9 mm, nhét gọn trong túi chống sốc mà mang theo bên mình vô cùng dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("03d3ff96-d976-4954-956e-d6208733d85d"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/28795b96-2313-4696-8169-12ae246121f6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f4e01738-40b8-46a6-890b-174c9bc2cbba"), GadgetId = Guid.Parse("798a6781-4512-407d-937a-4294c17e23a7"), Value = "Thoải mái cho một chế độ làm việc từ xa trên laptop học tập - văn phòng với Full HD Webcam, hình ảnh trình chiếu của bạn sẽ luôn được rõ nét, hạn chế nhoè mờ cho hiệu quả công việc được cao nhất. Nhiều dạng cổng kết nối thông dụng ở 2 cạnh máy như: Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("388021ea-6a92-4e70-b8b3-711510bb776a"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Acer không ngừng gây ấn tượng với những dòng sản phẩm mới đáp ứng hoàn hảo nhu cầu khách hàng. Laptop Acer Aspire 5 A515 58M 79R7 i7 13620H (NX.KQ8SV.007) với cấu hình \"độc lạ\" bất ngờ nhưng vô cùng mạnh mẽ, RAM cơ bản cao 16 GB, ngoại hình đẹp mắt,... là những thông số vô cùng tốt để giải quyết các công việc phức tạp và nhu cầu giải trí đa phương tiện.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("680ef7c5-a985-4996-b313-11d8451fb10d"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Laptop được trang bị vi xử lý Intel Core i7 13620H thế hệ 13 mạnh mẽ, mang đến hiệu năng xử lý vượt trội, sẵn sàng đáp ứng mọi nhu cầu công việc và giải trí của bạn. Dù là xử lý các tác vụ văn phòng nặng nhọc, sáng tạo nội dung multimedia mượt mà hay chinh phục những tựa game đình đám như FO4, LOL,... máy cũng hoàn thành xuất sắc nhiệm vụ mà hạn chế gặp hiện tượng giật lag hay quá tải.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("367c06a3-2ab4-48a1-9345-ee4d5003d565"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e0b37382-dd37-4665-941b-00d524bbe3f8.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b1150609-2632-4f57-8e77-825dcda09e87"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Kết hợp hoàn hảo với card màn hình Intel UHD Graphics cho phép người dùng thỏa sức sáng tạo với các ứng dụng đồ họa chuyên nghiệp như Adobe Photoshop, Premiere, Lightroom,... hay đắm chìm trong thế giới game sống động với chất lượng hình ảnh sắc nét, mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("dd096674-8248-43cc-8edf-295caa8e95a4"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Bộ nhớ RAM 16 GB LPDDR5 (Onboard) dung lượng lớn đảm bảo khả năng đa nhiệm mượt mà, giúp bạn dễ dàng làm việc trên nhiều ứng dụng cùng lúc mà không lo bị gián đoạn. Chuyển đổi nhanh chóng giữa các tab Chrome, chỉnh sửa ảnh trên Photoshop hay xem phim đều diễn ra trơn tru, mượt mà, mang đến cho bạn trải nghiệm làm việc và giải trí hoàn hảo nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("4b1f16e3-7829-4bb2-a316-b4f44f1f4cdf"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5844ab59-acd7-4c9b-8c88-c45504301df4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("dfe9c0ac-a7a9-4df1-bc3c-df3292974172"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Ổ cứng SSD 512 GB NVMe PCIe mang đến tốc độ khởi động máy, mở ứng dụng và truy cập dữ liệu siêu nhanh, tiết kiệm tối đa thời gian chờ đợi. Dung lượng có thể mở rộng đến 1 TB cho phép bạn lưu trữ thoải mái mọi tệp tin học tập, văn phòng, hình ảnh, video và game yêu thích mà không lo thiếu hụt không gian.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ccba8020-100d-4922-8570-0c9dd80560f6"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Laptop Acer sở hữu màn hình kích thước 15.6 inch rộng rãi, viền mỏng mang đến không gian hiển thị tối ưu, cho bạn cảm giác như đang hòa mình vào thế giới hình ảnh. Độ phân giải Full HD (1920 x 1080) cho hình ảnh sắc nét, sống động với màu sắc chân thực, góc nhìn rộng mang đến trải nghiệm hình ảnh hoàn hảo nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("3fee4101-a80f-435c-81a9-9b527645b76b"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Công nghệ Acer Purified Voice tiên tiến cho phép bạn tùy chỉnh âm thanh theo sở thích, mang đến trải nghiệm âm nhạc sống động, chân thực như đang thưởng thức tại rạp hát. Âm thanh trong trẻo, rõ ràng, từng nốt trầm bổng đều được thể hiện chi tiết, giúp bạn đắm chìm trong thế giới âm nhạc và giải trí đầy cảm xúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("1f25a99b-38a7-48ac-a2f0-aaae4a148c88"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/391bb7ed-9c6e-4b1f-93d1-92eb9293730d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3eced518-f1ea-40c5-9a83-153de768c07b"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Laptop Acer Aspire sở hữu thiết kế hiện đại, mỏng nhẹ với khối lượng chỉ 1.7 kg, giúp bạn dễ dàng mang theo bên mình mọi lúc mọi nơi. Vỏ máy được làm từ chất liệu nhựa cao cấp, nắp lưng bằng kim loại cứng cáp, bề mặt phẳng và ít chi tiết mang lại vẻ ngoài sang trọng và thanh lịch.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("4cbc5fdc-202e-46f9-a70d-c75ef8d867f7"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Tính năng bảo mật hữu ích với mở khoá vân tay cho phép người dùng đăng nhập vào thiết bị cá nhân chỉ với một chạm đơn giản. Thuận tiện gõ phím ngay cả trong bóng tối với đèn nền bàn phím.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("838c3616-95e6-48ce-812d-d2854c75c428"), GadgetId = Guid.Parse("7b0ed27f-f691-4bf1-912d-33980b2116a7"), Value = "Laptop học tập - văn phòng hỗ trợ đầy đủ các cổng kết nối thông dụng như: Jack tai nghe 3.5 mm, USB 3.2, HDMI và USB Type-C giúp bạn dễ dàng kết nối với các thiết bị ngoại vi khác như chuột, bàn phím, ổ cứng di động, máy chiếu,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e575f5ee-be60-42a9-90a2-24b1b9a4019d"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Với cấu hình mạnh mẽ và thiết kế thanh lịch, Lenovo IdeaPad 1 15AMN7 R5 7520U (82VG0061VN) sẽ đáp ứng đầy đủ các tiêu chí khi bạn chọn mua laptop học tập - văn phòng. Đây cũng là mẫu laptop đầu tiên trang bị CPU AMD 7000 series mới nhất tại Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5280a59f-934d-4f4f-a4f4-6c629e3f8fd4"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Ngoại hình tinh tế, thanh lịch", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("14bc3ed7-bcd0-4179-8703-8fa2af7adc08"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Điểm đầu tiên mình ấn tượng bởi chiếc máy này chính là thiết kế không mang quá nhiều chi tiết nhưng toát lên được nét hiện đại và thanh lịch, logo được đặt vỏn vẹn ở góc máy giữ cho diện mạo thêm tinh tế cũng như mang những nét đặc trưng vốn có của dòng laptop Lenovo. Khối lượng 1.58 kg cho mình cảm giác cầm nắm đầm tay cũng như không quá nặng để có thể linh động mang theo phục vụ công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("07650a14-9de6-49b1-90bf-febdab9c33d7"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e5887a52-e1b6-43fe-80e9-53de20e7dad8.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("850a9a8c-8573-4b56-b314-8ada97f8eec9"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Lớp vỏ nhựa màu xám được hoàn thiện khá tốt, cho mình cảm giác mát tay khi sờ hay khi cầm nắm đều chắc chắn. Bên cạnh đó, vỏ được chế tác bằng nhựa nhưng máy vẫn đảm bảo được độ chắc chắn, khi mình ấn nhẹ xuống mặt lưng máy thì không gặp hiện tượng bị flex.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("30986b80-6930-417d-a48f-042f455d944b"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Lenovo IdeaPad 1 được trang bị hệ thống bàn phím Fullsize, có cả cụm phím số cho phép mình nhập liệu những file tính toán với những số liệu phức tạp. Cá nhân mình khi gõ phím khá thích tay khi các phím được bố trí thoáng, hành trình phím sâu và có độ nảy tốt, kể cả khi soạn thảo văn bản trong thời gian dài cũng không bị mỏi tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("44f3c398-1de3-4fc0-bdd2-eef47d410f1a"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Mình chỉ hơi tiếc một chút khi bàn phím không hỗ trợ đèn nền, do đó trong thời gian đầu khi mới làm quen với laptop sẽ hơi khó khăn một chút nếu sử dụng vào ban đêm, nhưng về sau khi đã gõ quen tay mình cũng không còn quá chú tâm đến việc có hay không đèn nền bàn phím.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("976ec50a-c9d0-4b6d-a46e-c83d533fcd2b"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Touch Pad có độ lớn vừa phải cùng độ nhạy khá tốt nên khi kéo thả hay di chuyển con trỏ để thao tác thì cho mình cảm giác suôn sẻ. Tuy nhiên, với cổ tay nam hơi quá to so với bàn di chuột nên đôi khi mình cũng bị khựng lại khi thao tác, các bạn cũng gặp những tình trạng như vậy thì có thể lưu ý sắm thêm chuột bên ngoài để phục vụ công việc tốt hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("7c29435a-1fd5-499b-99b5-9e47416d1f7a"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Các cổng kết nối của laptop thì cũng được trang bị khá đầy đủ với: USB 2.0, USB 3.2, HDMI, Jack 3.5 mm và USB Type-C. Bạn không cần phải quá lo lắng khi không có đầu chuyển đổi trong mỗi lần thuyết trình hay trong trường hợp phải ghép nối với các thiết bị khác nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("c595a862-148c-48ef-b7f2-087abb974b55"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Lenovo IdeaPad 1 cho mình cảm giác khá an tâm khi sử dụng nhờ các tính năng nổi bật được trang bị như công tắc khóa camera, cho phép mình đóng mở camera một cách thủ công, đảm bảo an toàn cho hình ảnh và thông tin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("b751b05c-5cc2-4c0e-b7c8-717da45849bb"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Cấu hình ổn định, giải quyết công việc hiệu quả", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("5d52f9a0-fa84-4855-8402-5bfddcdd926f"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "So với một chiếc laptop học tập - văn phòng trong cùng phân khúc thì máy được trang bị với những cấu hình khá mới mẻ. Bộ vi xử lý AMD Ryzen 5 7520U với thiết kế 4 nhân, 8 luồng cho tốc độ xung nhịp tối đa 4.3 GHz cùng card tích hợp AMD Radeon 610M Graphics giúp mình xử lý trơn tru các tác vụ học tập, văn phòng trên Office như Word, Excel, PowerPoint,... cũng như xem phim, lướt web và giải trí đều rất mượt mà. Hơn nữa, với cấu hình như trên thì khi mình thử chơi tựa game Liên Minh Huyền Thoại quốc dân ở mức cấu hình thấp thì khả năng đáp ứng của máy cũng rất tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7262f251-8969-4c49-8702-e29929ffc6bf"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/01e40d45-bc6c-4f57-84df-27315347fda1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d80e136a-0f62-4965-8292-da63d2a4e351"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Độ đa nhiệm của máy thì không còn gì phải bàn vì với thói quen để cùng lúc nhiều tab Chrome hay các phần mềm khác như Spotify, Word,... khi sử dụng của mình mà máy vẫn đáp ứng tốc độ chạy mượt mà, hạn chế gặp hiện tượng giật lag nhờ RAM 8 GB LPDDR5 (Onboard) có tốc độ Bus lên đến 5500 MHz.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("484c39b3-5ef0-4722-a7b5-c988888477c4"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Ổ cứng 256 GB SSD NVMe PCIe còn mang đến tốc độ mở máy hay truy cập các ứng dụng nhanh chóng, không gian lưu trữ tương đối. Với 256 GB, cá nhân mình vừa đủ để tải các file tài liệu, luận văn hay thậm chí là các bộ phim và video, nhưng nếu bạn có nhu cầu lưu trữ cao thì hoàn toàn có thể nâng cấp dung lượng lên tối đa 1 TB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
