new GadgetDescription { Id = Guid.Parse("d2a85d23-1e70-4aa1-9eb2-c1d148e1ddc6"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Lenovo IdeaPad 1 được trang bị màn hình với kích thước 15.6 inch cùng độ phân giải Full HD (1920 x 1080) và tần số quét 60 Hz, có thể nói các thông số của màn chỉ ở mức cơ bản nhưng vẫn đáp ứng tốt cho mình ở các nhu cầu cơ bản hằng ngày như xem phim giải trí, soạn thảo, học tập hay làm việc văn phòng trên máy,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("b65cd443-f4bb-4afa-930e-34ae903eb9b3"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/db51b08b-f77a-4cf5-abbe-46ba97c1a927.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("f6d60728-465f-4d7e-9ebb-30eaa8c55649"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Hơn nữa, thứ mình thích nhất ở laptop Lenovo IdeaPad có lẽ chính là phần bản lề được thiết kế có thể mở 180 độ, khá tiện lợi, chia sẻ được thông tin với ngay cả người ngồi đối diện khi họp nhóm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("5713f0ac-25e6-4d4f-9368-a27037d31175"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Công nghệ Dolby Audio cho phép tùy chỉnh các loại âm thanh để phù hợp nhiều loại hoạt động của bạn như nghe nhạc, xem phim hay chơi game. Chất âm có độ phân giải cùng tần số cao nên mình mỗi khi giải trí chỉ cần để ở mức 60% là đã có thể tận hưởng âm thanh sống động và chân thực rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("8d6c6c32-cf4a-46a7-b27c-10367b1e1af8"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Pin dung lượng 42 Wh không cao nhưng máy vẫn có thể vận hành trong khoảng 5 tiếng đối với các tác vụ văn phòng kết hợp cùng giải trí. Bản thân mình khi mang máy theo đi học hay công việc thời lượng pin vẫn đáp ứng khá đầy đủ, nhưng mình vẫn khuyên các bạn nên luôn mang theo củ sạc để có thể kết nối kịp thời, nhanh chóng cũng như hỗ trợ các công việc thêm hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("18ab5b01-1389-487b-8aaa-185f0f39e056"), GadgetId = Guid.Parse("a8e73830-2909-4b24-95e2-8eb278f3f14d"), Value = "Tổng thể, Lenovo IdeaPad 1 15AMN7 R5 7520U (82VG0061VN) sở hữu hiệu năng khá tốt và có phần nhỉnh hơn khi so sánh với dòng máy khác trong phân khúc. Hiệu năng ổn định, ngoại hình đơn giản nhưng không kém phần thanh lịch cùng đa dạng các tiện ích hấp dẫn như bản lề mở 180 độ, công tắc khóa camera,... tất cả đều xứng đáng với giá trị mà mình bỏ ra.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("311647b2-a840-4f6c-8959-1fbd42124ca9"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Laptop Lenovo Ideapad Slim 3 15IAH8 i5 12450H (83ER000EVN) một mẫu laptop học tập - văn phòng sở hữu cấu hình mạnh mẽ với con chip Intel dòng H, RAM 16 GB cùng đa dạng các công nghệ hiện đại, hứa hẹn sẽ mang đến cho người dùng những trải nghiệm sử dụng làm việc và giải trí đa phương tiện một cách tối ưu và đầy tiện nghi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4096b8af-6633-423c-9c35-28000acb57fd"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Đẹp mắt và thanh lịch hơn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("9c5dd76f-a722-4712-ad48-a6d8c1ee5efe"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Theo như đánh giá cá nhân của mình, khi nhìn vào những chiếc laptop Lenovo IdeaPad thì người dùng có thể sẽ không nhận ra quá nhiều những điểm khác biệt giữa các mẫu, tuy nhiên thiết kế kiểu truyền thống là thế nhưng lại không hề \"lối mòn\" một chút nào, những mẫu máy này vẫn khá thu hút và được ưa chuộng về kiểu dáng, đối với không chỉ các bạn trẻ Gen Z mà còn là những đối tượng người dùng đã đi làm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("ad74b1da-5f37-482e-92da-02ffabf9b93a"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Ideapad Slim 3 được hoàn thiện với lớp vỏ nhựa bền bỉ đạt độ bền chuẩn quân đội MIL STD 810H và được phủ một lớp sơn xám trầm, mặt lưng máy thì không được hoàn thiện với quá nhiều chi tiết, chỉ có logo hãng được khắc nhỏ ở góc. Mình khá thích những kiểu dáng tối giản như thế này, không gây quá nổi bật khi sử dụng ở chỗ đông người nhưng vẫn đảm bảo được tính thẩm mỹ cao và độ chuyên nghiệp khi sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("5927202b-b249-4d2f-8cac-a71694f16032"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/99c7e15e-652c-4dfc-88ee-f37716df006e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("bef2674b-ec65-492c-a0b0-96163116f37c"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Cân nặng của máy chỉ nhẹ 1.62 kg nên cảm giác cầm nắm rất gọn gàng, mình có thể dễ dàng cất máy vào balo để mang đi học, đi làm hay ra quán cafe chạy deadline mà không sợ cồng kềnh. Đặc biệt, bản lề có thể mở 180 độ, giúp mình linh hoạt hơn khi sử dụng, dễ dàng chia sẻ thông tin khi họp hay làm việc cùng mọi người.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("cd439496-bfba-4eff-95e9-6ecf4af2641c"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Bàn phím sở hữu kiểu dáng thiết kế Full size quen thuộc nên rất dễ dàng khi mình nhập số liệu để tính toán, hành trình phím sâu cùng độ nảy ổn định giúp cho việc gõ trong thời gian dài cũng không bị mỏi hay khó chịu tay. Mình cũng hơi tiếc một chút khi máy không hỗ trợ đèn nền nhưng mình thấy nếu gõ quen tay trong thời gian dài thì hoàn toàn tự tin sử dụng ngay cả khi thiếu sáng luôn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("86acf39d-3ad8-43a1-9900-421ec9af486f"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Touchpad mượt mà dễ dàng di chuyển quên đi cảm giác sử dụng chuột ngoài cho những tác vụ công việc văn phòng. Ngoài ra, Lenovo IdeaPad Slim 3 còn được tích hợp bảo mật vân tay lên nút nguồn, tạo sự an toàn tuyệt đối cho thiết bị, đồng thời giúp tăng khả năng truy cập vào máy nhanh hơn hoặc đăng nhập vào các tài khoản để thanh toán nhanh chóng, vô cùng tiện lợi so với nhập mật khẩu truyền thống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("cdc0c825-679c-4952-92aa-089c0eba2f09"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Đầy đủ các dạng cổng kết nối thông dụng từ: HDMI, Jack tai nghe 3.5 mm, USB 3.2, USB-C 3.2 và khe SD tạo điều kiện khá thuận lợi cho mình có thể xuất file thuyết trình sang màn hình ngoài, kết nối thêm với chuột ngoài hoặc liên kết với các thiết bị khác cũng nhanh chóng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
