new GadgetDescription { Id = Guid.Parse("f58a7b7c-a766-4f56-8a1d-e0d86d67e0fd"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Điểm khá phổ biến trên những chiếc Lenovo IdeaPad là màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080), cùng với viền màn hình được thu nhỏ tạo cảm giác góc nhìn được rộng hơn, tăng sự thích mắt hơn khi giải trí xem phim. Hình ảnh và màu sắc hiển thị trên màn hình đảm bảo được độ tươi sáng, tông màu đen, đầy đủ độ sâu khá nịnh mắt, chân thực với những trải nghiệm như xem phim, lướt web,…", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
