new GadgetDescription { Id = Guid.Parse("2983204c-212c-439c-8f85-6e7328426355"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Nhờ con chip Intel Core i5 12450H tăng xung nhịp tối đa lên đến 4.4 GHz cùng card tích hợp Intel UHD Graphics giúp mình hoàn toàn tự tin làm chủ mọi công việc từ xử lý văn bản tại trường học, cơ quan, tạo file thuyết trình hoặc thậm chí là một chút coding liên quan đến ngành học thì máy hoàn toàn đáp ứng khá tốt. Không những thế, mình cũng chơi thử ở một số tựa game hot như LOL hay Genshin Impact thì máy đáp ứng ở mức FPS khá tốt, giao tranh hay di chuyển đều mượt mà, tuy nhiên nếu chơi thử thì bạn cũng nên chỉ để ở mức cấu hình Medium thôi là tốt nhất rồi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("37d65212-2721-45bb-b33e-09cd56188aac"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b344baf2-ff5e-41a7-b481-197c98590b75.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("a252f5be-eb62-4110-b6ff-e1b39f63df2d"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Bộ nhớ RAM 16 GB chuẩn LPDDR5 trên laptop Lenovo giúp mình linh động khá cao trong mọi công việc, chỉnh sửa Photoshop cùng lúc với mở các tác vụ Chrome mà máy vẫn hoạt động trơn tru. Bộ nhớ SSD 512 GB NVMe PCIe 4.0 cho tốc độ phản hồi nhanh chóng khi khởi động máy hay mở ứng dụng đồng thời cũng tạo nên một kho lưu trữ vừa đủ cho bạn thỏa thích lưu các tệp thông tin dữ liệu học tập hay làm việc cần thiết. Ngoài ra, hãng cũng ưu ái thêm việc tháo lắp thanh khác tối đa 1 TB cho mình có thêm nhiều khoảng trống lưu trữ hoặc những bạn có nhu cầu sử dụng cao hơn đều thoải mái sử dụng mà không lo đầy bộ nhớ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("d50938b1-c3b2-4409-9c3b-8cfcb0fd5753"), GadgetId = Guid.Parse("98103ff4-2407-43b0-a913-6acf0b099991"), Value = "Nhìn tổng quan lại, laptop Lenovo Ideapad Slim 3 15IAH8 i5 12450H (83ER000EVN) đã thể hiện cực kì tốt mọi thông số so với mức giá thành của mình, không chỉ mang đến hiệu suất công việc cao hơn đáng kể, ngoại hình với thẩm mỹ tuyệt vời mà còn nhiều tính năng sử dụng khác nữa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("4f5e29d0-43f7-4a54-bcbf-40de1185b2a5"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Từng chi tiết của laptop Lenovo LOQ 15IAX9 i5 12450HX (83GS00DBVN) đều được thiết kế để chinh phục thế giới game thật hấp dẫn với sức mạnh vượt trội cùng đa tính năng hữu ích. Hãy tham khảo ngay mẫu laptop gaming với thiết kế độc đáo, lạ mắt này với mức giá lý tưởng ở kệ hàng Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("a23ba43b-df7e-4540-b3ff-8370dacfdddf"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Cấu tạo máy cực phong cách qua lớp vỏ nhựa màu xám, kiểu dáng đặc trưng với phần gù nhô ra trên những mẫu laptop gaming nhà Lenovo mang cảm hứng như những chiếc phi thuyền vũ trụ. Khối lượng 2.38 kg giúp bạn dễ dàng mang theo máy đến bất cứ đâu để luôn sẵn sàng cho những cuộc chiến mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("e80b0f57-e1a6-4927-afd0-2edc9829ea24"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6f3f5408-89dd-462c-8fe8-e82659075afe.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("cb5234a4-232d-4714-aeda-acdfcd373419"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Đèn nền đơn sắc trắng trên bàn phím sẽ là trợ thủ đắc lực trong những màn đêm game đầy thử thách, đảm bảo rằng game thủ luôn có thể chiến đấu hiệu quả ngay cả trong điều kiện ánh sáng yếu. Sự bố trí hợp lý của cổng kết nối như: LAN (RJ45), USB 3.2, HDMI, USB-C 3.2,... khiến việc kết nối với các thiết bị ngoại vi chưa bao giờ dễ dàng đến thế.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("cb33f82a-6829-44e4-bd8c-d5d6b609d196"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080) và tấm nền IPS không chỉ cho phép bạn trải nghiệm hình ảnh sắc nét mà còn cung cấp góc nhìn rộng lên đến 178 độ. Tần số quét 144 Hz kết hợp với công nghệ G-Sync đảm bảo rằng mọi chuyển động trong game đều mượt mà, giúp bạn phản xạ nhanh nhạy trong những tựa game đua xe, bắn súng hay hấp dẫn theo dõi những tựa phim bom tấn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("17143a17-f581-47ed-8404-1a1979a8c5c0"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ae4a9888-3da8-4158-8cd6-9d0311351026.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("29f03404-0a5e-4011-99c0-0a70ba788d51"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Chuẩn màu 100% sRGB là một công cụ hữu ích cho những ai làm việc với thiết kế đồ họa, giúp bạn đạt được màu sắc chính xác nhất cho mọi dự án. Hơn nữa, lớp phủ chống chói Anti Glare còn hạn chế được tình trạng phản sáng trên màn hình làm khó chịu tầm quan sát của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("5fddbb59-5d79-4427-8e49-c151cdd37944"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Nhờ công nghệ Nahimic Audio, chất lượng âm thanh trên laptop Lenovo không chỉ đơn thuần là âm thanh, mà là một phần của trải nghiệm game tuyệt vời. Âm bass mạnh mẽ, khả năng lọc tiếng xuất sắc và âm thanh chân thực sẽ đưa bạn vào trung tâm của mọi trận đấu, giúp bạn cảm nhận từng tiếng bước chân hay âm thanh chiến trường một cách rõ nét nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("eaa5b52c-ba8a-4038-8a2b-2646b2ab1290"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Qua sức mạnh của bộ vi xử lý Intel Core i5 12450HX sở hữu 8 nhân và 12 luồng, card rời NVIDIA GeForce RTX 2050 4 GB, Lenovo LOQ 15IAX9 chính là cỗ máy chiến đấu hấp dẫn ở nhiều tựa game. Cho dù bạn đang chiến đấu trong những trận đấu căng thẳng hay điều chỉnh đồ họa cho các công cụ thiết kế, sáng tạo thì máy vẫn luôn hoạt động trơn tru, mang lại hiệu suất tối ưu cho kể cả công việc văn phòng thường ngày lẫn giải trí thông dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("09ef8d4d-d966-49d0-835b-7d798e9042c7"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/20ff67f9-0fd7-4cac-b6a1-48e42488f1f4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("5a98910f-da2a-47d8-bffa-08d4a4470c0d"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Với RAM 24 GB sẵn có, bạn sẽ không bao giờ phải lo lắng về việc thiếu bộ nhớ khi chiến đấu trong các trận game kịch tính hay khi làm việc với nhiều ứng dụng cùng lúc. Và nếu có nhu cầu xử lý dữ liệu lớn hơn hoặc muốn nâng tầm trải nghiệm chiến game thì khả năng nâng cấp RAM lên 32 GB sẽ cho phép bạn thỏa sức hoàn thành mọi tác vụ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("84b1e911-2ab8-4c97-aafd-18e1cc7387a1"), GadgetId = Guid.Parse("d6d035e0-533b-4f01-9bce-6f1601f72f97"), Value = "Ổ cứng SSD 512 GB NVMe PCIe không chỉ giúp laptop Lenovo LOQ khởi động nhanh chóng mà còn cho phép bạn lưu trữ nhiều game yêu thích mà không lo hết dung lượng. Và nếu cần nhiều không gian hơn nữa, người dùng có thể nâng cấp lên 1 TB đảm bảo rằng mọi trò chơi và dữ liệu quan trọng của bạn luôn sẵn sàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e7e66ac8-d197-42f9-a557-2fa906582235"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Ấn tượng mạnh mẽ từ vẻ bề ngoài độc đáo, thu hút đến khả năng chiến game đỉnh cao trên mẫu laptop Lenovo Gaming LOQ 15ARP9 R7 7435HS (83JC0040VN). Máy sở hữu con chip AMD Ryzen 7 mạnh mẽ, card rời RTX 40 Series, RAM 24 GB,... sẽ cùng bạn có những khoảnh khắc chiến đấu thực sự xuất thần.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("3c2515db-51c6-4881-bda4-e1b76c3ca81c"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Laptop Lenovo LOQ được trang bị bộ vi xử lý AMD Ryzen 7 7435HS với 8 nhân và 16 luồng. Điều này mang lại hiệu năng mạnh mẽ, giúp bạn xử lý mượt mà mọi tác vụ từ chơi game đến công việc đồ họa nặng. Các game thủ có thể tận hưởng trải nghiệm chiến đấu không bị gián đoạn, trải nghiệm được đa dạng nhiều con game \"hot hit\" trên Steam hay kể cả khi chạy các ứng dụng nặng hoặc thực hiện livestream mà không lo máy bị chậm hay lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("2edcd305-00b0-42bc-8e60-21fc080ee85b"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/35655bb5-3d5b-49fe-b818-2a97f04281bf.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("fdbde5be-70d5-4fd9-9f3e-b4b07ae8c146"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Card đồ họa rời NVIDIA GeForce RTX 4050 6 GB mang lại khả năng xử lý đồ họa mạnh mẽ, giúp bạn tận hưởng chất lượng hình ảnh tuyệt vời nhờ tính năng RAY TRACING hỗ trợ đổ bóng hình ảnh sống động trong các tựa game đòi hỏi đồ họa cao như Valorant, Cyberpunk 2077,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("00fb6cf5-f2c6-4204-bca9-11ee3252f7be"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Bộ nhớ RAM 24 GB DDR5 với tốc độ Bus 4800 MHz, cho khả năng đa nhiệm linh động, đặc biệt hữu ích cho game thủ khi cần mở đồng thời nhiều ứng dụng như game, chuyển đổi phần mềm chat Discord và trình duyệt. Hơn nữa, bộ nhớ RAM cơ bản lớn nên bạn có thể thoải mái sử dụng ngay khi mua về tuy nhiên laptop vẫn có thể nâng cấp RAM lên 32 GB cho đa dạng nhu cầu hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("d87a7230-aab5-4f53-8b03-ce21986c058e"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Ổ cứng SSD 512 GB NVMe PCIe không chỉ mang đến không gian lưu trữ rộng rãi mà còn hỗ trợ truy cập và khởi động máy nhanh chóng, giúp bạn tiết kiệm thời gian mỗi khi khởi động game hay ứng dụng. Không gian bộ nhớ cũng có thể mở rộng lên đến 1 TB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("038025b6-214b-45e1-b852-b2f8cc22bc85"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/71c425c4-cfa6-4a3e-8cd7-e4680593f6d0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("9831c08f-901e-4d6b-9b2d-300d3b3bf3d5"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080) và tần số quét 144 Hz mang lại hình ảnh sắc nét, chuyển động mượt mà. Điều này đặc biệt quan trọng đối với các tựa game yêu cầu phản xạ nhanh như bắn súng và đua xe, nơi mà từng khung hình đều có thể quyết định thành bại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("a4236493-3730-4356-961c-bf1c1b63f7e8"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Độ phủ màu 100% sRGB mang đến độ chuẩn xác cao hơn khi giải quyết công việc đồ hoạ. Công nghệ Low Blue Light và chống chói Anti Glare giúp bảo vệ mắt và cung cấp trải nghiệm hình ảnh chân thực trong mọi điều kiện ánh sáng, cho phép game thủ chơi lâu mà không bị mỏi mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d334af25-cfdd-47e4-9260-393efaaec0e5"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f081e06f-fa2b-4f37-8a50-82e5689617b1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("0cccb913-a11a-41ff-ba52-5c4c4c3b29fa"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Laptop Lenovo với hệ thống âm thanh 3D Nahimic Audio đem đến trải nghiệm âm thanh sống động, rõ ràng và chi tiết, giúp bạn nghe rõ từng bước chân của đối thủ trong các tựa game bắn súng. Khả năng tái tạo âm thanh chi tiết còn tăng cường sự tập trung và phản ứng nhanh trong các tình huống game căng thẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("3faf65c4-0b1c-4acc-9e7c-810c8e3d4aaf"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Laptop với kiểu dáng đặc trưng từ dòng gaming nhà Lenovo với phần bản lề được nhô ra độc đáo, đường nét tinh xảo và đầy mạnh mẽ. Lớp vỏ nhựa bền bỉ, các chi tiết được hoàn thiện tỉ mỉ, mang lại cảm giác chắc chắn và bền vững hơn. Đèn nền đơn sắc màu trắng dưới bàn phím giúp game thủ dễ dàng thao tác trong điều kiện ánh sáng yếu. Bản lề mở 180 độ linh hoạt, thuận tiện cho nhiều tình huống sử dụng từ làm việc đến giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("6c65234d-630f-475e-ba53-dda2916cd469"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Khối lượng cân đối 2.38 kg so với những chiếc laptop gaming khổ lớn, dễ dàng mang theo, thuận tiện cho việc di chuyển hàng ngày và đặc biệt hữu ích cho những game thủ thường xuyên phải mang máy đến các buổi thi đấu. An tâm hơn khi mang theo với độ bền chuẩn quân đội MIL STD 810H, chịu đựng tốt trước các va đập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("9f8681bb-377c-49cc-b5be-d0ac430af465"), GadgetId = Guid.Parse("1c534a47-213d-4ee1-b542-9d10e79292ea"), Value = "Thiết bị được bố trí đa dạng các cổng kết nối bao gồm: LAN, Jack tai nghe 3.5 mm, USB 3.2, HDMI, USB-C 3.2 để game thủ truyền xuất dữ liệu đến màn hình ngoài, bàn phím hay game console.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("195c265f-0b62-413a-8046-a10dda544507"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Laptop Lenovo Yoga Slim 7 14IMH9 Ultra 7 155H (83CV001VVN) sở hữu thiết kế thanh mảnh, gọn nhẹ, dễ dàng mang theo bên mình mọi lúc mọi nơi. Được trang bị chip Intel Ultra 7 thế hệ mới, laptop mang đến hiệu suất vượt trội, đáp ứng tốt mọi nhu cầu công việc và giải trí.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("be0b895c-48bf-4cf9-989b-f22520c7cbf2"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Nổi bật với thiết kế mỏng nhẹ ấn tượng, chỉ 14.9 mm và khối lượng 1.39 kg, Lenovo Yoga mang đến cho bạn sự tiện lợi vô cùng khi di chuyển. Thoải mái đặt máy vào balo, túi xách và mang theo bên mình mọi lúc mọi nơi, đồng hành cùng bạn trong mọi hành trình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f7538d56-9959-4eee-9ab0-9900bd872222"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Máy còn đạt chuẩn độ bền quân đội MIL STD 810H, mang đến khả năng chống chịu va đập và tác động ngoại lực vượt trội, đồng hành cùng bạn mọi lúc mọi nơi. Laptop đạt tiêu chuẩn nền Intel Evo đáp ứng hoàn hảo mọi nhu cầu sử dụng của bạn. Khởi động tức thì chỉ trong vòng 1 giây từ chế độ Sleep, sạc nhanh chóng, thời lượng pin ấn tượng,... giúp bạn luôn sẵn sàng chinh phục mọi thử thách.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("966f8a2e-507c-48ae-8d72-eec49dd927cd"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3ee6e457-d7f3-4f64-8961-214ac72aabb5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("0d81980c-5037-48aa-bba1-79477df8dc70"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Bàn phím của chiếc máy tính này được trang bị đèn nền đơn sắc trắng, mang đến khả năng hiển thị rõ ràng ngay cả trong môi trường thiếu sáng, cho phép bạn làm việc hiệu quả mọi lúc mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("0c1af42a-ddf7-4fc4-a3c5-e43156987931"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Webcam Full HD tích hợp sẵn giúp bạn dễ dàng tham gia các cuộc họp trực tuyến hay trò chuyện video với bạn bè, gia đình. Camera IR kết hợp với công nghệ mở khóa khuôn mặt cho phép bạn đăng nhập nhanh chóng và khả năng bảo mật cao. Laptop còn trang bị công tắc khóa camera giúp bạn kiểm soát quyền riêng tư hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("a266aef2-f97f-4164-ada0-afc495c25790"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/937bf877-4bd3-46f8-a5bc-a7da3211a658.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("9136486f-12bd-4454-9a02-01f8c7bfa432"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Máy tích hợp nhiều cổng kết nối tiện lợi như jack tai nghe 3.5 mm, HDMI, USB Type-C và USB 3.2 (Always on) được bố trí dọc hai bên thân máy, thuận tiện hơn trong quá trình sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("71fbe031-0053-4fbd-99b2-392f260a0ad0"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Lenovo trang bị cho chiếc laptop này độ phân giải WUXGA (1920 x 1200), mang đến hình ảnh chi tiết và sắc nét, cho phép bạn đắm chìm trong từng khung hình. Công nghệ màn hình OLED tiên tiến giúp hiển thị màu sắc rực rỡ, sống động với độ đen sâu, mang đến trải nghiệm thị giác tuyệt vời cho mọi tác vụ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("0dff75c3-9fa3-4781-b261-6d223147cb12"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/085e9bd8-9deb-4853-8ada-7f7b4a68b086.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("1024febe-5911-445c-9f2e-9d15d19bfde2"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Nhờ đạt được độ phủ màu 100% DCI-P3, chiếc laptop này tái tạo màu sắc chính xác, đáp ứng nhu cầu khắt khe của các nhà sáng tạo nội dung và chuyên gia đồ họa. Hơn thế nữa, công nghệ Dolby Vision được tích hợp mở rộng dải tương phản và độ sáng, nâng tầm trải nghiệm hình ảnh với những khung hình sống động như thật, từng chi tiết được thể hiện rõ ràng và sắc nét.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("bd50520a-da8d-4f66-acdf-a98e61542832"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Nhờ công nghệ Smart AMP và Dolby Atmos mà chiếc laptop mỏng nhẹ này có thể khuếch đại âm thanh một cách mạnh mẽ, cho bạn tận hưởng trọn vẹn từng giai điệu, từng chi tiết âm thanh dù là nhỏ nhất. Hiệu ứng âm thanh sống động, đưa bạn vào thế giới âm thanh vô cùng kì diệu, giải tỏa mọi căng thẳng khi làm việc, học tập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("1cdc28f2-8487-4745-887a-1f8f0fd18a4f"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Được trang bị bộ vi xử lý Intel Core Ultra 7 155H tiên tiến với 16 nhân và 22 luồng mạnh mẽ, Lenovo Yoga sẵn sàng đáp ứng mọi nhu cầu công việc và giải trí của bạn. Điểm đặc biệt của thế hệ chip mới này là tích hợp NPU - nhân xử lý AI hiện đại. Nhờ vậy, laptop sẽ có khả năng tự động tối ưu hóa hiệu suất cho từng công việc, phân bổ nguồn điện thông minh để kéo dài thời gian sử dụng pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("57778628-528f-4ab1-84d6-6c8d9fc33354"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Kết hợp cùng GPU Intel Arc Graphics, chiếc laptop Lenovo không chỉ hoàn thành xuất sắc các tác vụ văn phòng, doanh nghiệp hay lướt web thông thường mà còn chinh phục hoàn hảo những công việc chuyên môn đòi hỏi đồ họa cao như chỉnh sửa ảnh hay video.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("bbce7b99-b1e6-4ab4-8238-452c5be6e205"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Laptop Lenovo Yoga mang đến sức mạnh vượt trội với bộ nhớ RAM 32 GB LPDDR5X Onboard giúp bạn xử lý đa nhiệm mượt mà, chạy cùng lúc nhiều ứng dụng mà không lo giật lag hay gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("d7f8e2f9-50e5-4888-841c-539412ff7815"), GadgetId = Guid.Parse("aceeb60d-d2d1-4ef6-991f-cb441d1500b2"), Value = "Ổ cứng SSD NVMe PCIe Gen 4 dung lượng 1 TB cho bạn không gian lưu trữ khổng lồ, thỏa sức tải xuống và lưu trữ mọi dữ liệu, tài liệu học tập, phim ảnh, âm nhạc mà không cần lo lắng về giới hạn. Tốc độ truy cập dữ liệu cực nhanh giúp bạn tiết kiệm thời gian, nâng cao hiệu quả công việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("09aed1c7-6f18-4354-82df-ba5fc5746145"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Nếu bạn đang cần một người bạn đồng hành học tập, làm việc hiệu quả, linh động nhưng lại sở hữu một mức giá thành cân đối thì đừng bỏ qua laptop Lenovo ThinkBook 16 G7 Ultra 5 125U (21MS007GVN). Máy với hiệu năng tích hợp cao, màn hình 16 inch sắc nét hỗ trợ người dùng mọi việc từ cơ bản đến nâng cao.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("bd69861c-4184-4c17-8955-e856aaa41a3d"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Bộ vi xử lý Intel Core Ultra 5 125U mới nhất với cấu trúc 12 nhân 14 luồng, cho tốc độ xung nhịp tối đa lên đến 4.3 GHz nhờ công nghệ Turbo Boost, giúp bạn dễ dàng vượt qua mọi tác vụ văn phòng như Word, Excel, PowerPoint,... một cách hiệu quả và nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f94e5da1-2fee-43aa-bc4c-ae3deecc2d64"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0fb8be9d-5332-4705-b61f-137973522c9d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("35126e8d-0706-4953-92f2-947be58fe68e"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "GPU Intel Graphics trên laptop là người bạn đồng hành hoàn hảo cho những ai đam mê sáng tạo nội dung hoặc làm việc với các ứng dụng đồ họa. Hiệu suất mạnh mẽ của nó cho phép xử lý trơn tru các phần mềm từ lập trình đến đồ họa như Photoshop, AI, Figma,... Bạn còn có thể chơi các tựa game như FO4, LOL, Valorant,... ở cấu hình được căn chỉnh hợp lý để giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("190788a5-4d76-43c0-ace0-dc2c93bac98b"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "RAM 16 GB đảm bảo khả năng đa nhiệm tuyệt vời cho laptop, cho phép bạn mở nhiều ứng dụng cùng lúc, chuyển đổi mượt mà giữa các ứng dụng mà không gặp tình trạng giật lag hay đơ máy. Đáp ứng tối đa nhu cầu sử dụng khi có thể nâng cấp RAM lên tối đa tận 64 GB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("9641a875-4e73-4dce-ba91-e9cc178d11d7"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Ổ cứng SSD 512 GB NVMe PCIe Gen 4 mang đến tốc độ khởi động hệ thống nhanh chóng, giúp tiết kiệm thời gian chờ đợi đáng kể. Dung lượng thoải mái cùng khả năng tháo lắp và thay thế thanh khác tối đa lên được 2 TB giúp người dùng sử dụng mà không cần lo lắng về tài nguyên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("bde0ed84-4d3a-403c-9d6d-768a274ad1c9"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/87071d16-59bb-46d4-9147-8f1526d2b22a.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("53ac9834-c992-4d29-b145-b03ccd8b8a6d"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Laptop Lenovo sở hữu màn hình 16 inch rộng lớn, độ phân giải WUXGA (1920 x 1200) sắc nét mở ra không gian làm việc, giải trí tiện dụng hơn, mở nhiều cửa sổ nhỏ hay xem phim đã mắt, thu hút. Màn hình cũng hỗ trợ công nghệ chống chói Anti Glare, giảm thiểu lượng ánh sáng phản xạ đến mắt người gây khó chịu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("9d8d4410-2c54-4a13-9e82-e2778a8d1ffa"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Công nghệ âm thanh Dolby Audio trên laptop Lenovo ThinkBook mang đến trải nghiệm giải trí đỉnh cao với âm thanh sống động và chi tiết, giúp bạn đắm chìm vào từng bản nhạc hay bộ phim yêu thích.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9418afcf-34b9-4e79-a56d-241446621901"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Thiết kế laptop sang trọng, tinh tế với lớp vỏ nhựa cứng cáp và nắp lưng kim loại. Độ bền đạt chuẩn quân đội MIL-STD 810H đảm bảo khả năng chống va đập, chống sốc và hoạt động tốt trong môi trường khắc nghiệt, cho phép bạn yên tâm sử dụng laptop dù là ở bất kỳ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a50be946-e157-47d1-8c9c-0378c5a80aef"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/01f63fea-991a-46b0-a8e0-169fe5a825ad.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c6a15a2e-748c-4441-939d-892104a05589"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Hơn nữa, phần bản lề mở được 180 độ, vô cùng thuận tiện khi cần chia sẻ thông tin trong cuộc họp và thảo luận. Một số tính năng bảo mật hiện đại cũng được tích hợp như: bảo mật vân tay, công tắc khóa camera và mở khóa khuôn mặt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("a2b7d29a-f03d-4f79-b056-fbfc33c087ab"), GadgetId = Guid.Parse("ad8b304a-e1f2-45e0-b943-4af8662402db"), Value = "Bàn phím laptop học tập - văn phòng có đèn nền giúp bạn dễ dàng thao tác trong môi trường thiếu sáng. Các cổng kết nối đa dạng như: LAN (RJ45), USB 3.2, HDMI, USB-C,... giúp bạn dễ dàng kết nối với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("8eaa80d1-51af-4aef-8879-5f5d6785a97d"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Bạn đang tìm kiếm một chiếc laptop mạnh mẽ, đa nhiệm và giá cả phải chăng? Laptop Lenovo ThinkBook 14 Ultra 7 (21MR0072VN) chắc chắn là một lựa chọn đáng cân nhắc. Với cấu hình ấn tượng và thiết kế tiện dụng, chiếc laptop học tập - văn phòng này sẽ giúp người dùng vượt qua mọi thử thách từ công việc đến giải trí.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("06a0e0f3-619d-49cb-a3af-aa0bf65da82a"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Lenovo ThinkBook 14 được trang bị chip Intel Core Ultra 7 Meteor Lake - 155U mới nhất với 12 nhân và 14 luồng, tốc độ tối đa lên đến 4.8 GHz nhờ công nghệ Turbo Boost. Điều này giúp người dùng không chỉ tự tin giải quyết mọi vấn đề công việc thường ngày, tạo lập văn bản, sáng tạo ý tưởng đồ hoạ, lập trình dữ liệu,... mà còn đảm nhận luôn được nhiều nhu cầu giải trí, xem phim, lướt web, chơi game,... cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("6d8aa56b-1423-4036-af6e-2f3bf032229c"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "GPU Intel Graphics trên laptop Lenovo ThinkBook là công cụ tuyệt vời cho những ai đam mê sáng tạo, tạo hiệu quả cao khi làm việc với các phần mềm đồ họa như Photoshop, Illustrator, Figma và thậm chí là lập trình. Ngoài ra, máy còn đủ sức mạnh để bạn thưởng thức các tựa game phổ biến như FIFA Online 4, League of Legends,... ở cấu hình hợp lý. Khả năng xử lý đồ họa mạnh mẽ này cũng hỗ trợ tốt cho việc thiết kế, chỉnh sửa video ngắn và các dự án sáng tạo khác, mang lại trải nghiệm làm việc và giải trí toàn diện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("48ada06e-e831-4f1f-a57e-ab7fb7663788"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/e2ed3809-3dc1-4ef0-94ac-d9081b8b3f4f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("723263d5-9ea9-4756-be89-72b055c6865f"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Thế hệ chip mới còn tích hợp với NPU - nhân xử lý AI tạo lập trình công việc phù hợp, phân luồng các tác vụ tiện lợi và hiệu quả hơn cho người dùng. Bên cạnh đó, điều này cũng hỗ trợ nâng cao hơn thời gian sử dụng máy hơn rất nhiều so với các thế hệ chip trước.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("b777d0b1-6355-4bee-8c75-3ba4d76ddd19"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Laptop với RAM 16 GB mang lại trải nghiệm đa nhiệm tuyệt vời, cho phép bạn chạy nhiều ứng dụng cùng lúc và chuyển đổi giữa chúng mượt mà. Khả năng nâng cấp RAM lên đến 64 GB còn tương thích được tốt hơn với các dạng file dữ liệu lớn, vận dụng đồ hoạ nhiều layer mà không bị chậm hay đơ máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("9386cac8-ae57-420d-ad2d-9a0a77f1dc4a"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/943c2996-683d-4bcd-8bfb-83f4ee7d6538.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("be20b481-f4c7-46f5-97ec-325b9046c430"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Ổ cứng SSD 512 GB NVMe PCIe Gen 4 không chỉ giúp khởi động hệ thống nhanh chóng mà còn cung cấp không gian lưu trữ rộng rãi. Bạn có thể nâng cấp dung lượng lưu trữ lên đến 2 TB, đảm bảo không gian thoải mái cho tất cả dữ liệu và ứng dụng cần thiết. Tốc độ đọc/ghi cao của ổ cứng SSD tăng tốc thời gian khi truy cập dữ liệu, cài đặt phần mềm và khởi động hệ điều hành, nâng cao trải nghiệm lên đáng kể.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("68358488-b079-42fe-94ce-71e13452b8f5"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Màn hình 14 inch với độ phân giải WUXGA (1920 x 1200) của Lenovo ThinkBook mang đến không gian làm việc và giải trí rộng lớn. Tầm nhìn được mở rộng hơn đến 178 độ với tấm nền IPS, công nghệ chống chói Anti Glare giúp giảm thiểu phản chiếu ngay cả trong điều kiện ánh sáng mạnh. Người dùng sẽ thích thú với khả năng hiển thị rõ ràng và sắc nét dù là khi xem phim, lướt web hay làm việc với các ứng dụng đồ họa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("4e06230d-7f23-4189-b804-dbae5726d175"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Công nghệ Dolby Audio cung cấp âm thanh sống động và chi tiết, giúp bạn đắm chìm trong từng giai điệu và từng cảnh phim. Cho dù bạn đang nghe nhạc, xem phim hay tham gia các cuộc gọi video, âm thanh rõ ràng và trung thực sẽ mang lại cảm giác thú vị và chân thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("bbaee5a3-667e-4237-8478-d6fc5506978c"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/96e8a762-fdc0-438d-a5b3-dfe469121a8d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f6411738-3001-4c42-87c2-124c25f32859"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Thiết kế của laptop Lenovo toát lên vẻ sang trọng và chắc chắn với lớp vỏ kim loại nhôm cao cấp. Độ bền đạt chuẩn quân đội MIL STD 810H đảm bảo khả năng chống va đập, chống sốc và hoạt động tốt trong nhiều môi trường khác nhau, giúp bạn yên tâm sử dụng laptop mọi lúc, mọi nơi. Khối lượng \"gọn nhẹ\" chỉ có 1.38 kg cho hành trình di chuyển thật linh hoạt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("bde133e9-9854-42a1-9e86-1a73b7c9fa08"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Đa tính năng bảo mật tiên tiến như bảo mật vân tay, công tắc khóa camera và mở khóa khuôn mặt đảm bảo an toàn cho dữ liệu và sự riêng tư của bạn. Những tính năng này không chỉ giúp bảo vệ thông tin cá nhân mà còn tăng cường an ninh cho các dữ liệu quan trọng, giúp bạn yên tâm sử dụng laptop trong mọi hoàn cảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("3e1ae75c-dbdc-41cc-83c4-6d8fa730050c"), GadgetId = Guid.Parse("bdca3af0-516c-4e05-b1f9-306d0f4756ce"), Value = "Bàn phím với đèn nền giúp bạn dễ dàng làm việc trong điều kiện thiếu sáng. Máy còn hỗ trợ nhiều cổng kết nối như: LAN (RJ45), USB 3.2, HDMI, USB-C,... đảm bảo rằng bạn có thể kết nối mọi thiết bị cần thiết, từ màn hình phụ, máy chiếu đến các thiết bị lưu trữ ngoại vi, mang lại sự linh hoạt và tiện lợi trong công việc hàng ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("51564819-fafe-4f1a-be10-86be3d5ae05a"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Laptop Lenovo V15 G4 IRU i5 1335U (83A1000LVN) mang đến sự kết hợp hoàn hảo giữa vẻ đẹp hiện đại cùng hàng loạt các công nghệ tiên tiến, là một lựa chọn không thể bỏ qua để tối ưu trải nghiệm trong học tập, công việc và giải trí đối với các bạn học sinh - sinh viên hoặc nhân viên văn phòng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("c3dcb643-4c83-4a6a-bbcf-fc2a0c4b66ad"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Kiểu dáng tinh giản, sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("ad7ea474-fb00-490a-91f5-d28a8e5ac877"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Trên hành trình tìm kiếm sự hoàn hảo của dòng máy laptop học tập - văn phòng, laptop Lenovo tỏa sáng với sự đơn giản và đẳng cấp của ngoại hình. Không phô trương, không quá nhiều chi tiết phức tạp, máy chỉ đơn giản là một biểu tượng của sự chắc chắn, tối giản. Khi được chế tác từ lớp vỏ nhựa hoàn toàn cùng tông xám trầm, máy mang đến một vẻ đẹp cứng cáp, thanh lịch, khiến mình tự tin mỗi khi đặt nó ở bất kỳ góc độ nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("aa2123eb-cbab-4200-991b-47500e1e9478"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4e57a1b7-e615-4bbe-b096-a219d897df87.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("64c3129c-be79-48c4-a014-2521d68881c8"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Khối lượng của em máy khá đầm tay chỉ vỏn vẹn 1.67 kg, khiến nó trở thành một lựa chọn lý tưởng cho việc di chuyển hàng ngày. Khả năng bỏ gọn vào balo hay túi xách mà không chiếm quá nhiều diện tích không gian, máy mang lại sự thoải mái và tiện ích khi bạn cần di chuyển, dù là trong học tập, công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("0d1775f8-c073-4b0a-b141-e7f43030a4b3"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Thú vị hơn nữa, trong những lúc tác động mạnh hay thao tác mở 180 độ, mình cảm thấy không có dấu hiệu nào của sự lung lay, lỏng lẻo nào cả, kể cả khi mình gõ phím mạnh thì phần màn hình, bản lề khá là chắc luôn, nên các bạn cứ yên tâm về độ bền mà em nó mang lại nhé. Điều đặc biệt đáng chú ý, tính năng độ bền chuẩn quân đội MIL STD 810H được tích hợp, giúp máy chịu được những va đập mạnh mẽ hoặc rơi nhẹ mà không gây tổn thương lớn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("398e393d-28b2-42ed-9ea6-8d0b25967f66"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Về phần bàn phím, việc sở hữu một layout fullsize cùng các cụm phím số riêng biệt là điểm nhấn đáng chú ý. Điều này giúp cho việc nhập liệu trở nên dễ dàng, thuận tiện, đặc biệt là đối với các công việc văn phòng hay kế toán. Tuy hành trình phím không quá sâu, nhưng việc các phím được sắp xếp gần nhau làm cho mình gặp phải tình trạng nhầm phím khi gõ quá nhanh, nên các bạn cần lưu ý nhé.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("1a815cc3-a194-4c4d-b0a5-08fb998cc5be"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Với touchpad, mặc dù có kích thước hơi nhỏ, nhưng vẫn đưa đến một cảm giác khá thoải mái, linh hoạt trong thao tác. Độ mượt mà và độ nhạy của bàn di chuột đều được mình đánh giá khá cao, không gây ra bất kỳ khó khăn nào cho người dùng. Điều này tạo điều kiện thuận lợi cho việc sử dụng máy một cách hiệu quả, thoải mái nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("8966bb07-1e13-46e2-ae84-c67ffdbf3086"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Bên cạnh đó, các cổng giao tiếp được sắp xếp ở hai bên thân máy như USB Type-C, USB 2.0, Jack tai nghe 3.5 mm, USB 3.2, HDMI, LAN đảm bảo rằng bạn sẽ không gặp bất kỳ vấn đề nào khi cần kết nối với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("11504535-8827-4273-b27e-c519b1c37e7e"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Khung hình sắc nét, chi tiết", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("061f4ecb-83be-46f5-882d-e1ca289abf59"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Màn hình rộng 15.6 inch của laptop không chỉ mang lại cho mình trải nghiệm hình ảnh đắm chìm mà còn tạo điều kiện thuận lợi trong công việc, giải trí với các cạnh viền siêu mỏng khi mở ra một góc nhìn mở rộng. Bên cạnh đó, độ phân giải Full HD (1920 x 1080) kết hợp cùng tấm nền IPS tạo ra hình ảnh sắc nét, màu sắc tươi sáng, sinh động, đem đến cảm giác xem vô cùng hấp dẫn cho mình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("46aac1f4-d3d3-4817-866d-beca26b49577"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/cdf573aa-141e-4d1d-8368-17fe52367415.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("eefff01f-a824-4810-93bc-29e689abe200"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Khả năng hiển thị màu sắc đa dạng nhờ độ phủ màu 45% NTSC hỗ trợ máy hoàn toàn đáp ứng được các nhu cầu làm việc như văn phòng, thiết kế hình ảnh, render video nhẹ nhàng. Điều này đồng nghĩa với việc mình có thể tin tưởng vào hiệu suất về chất lượng hình ảnh mà máy mang lại trong những nhiệm vụ đòi hỏi màu sắc chuẩn xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("e6e6de68-c267-4582-8cdc-a6d7eeebfba7"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Mình hoàn toàn yên tâm hơn khi hãng quan tâm đến sức khỏe người dùng nhờ trang bị các tính năng chống chói Anti Glare, Low Blue Light các bạn vẫn có thể làm việc trong cả một ngày dài mà không gặp bất kỳ tình trạng lóa mắt trong môi trường ánh sáng mạnh, giảm thiểu căng thẳng cho thị giác và giúp mình có thể sử dụng máy với tâm thế thoải mái nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("c2c6039c-8cde-4fd2-9184-45e28d636fcd"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Em máy còn tích hợp âm thanh Dolby Audio, thiết bị trở thành một trung tâm giải trí đích thực. Công nghệ này hỗ trợ tăng cường âm trầm mạnh mẽ, mỗi chi tiết âm nhạc, hiệu ứng âm thanh trong phim đều được tái tạo một cách rõ ràng và sắc nét. Cho dù mình đang thưởng thức âm nhạc yêu thích hay xem phim hành động, âm thanh này sẽ làm cho mỗi trải nghiệm của mình trở nên sống động, sâu sắc hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("5fef0f35-bb72-4355-be26-e9e08bea9dc2"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Cấu hình ổn định cho mọi tác vụ văn phòng", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("88039e14-2edd-4cd0-9150-57f6b6f5d03d"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Sử dụng cấu hình khá lý tưởng đến từ con chip Intel Core i5 1335U mới nhất đi cùng card Intel Iris Xe Graphics cam kết về hiệu suất, đảm bảo mọi trải nghiệm người dùng hoàn toàn mượt mà và không gián đoạn cho các nhiệm vụ từ cơ bản như soạn thảo văn bản, trang tính Excel hay thậm chí là chỉnh sửa video, thiết kế poster, banner cơ bản trên ứng dụng như Photoshop, Capcut. Điều này giúp người dùng tự tin thực hiện mọi nhiệm vụ mà không gặp phải bất kỳ trở ngại nào từ phía hiệu suất của máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("ad404baa-dc32-477c-b832-3b0774126ec4"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Việc thử nghiệm máy trên CINEBENCH R23 cũng đã cho thấy khả năng vượt trội với 6410 điểm đa nhân và 1633 điểm đơn nhân, đủ để mình có thể thưởng thức các tựa game nhẹ nhàng như LOL, CS:GO mà không gặp bất kỳ khó khăn nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("b8a15812-f323-42bc-b395-d72122420727"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/865df146-7f33-4653-901a-6e096dab5562.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("21a00e3f-4fb3-47be-b6e5-538bbeeca662"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Với bộ nhớ RAM 16 GB, máy đảm bảo đủ sức đáp ứng mọi yêu cầu của người dùng, cho dù là các tác vụ đòi hỏi tài nguyên nhiều. Mặc dù không hỗ trợ nâng cấp RAM thêm, nhưng với dung lượng này, laptop Lenovo V Series vẫn đủ để mình tiến hành công việc một cách trơn tru, hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("60a45f26-27b9-46f6-a149-850f876cb830"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Ổ cứng SSD dung lượng lên đến 512 GB có hỗ trợ nâng cấp tận 1 TB em máy này không những cung cấp không gian lưu trữ rộng rãi cho tài liệu cá nhân, video, ứng dụng yêu thích mà còn đưa đến tốc độ phản hồi vô cùng nhanh chóng. Với em này, việc chờ đợi thực sự không cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("7f0c3c10-a397-476e-a6f9-ff867eeb367f"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Sở hữu viên pin 38 Wh mình có thể làm việc liên tục trong 4 - 5 tiếng, đủ để hoàn thành các tác vụ văn phòng hay giải trí mà không cần lo lắng về việc sạc pin. Tuy nhiên, nếu bạn đang thực hiện các tác vụ nặng hơn, thời gian sử dụng pin có thể giảm xuống đâu đó khoảng 2 - 3 tiếng. Do đó, việc mang theo cục sạc khi ra ngoài là điều khuyến khích, để các bạn có thể tiếp tục sử dụng máy thoải mái mà không bị gián đoạn công việc giữa chừng nhé.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("28678bde-269f-416c-9761-9ab174410dfa"), GadgetId = Guid.Parse("47af9357-7d0c-4c46-bc37-35ba938354c5"), Value = "Tóm lại, laptop Lenovo V15 G4 IRU i5 1335U (83A1000LVN) là một sự lựa chọn lý tưởng cho các bạn học sinh và sinh viên. Với khả năng hoạt động linh hoạt, hiệu suất ổn định, em máy này sẽ là người bạn đồng hành đáng tin cậy trong suốt quá trình học tập của bạn. Hãy tham khảo ngay em nó tại Thế Giới Di Động nhé!", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("a298c6fe-d52a-4421-a3a7-e4a6e07f06dc"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Laptop Lenovo V15 G5 IRL Core 5 120U (83HF0000VN) là sự kết hợp hoàn hảo giữa hiệu suất mạnh mẽ và thiết kế hiện đại, phù hợp cho học sinh, sinh viên và nhân viên văn phòng. Với cấu hình ổn định, dung lượng lưu trữ rộng rãi và vẻ ngoài cuốn hút, sản phẩm này đang được bán với mức giá hấp dẫn tại Thế Giới Di Động.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("3a84b8aa-249f-47bf-ad73-f748b28231aa"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Trái tim của Lenovo V15 G5 chính là bộ vi xử lý Intel Core 5 Raptor Lake - 120U, gồm 10 nhân và 12 luồng hoạt động cùng công nghệ Turbo Boost, đẩy tốc độ lên tối đa tới 5.0 GHz. Điều này đảm bảo rằng mọi tác vụ, từ công việc văn phòng với Word, Excel, PowerPoint cho đến những ứng dụng đồ họa cơ bản, đều được xử lý mượt mà. Bạn còn có thể dễ dàng làm việc với các nền tảng coding, khiến sản phẩm này trở thành công cụ hữu ích cho cả lập trình viên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("6f5e898d-95e5-47ba-8d40-ed3addd0ade7"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/37f263f0-fdb8-426c-b926-6e968df450e3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("2cf1cb24-d1f2-42a9-8e2f-9aa2c508089c"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Sở hữu card đồ họa tích hợp Intel Graphics, laptop không chỉ mang lại hình ảnh chất lượng cao cho các hoạt động xem phim, chỉnh sửa ảnh trên Photoshop, AI hay Canva mà còn đủ sức chạy các tựa game nhẹ nhàng để bạn thư giãn sau những giờ làm việc căng thẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("98ed5b64-1625-46f7-bbfa-8b813797dad8"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Với RAM 16 GB, chiếc laptop Lenovo V Series này cho phép bạn mở hàng chục ứng dụng cùng lúc mà vẫn đảm bảo hoạt động mượt mà, hạn chế tình trạng giật lag. Ổ cứng SSD 512 GB NVMe PCIe 4.0 không chỉ cung cấp dung lượng lưu trữ dồi dào mà còn mang lại tốc độ truy xuất dữ liệu nhanh chóng, giúp bạn làm việc hiệu quả hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("ebc05c76-495f-4cc1-a760-384936eef745"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/58b7700a-6d9d-4372-b465-97ddf8662288.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("860ee9ba-a586-441a-a4e7-fe46d9a335d1"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Màn hình IPS 15.6 inch với độ phân giải Full HD (1920 x 1080) cung cấp hình ảnh rõ nét, màu sắc chân thực, phù hợp với mọi nhu cầu sử dụng từ công việc đến giải trí. Độ sáng 300 nits đảm bảo bạn có thể sử dụng máy thoải mái trong mọi điều kiện ánh sáng. Công nghệ Low Blue Light giúp giảm thiểu ánh sáng xanh, bảo vệ mắt bạn trong thời gian dài làm việc. Bên cạnh đó, màn hình còn được trang bị lớp phủ Anti Glare, chống chói hiệu quả khi sử dụng ngoài trời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("1ffd42d4-03b4-402c-9f8f-57dbccae685d"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Laptop Lenovo sở hữu thiết kế tinh tế với gam màu xám hiện đại, thân máy được hoàn thiện từ lớp vỏ nhựa cứng cáp. Không chỉ đẹp mắt, máy còn đạt tiêu chuẩn độ bền quân đội MIL STD 810H, giúp bạn yên tâm mang theo khi di chuyển. Với khối lượng chỉ 1.61 kg, sản phẩm này dễ dàng nằm gọn trong balo, sẵn sàng đồng hành cùng bạn mọi nơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("6c1b3bbe-a638-4996-9320-83e75e57f9d0"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/5c61fb07-6d62-4255-9510-5c947e455847.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("ad13d2dd-9852-475a-9243-26eec8e829da"), GadgetId = Guid.Parse("a46ce3df-2183-4ef9-845b-672419716dc2"), Value = "Laptop học tập - văn phòng có trang bị cảm biến vân tay, giúp bạn đăng nhập nhanh chóng và an toàn. HD Webcam hỗ trợ chất lượng hình ảnh sắc nét cho các buổi họp trực tuyến, kèm theo đó là công tắc khóa camera để bảo vệ quyền riêng tư của bạn. Máy cũng không thiếu các cổng kết nối hiện đại như: LAN (RJ45), USB 3.2, HDMI, USB-C 3.2,... đảm bảo bạn luôn sẵn sàng kết nối với mọi thiết bị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("6bb9d972-2d6c-4043-a5f3-12cd5da203aa"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop Lenovo ThinkPad E14 Gen 6 Ultra 7 (21M7004UVN) một trong những sự lựa chọn hàng đầu cho những ai tìm kiếm một chiếc laptop văn phòng có hiệu năng mạnh mẽ nhờ chip AI Intel Core Ultra 7 cùng kiểu dáng hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b1b529c6-0348-4a01-9c48-b7d762471ab1"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop Lenovo này sở hữu nét đặc trưng của dòng ThinkPad với vẻ ngoài cứng cáp với các đường nét tinh tế. Vỏ máy được làm từ kim loại - nhôm với màu đen bóng bẩy truyền thống, tạo nên vẻ ngoài thanh lịch và chuyên nghiệp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8e483f79-6ac5-43a8-8fb8-855a143b7e41"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Kích thước máy khá nhẹ 1.44 kg khiến cho việc di chuyển dễ dàng, thích hợp cho những bạn thường xuyên di chuyển nhiều ngoài đường. Máy còn được trang bị đèn bàn phím đơn sắc màu trắng, giúp bạn làm việc hiệu quả ngay cả trong điều kiện ánh sáng yếu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("696eab58-46e2-467b-8470-82b4f9011976"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/401e33b5-fe18-42a3-8a49-ea60e717f1bd.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("499b5c57-c3ab-48ee-bf93-a52ce8f9565f"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Máy đạt độ bền chuẩn quân đội MIL STD 810H đảm bảo máy có thể chịu đựng các điều kiện khắc nghiệt như va đập, nhiệt độ cao và độ ẩm. Ngoài ra, tính năng mở khóa khuôn mặt và bảo mật vân tay giúp bạn truy cập máy nhanh chóng và an toàn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("3a24e0cd-1b7b-416e-987a-95be0b88d06b"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop có các cổng kết nối đa dạng, bao gồm: LAN (RJ45), HDMI, USB 3.2,... đảm bảo kết nối tiện lợi cho mọi nhu cầu công việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("17ab25dc-2a04-4139-bd76-1de3371defa9"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/380d30b5-4fc2-4b15-a0da-f876beea8802.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("eab9504f-123f-4b49-939f-7528eeae0b8f"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Được trang bị công nghệ CPU Intel Core Ultra 7 155U, phù hợp cho nhiều tác vụ khác nhau. Với 12 nhân và 14 luồng, máy xử lý tốt công việc văn phòng, thiết kế đồ họa 2D và render video ngắn. Ngoài ra, con chip này cũng hỗ trợ giải trí đa phương tiện, học tập trực tuyến và phân tích dữ liệu hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("f32dfeca-44f5-4fae-a4f9-3b1985d5a647"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Đây là mẫu laptop RAM 16 GB DDR5 với tốc độ bus 5600 MHz, đảm bảo khả năng xử lý nhanh chóng và hiệu quả các ứng dụng đa nhiệm. Ổ cứng SSD NVMe PCIe 4.0 dung lượng 512 GB mang đến tốc độ đọc/ghi dữ liệu nhanh chóng. Ngoài ra, máy còn khả năng tháo ra và nâng cấp ổ cứng lên tối đa 1 TB, cung cấp không gian lưu trữ rộng rãi hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f7ceedb7-ddf8-4cfa-bb89-4d46b08e91b7"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ca3f4994-5644-406f-8444-90800edb2301.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9ed6e3f8-4418-43c3-8c93-ee7af0c78e0b"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop Lenovo ThinkPad được trang bị card đồ họa tích hợp Intel Graphics, mang lại hiệu suất đồ họa ổn định và tiết kiệm năng lượng. Card đồ họa tích hợp này phù hợp cho các tác vụ hàng ngày như lướt web, xem phim, và làm việc với các ứng dụng văn phòng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("2d037eea-8f77-4da7-b78b-c5caef02e099"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop sở hữu màn hình 14 inch với độ phân giải WUXGA (1920 x 1200), mang đến hình ảnh sắc nét và chi tiết. Tấm nền IPS không chỉ cung cấp góc nhìn rộng mà còn giữ cho màu sắc và độ sáng ổn định từ mọi góc độ, tạo ra trải nghiệm xem tốt hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2f9d3dc2-5827-45cc-a790-77576c5c0361"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Ngoài ra, công nghệ chống chói Anti Glare giúp giảm thiểu hiện tượng chói mắt khi sử dụng laptop trong môi trường ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("9e4f38ab-afc2-47ab-98c1-69b7233dcf42"), GadgetId = Guid.Parse("764ceffa-3e23-432a-8a83-ad80ee86905f"), Value = "Laptop Lenovo ThinkPad E14 còn được trang bị công nghệ âm thanh cao cấp để nâng cao trải nghiệm giải trí và làm việc. Hệ thống âm thanh được hiệu chỉnh bởi Harman/Kardon kết hợp công nghệ Dolby Atmos, mang lại chất lượng âm thanh rõ ràng và sống động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("a8dd18fb-dcea-48c4-98a3-f9c2231bd12d"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Laptop Apple MacBook Air M1 2020 thuộc dòng laptop cao cấp sang trọng có cấu hình mạnh mẽ, chinh phục được các tính năng văn phòng lẫn đồ hoạ mà bạn mong muốn, thời lượng pin dài, thiết kế mỏng nhẹ sẽ đáp ứng tốt các nhu cầu làm việc của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
