new GadgetDescription { Id = Guid.Parse("41d0e728-71b8-415a-878e-8d131f88adb7"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Chiếc MacBook này được trang bị con chip Apple M1 được sản xuất độc quyền bởi Nhà Táo trên tiến trình 5 nm, 8 lõi bao gồm 4 lõi tiết kiệm điện và 4 lõi hiệu suất cao, mang đến một hiệu năng kinh ngạc, xử lý mọi tác vụ văn phòng một cách mượt mà như Word, Excel, Powerpoint,... thực hiện tốt các nhiệm vụ chỉnh sửa hình ảnh, kết xuất 2D trên các phần mềm Photoshop, AI,... máy còn hỗ trợ tiết kiệm được điện năng cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
