new GadgetDescription { Id = Guid.Parse("57987d50-806b-4927-8d1d-aeea4192aaef"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Vỏ máy được thiết kế nguyên khối từ nguyên liệu nhôm tái chế 100% vừa góp phần bảo vệ môi trường vừa cho độ bền cao, chịu va đập tốt bảo vệ các linh kiện bên trong an toàn nếu có xảy ra va chạm mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("26607d50-0c40-4905-b796-3b7d5f1cf5ea"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Magic Keyboard có hành trình phím 1 mm mang lại trải nghiệm gõ nhạy, thoải mái và yên tĩnh, bàn phím còn được trang bị thêm đèn nền vừa tạo cảm giác sang trọng vừa hỗ trợ bạn gõ phím chính xác hơn trong môi trường thiếu sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("f99da8f8-3d4d-4b69-8d86-df1b1cfaba36"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Laptop được trang bị 2 cổng kết nối Thunderbolt 3 với tốc độ truyền tải lên đến 40 Gb/s cho tốc độ truyền tải nhanh gấp 2 lần so với thế hệ Thunderbolt 2 trước đây. Bên cạnh Thunderbolt 3 máy còn được trang bị thêm kết nối không dây là Wi-Fi 6 cho tốc độ truyền tải dữ liệu nhanh chóng lên đến 10.000 Mbps cao hơn Wi-Fi 5 chỉ có 7.000 Mbps và Bluetooth v5.0 có tốc độ tối đa là 50 Mbps cho bạn trải nghiệm mượt mà hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("ef587b54-c022-4ae5-a716-da57b03f2a57"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Apple trang bị cho chiếc Macbook M1 này bảo mật vân tay giúp bạn mở khóa nhanh chóng chỉ với một cái chạm nhẹ. Bên cạnh đó, vân tay của bạn sẽ được tích hợp vào việc xác nhận thông tin khi mua sắm online hay truy cập vào các tài liệu có bảo mật mà không cần phải nhập mật khẩu cực kỳ tiện lợi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
