new GadgetDescription { Id = Guid.Parse("099dbd39-d8cb-4eb0-9fd1-5b39366124fe"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Laptop màn hình Retina 13.3 inch cao cấp cho hình ảnh sắc nét màu sắc tự nhiên. Kết hợp cùng công nghệ True Tone giúp điều chỉnh cân bằng sắc trắng cũng như độ sáng để màu sắc hình ảnh hiển thị chính xác nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
