new GadgetDescription { Id = Guid.Parse("6f942e66-fab2-41d3-9a49-f1ecad868ab7"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Ngoài ra, góc nhìn của macbook air 2020 cũng được mở rộng hơn nhờ có công nghệ IPS đem lại trải nghiệm nhìn đã mắt, dù bạn nhìn màn hình bằng góc nghiêng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("e6fb5c74-98ca-40e0-8d5a-1614c86950c1"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "MacBook Air M1 được trang bị camera FaceTime HD giúp bạn gọi video cho người thân, bạn bè với chất lượng hiển thị cao. Tích hợp dãy 3 micro giúp truyền tải giọng nói của bạn chính xác hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("b2c6a645-b6b2-498e-8860-67688593b676"), GadgetId = Guid.Parse("4148eb06-c456-403c-bf6c-5b78f73f5715"), Value = "Apple MacBook Air M1 2020 với hiệu năng mạnh mẽ đáp ứng tốt mọi công việc, thiết kế cực mỏng sẽ là sự lựa chọn tuyệt vời dành cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("33c08c12-88e9-4107-8509-c51752391ef1"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Sau 14 năm, ba lần sửa đổi và hai kiến trúc bộ vi xử lý khác nhau, kiểu dáng mỏng dần mang tính biểu tượng của MacBook Air đã đi vào lịch sử. Thay vào đó là một chiếc MacBook Air M2 với thiết kế hoàn toàn mới, độ dày không thay đổi tương tự như MacBook Pro, đánh bật mọi rào cản với con chip Apple M2 đầy mạnh mẽ.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ab946681-77a4-4b98-82e7-32a51803e6e2"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Lột xác ngoạn mục với thiết kế thời thượng, sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("cc9e724a-9784-42c1-9a81-cace67cde74f"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Sự đẳng cấp đến từ ngoại hình của chiếc MacBook Air nhà Apple là điều không thể phủ nhận và khó có dòng sản phẩm cùng phân khúc nào có thể qua mặt được. Vẫn là lớp vỏ kim loại nguyên khối cứng cáp cùng các cạnh góc vuông vức, sang trọng nhưng chiếc MacBook Air 2022 năm nay đã thoát ra khỏi ngôn ngữ thiết kế nhẹ nhàng vốn có từ lâu và khoác lên diện mạo mới tương tự như “đàn anh” MacBook Pro.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("c38aa00e-ec21-44df-8f36-a3cdd51ec1c1"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ff487bf4-a1cf-44e1-8d15-f301ac8e89de.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("15aecfda-99fd-4fb2-b9b3-8cb7039a4817"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Bề dày 11.3 mm cùng cân nặng ấn tượng 1.24 kg sẽ là một điểm cộng hoàn hảo cho khả năng di động, luôn sẵn sàng đồng hành cùng một người dùng là sinh viên, dân văn phòng như mình đến trường học, công ty hay cũng thoải mái cho người dùng sáng tạo và đặc biệt là những chuyến công tác xa của doanh nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("b11b8e8f-7ff7-4656-b5a6-dc11b255caaa"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Mặc dù có ngoại hình mỏng và khối lượng nhẹ hơn song MacBook Air mới vẫn không kém phần chắc chắn, độ hoàn thiện tốt so với trước đây. Khung máy cứng, nắp máy gần như không uốn cong khi mình tác động lực và vẫn có thể mở lên bằng một ngón tay. Apple luôn đứng đầu khi nói đến chất lượng sản xuất và chiếc Air mới cũng không ngoại lệ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("23ef9dc9-c170-4dce-a186-0a8265a75fb0"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Cá nhân mình rất thích màu Midnight với lớp hoàn thiện màu xanh đen đậm có thể thay đổi tùy theo ánh sáng nhưng lại có điểm trừ khi dễ bám dấu vân tay, làm mất đi ngoại hình sang chảnh vốn có của một chiếc laptop cao cấp - sang trọng, bạn có thể chọn mua màu xám, bạc hoặc vàng sẽ không dễ bị bám dấu vân tay hoặc chịu khó lau chùi thường xuyên để giữ cho máy luôn sáng bóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("ff4db52e-e2be-4f5a-ae18-c51ce671cb95"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Một sự thay đổi rõ rệt trên chiếc bàn phím Magic Key của dòng laptop Apple M2 năm nay chính là kích thước của hàng phím chức năng F được gia tăng bằng với các dòng phím khác, cho mình thao tác sử dụng chuẩn xác, không bị nhầm lẫn. Hơn nữa trải nghiệm gõ phím cũng rất nhẹ nhàng, êm tay, tiếng ấn cũng nhẹ nhàng hơn so với bàn phím cánh bướm cũ. Kết hợp cùng đèn nền hỗ trợ mình làm việc dễ dàng hơn trong nhiều điều kiện ánh sáng tối khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("3c85d2f2-9cc5-4821-8fce-39e0056ce75d"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Tính năng Touch ID (cảm biến vân tay) tích hợp nút nguồn thay cho mật khẩu cho phép mình mở khóa máy, truy cập các ứng dụng và thanh toán ứng dụng một cách nhanh chóng, không cần tốn nhiều thời gian hay phải nhớ mật khẩu như các kiểu bảo mật truyền thống.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("6c49c81c-50cb-4d6d-be55-9c51dda889c8"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Bàn di chuột không có khác nhiều so với các mẫu cũ mặc dù Apple đã làm rộng hơn một chút, hoạt động giống nhau về mặt chức năng với khả năng di vuốt tuyệt vời, hỗ trợ cử chỉ và từ chối lệnh khi mình lỡ đặt lòng bàn tay lên. Một trong những thay đổi thú vị khác trên chiếc Air mới chính là lưới loa đã được giấu đi, nằm ở bản lề giữa bàn phím và màn hình để giữ cho ngoại hình gọn gàng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("cbfcbfb7-684a-4418-af2d-4dbff9f7a28b"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "MacBook Air M2 2022 sở hữu đầu sạc MagSafe tương tự như MacBook Pro 14 và 16, cho phép mình sạc mà không lo vấp phải dây cáp khiến máy tính rơi xuống sàn. Nhờ có cổng MagSafe mà mình thực sự có thêm một cổng USB-C so với các kiểu máy cũ. Thay vì phải sử dụng một cổng để sạc máy tính và cổng còn lại cho thiết bị ngoại vi, giờ đây mình có thể sử dụng cả hai cổng hỗ trợ Thunderbolt cho các phụ kiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("957771cb-23a6-47d5-aa8b-f54a0e8796c8"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Phá vỡ mọi giới hạn nhờ sức mạnh từ bộ vi xử lý Apple M2", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("5222156d-4da4-4594-9d16-e66de9183a83"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Bộ vi xử lý Apple M2 tuy có mặt cấu tạo tương đồng với M1 nhưng hiệu suất hoạt động cao hơn 18% và nhanh hơn 1.9 lần so với các dòng laptop sở hữu bộ vi xử lý 10 lõi khác, dư sức cân mọi tác vụ từ học tập, làm việc đến đồ họa đến kỹ thuật chuyên sâu. Đồ họa GPU 8 nhân cho khả năng hoạt động mạnh mẽ hơn 35%, xử lý lên đến 15.8 nghìn tỷ tác vụ trong 1 giây nên các công việc liên quan đến hình ảnh, đồ họa, video,... đều được giải quyết rất mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("a2ba16f5-2440-4001-99b3-2f784a00d870"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Máy đáp ứng hoàn hảo việc chỉnh sửa video và ảnh nhẹ, đặc biệt nếu bạn sử dụng ứng dụng Ảnh hoặc iMovie của Apple cho các tác vụ đó. Laptop RAM 8 GB cho phép mình sử dụng cùng lúc nhiều cửa sổ ứng dụng khác nhau với độ đa nhiệm mượt mà đáng kể, vừa làm việc trên Photoshop vừa nghe nhạc vẫn rất trơn tru.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("3841400e-2f2f-4c9b-9030-1846fd7e8d3d"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/28f2c47f-0ac4-4a84-a477-b3327a22042d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("9d033fa0-0fcc-498b-b310-3e4d3ac65e28"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Không gian ổ cứng SSD 256 GB vừa đủ cho các tài liệu, ứng dụng học tập nhưng do chỉ được trang bị một chip NAND duy nhất thay vì hai chip như trên các mẫu M1 hoặc M2 mới có ổ cứng 512 GB trở lên nên tốc độ đọc ghi đã bị giới hạn hơn đáng kể. Mình đã thử đo tốc độ đọc bằng công cụ Black Magic Speed Test được 1543 MB/s, thấp hơn gần một nửa so với con số 2982 MB/s của MacBook Air M1 256 GB.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("9d61cf4b-a2ba-4e13-8972-a8850422519c"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Mình đã kiểm chứng nhiệt độ của máy bằng cách chạy các tác vụ nặng cùng lúc như render video, mở nhiều tab Safari và chạy thêm Photoshop thì sau thời gian dài máy đã nóng lên rõ rệt. Theo mình lý do một phần đến từ ngoại hình mỏng hơn, thêm vào đó là tản nhiệt không có quạt nên máy nóng lên nhanh cũng là điều dễ hiểu. Trên thực tế cũng có ít người sử dụng MacBook Air cho các tác vụ nặng như trên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("4ae0edee-f2be-45bf-a52c-e5fd1034514a"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Sở hữu pin 52.6 Wh nhưng do hiệu năng và nhiệt độ cao nên M2 Air có thời lượng pin tương tự như mẫu M1, cho phép mình sử dụng từ 8 đến 10 giờ ở mức độ sáng 200 nits và nhiều tác vụ làm việc văn phòng khác nhau. Vẫn thoải mái để mình có thể xách máy ra quán cà phê làm việc trong suốt cả ngày dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("33b28bbd-082f-4e9c-a0df-c47208a5ecd6"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Trải nghiệm hình ảnh, âm thanh trên cả tuyệt vời", Type = ParseGadgetDescriptionType("BoldText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("e8d7df31-0b9c-47df-a4f3-0925c8368280"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Màn hình của chiếc Air lớn hơn một chút so với trước đây, 13.6 inch thay vì 13.3 inch nhưng phần lớn kích thước được gia tăng theo chiều dọc. Các viền xung quanh màn hình đã được làm mỏng hơn và các góc cũng được bo tròn, mang đến vẻ ngoài hiện đại hơn trước.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("eb24f2a6-7bda-4b07-851b-763bf241721a"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Màn hình có độ sáng cao 500 nits khi sử dụng trong nhà mình thực sự không nhận ra sự khác biệt nhưng khi xách máy ra quán cà phê mới thấy rõ được sự khác biệt, nội dung luôn được hiển thị với độ sắc nét cao mà không hề bị hiện tượng hắt sáng gây khó chịu. Công nghệ True Tone sẽ tự động điều chỉnh mức độ ánh sáng phù hợp với môi trường, giúp cho nội dung thể hiện tự nhiên hơn và giảm thiểu hiện tượng mỏi mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("77cfa7f5-de85-48d9-bf66-7ae633ce0428"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7b38704f-2898-4e8a-afe3-4b923b324729.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("84aa3f4e-27bc-416f-8018-413261889868"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Độ phân giải Liquid Retina (2560 x 1664) kết hợp cùng dải màu rộng P3 và khả năng hiển thị 1 tỷ màu tạo nên một không gian hoàn chỉnh cho mọi trải nghiệm hình ảnh với màu sắc sinh động và chuẩn xác trên từng vật thể nhỏ nhất. Ngoài ra cũng đáp ứng được nhu cầu làm việc với đồ họa, hình ảnh của mình trên các ứng dụng nhà Adobe như Photoshop, Lightroom,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("6b770b9b-632d-48ac-9916-5e3c1a230055"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Notch màn hình trên chiếc MacBook Air M2 tạo điểm mới mẻ và cũng là điểm nhận dạng thế hệ MacBook mới nhưng lại không cho mình trải nghiệm sử dụng tốt, phần notch không xấu hay gây mất tập trung mà chưa được tùy biến tốt, sẽ bị ẩn đi khi mình mở những ứng dụng làm việc và hiển thị trở lại khi sử dụng ở dạng cửa sổ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 23 },
new GadgetDescription { Id = Guid.Parse("cfe2b7dd-a581-4949-9446-564a4e1f29da"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Bù lại Camera Facetime HD 1080p bên trong chiếc notch có nhiều cải tiến cho hình ảnh sắc nét hơn, màu sắc và độ tương phản cũng tốt hơn. Kết hợp với dải ba micrô giúp cho quá trình học tập, làm việc trực tuyến hay gọi video của mình diễn ra suôn sẻ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 24 },
new GadgetDescription { Id = Guid.Parse("beebc756-1939-4148-99fa-7c1bc668be7f"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "Hệ thống bốn loa mới bao gồm hai loa tweeter, hai loa trầm cùng công nghệ Spatial Audio và Dolby Atmos cho mình trải nghiệm âm thanh to rõ, ít tiếng vang hơn khi chỉnh ở mức âm lượng tối đa. Chất lượng vượt xa so với các dòng máy tính khác, cung cấp âm thanh rõ ràng khi xem video, nghe nhạc, YouTube,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 25 },
new GadgetDescription { Id = Guid.Parse("f72e360d-a38f-414c-ae7e-5b645abe074c"), GadgetId = Guid.Parse("96da0615-c1b2-44ca-b0fb-4b90fd3b5850"), Value = "MacBook Air M2 2022 chính là sự kết hợp hoàn hảo giữa phong cách thiết kế thời thượng, hiệu năng vượt trội cùng màn hình hiển thị sắc nét, là một cánh tay phải đắc lực cùng mình đối đầu mọi thách thức.", Type = ParseGadgetDescriptionType("NormalText"), Index = 26 },
new GadgetDescription { Id = Guid.Parse("8e37c9f6-7541-4b6e-a401-e80a5843370d"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Tiếp nối những thành công mạnh mẽ từ phiên bản Macbook Air M2, thì nay MacBook Air M3 đã quay lại thật bùng nổ vào những tháng đầu 2024 này. Với con chip M3 cải tiến tuyệt vời, ngoại hình đến tiện ích \"miễn chê\" sẽ cho bạn trải nghiệm những chế độ công việc tuyệt vời.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("91258213-11a4-402a-98a3-71a573db733d"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Nguồn tài nguyên tuyệt vời từ dòng chip thế hệ mới", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("5ccd6090-2ddf-4bca-8986-d5347baa3173"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Macbook Air sử dụng con chip Apple M3 với 8 nhân CPU được sản xuất dựa trên tiến trình 3 nm mới, mỏng nhẹ hoàn toàn nhưng lại sở hữu mức hiệu năng theo hãng nhận định là vượt trội hơn hẳn các thế hệ trước, 1.6x lần nhanh hơn thế hệ M1 và vận dụng nhanh hơn đến 13x lần đối với thế hệ chip Intel.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("df9ed20b-b105-421d-b5fb-bc4b43f4d4dc"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/af76d683-082f-47ce-ba07-1af69e5c6e27.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("46031258-3fbb-4cc5-a6d6-8380ab5838ca"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Với khả năng tiếp cận một hệ sinh thái ứng dụng rộng lớn hỗ trợ cho các tính năng AI tiên tiến, chất lượng công việc cũng được nâng lên đáng kể từ tự động cải thiện hình ảnh trong Pixelmator Pro, cho đến loại bỏ tiếng ồn trong nền khỏi video bằng CapCut.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c7cf35b8-10eb-4afe-84bb-a1f05edf09d3"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Thực hiện thật hoàn hảo từ những tác vụ công việc hàng ngày, Macbook Air M3 còn chinh phục người dùng sáng tạo khi trang bị 8 nhân GPU vừa đảm nhận các nhiệm vụ tạo dựng và xuất hình ảnh đáng kinh ngạc, cho phép bạn thao tác mượt mà các ứng dụng đồ họa như Adobe Illustrator, Premiere,... vừa chơi các video game đồ hoạ hay phát phim ở mức phân giải 4K.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("81673f25-f013-4d11-b063-422c4cb15afd"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Đảm nhiệm linh hoạt và mượt mà khối lượng công việc lớn cùng lúc với RAM 8 GB đi cùng băng thông bộ nhớ đạt 100 GB/s, chuyển qua lại nhiều phần mềm mà không gặp bất cứ khoảng dừng nào. Ổ cứng SSD 256 GB cho không gian lưu trữ ổn định đồng thời gia tăng tốc độ đọc ghi nhanh chóng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("3096df19-26a1-4ab8-b048-80313b7f5d50"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Hệ điều hành macOS tích hợp trên các mẫu máy của Apple Silicon mang đến kho ứng dụng cực khủng từ chỉnh sửa nội dung cho công việc, học tập đến giải trí cá nhân, đồng thời còn nâng cao trình bảo mật hệ thống, giữ tính an toàn cao cho thông tin của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("b12c40dc-1b26-4eb0-b7af-7996615dbbe7"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Ngoại hình hiện đại, biểu tượng của nét sang trọng", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("902d01a6-d245-478f-8b77-0951304f232b"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Những chiếc Macbook Air 13 inch đã có sự cải tiến đáng kể về mặt thiết kế vào khoảng 3 năm trước mà vẫn duy trì kiểu dáng đó cho đến bây giờ với các bề mặt dát phẳng khi chế tác từ vỏ nhôm tái chế 100%, thân máy mỏng cùng khối lượng chỉ vỏn vẹn 1.24 kg đặc trưng cho cái tên \"Air\" của thiết bị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("36a83d7e-ddc7-457b-b405-b93c930e8975"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3404649e-73db-495d-8680-e6183f3ede96.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("6df8e63d-ca90-43a9-93a3-493925defd0b"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Bàn phím Magic Key với độ cao phím tiêu chuẩn, thoải mái khi bấm gõ, hàng phím chức năng cùng bộ phím tắt cực kì thuận tiện cho các công việc thường ngày. MacBook luôn là sự lựa chọn hàng đầu cho những người làm việc văn phòng hay yêu thích sự yên tĩnh với trải nghiệm gõ phím cực êm tay và hạn chế tối đa việc ảnh hưởng tiếng ồn đến những người xung quanh. Đèn nền có thể tinh chỉnh độ sáng tuỳ theo bất cứ hoàn cảnh môi trường nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("b6e87bbf-13e7-4bd7-bfe4-f207e6d1e810"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Tính năng bảo mật vân tay được tích hợp với phím nguồn của máy vừa giúp bạn mở máy nhanh chóng chỉ bằng một chạm, vừa củng cố thêm hệ thống bảo mật thông tin riêng tư khi truy cập các trang web và truy xuất thông tin trong quá trình sử dụng hàng ngày. 3 microphones hỗ trợ tối đa cho quá trình học tập và làm việc từ xa, thu âm thanh ấn tượng để nâng cao hiệu suất công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("40b94217-c64a-4a19-b1ad-29632968013d"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Hai cổng Thunderbolt 3 / USB 4, Jack tai nghe 3.5 mm cùng MagSafe 3 đã đem lại cho máy vẻ đẹp tối giản nhưng vẫn đảm bảo truyền tải mọi dữ liệu hay hình ảnh, video,... đến các thiết bị ngoại vi khác một cách nhanh chóng và dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("a7220ed0-80e2-46a2-95d2-eafa7f1735e9"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Màn hình Retina cực nét", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("01fac11f-45d5-4458-9920-f51c06d02f98"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Kích thước màn hình 13.6 inch có độ phân giải Liquid Retina (2560 x 1664) cùng độ hiển thị tối ưu đến 1 tỷ màu đưa đến những mảng màu sắc đỉnh cao để giải trí, tính tương phản cao cùng độ sắc nét tuyệt vời của hình ảnh sẽ khiến bạn đắm mình sâu vào những cung bậc cảm xúc trong các bộ phim yêu thích, làm việc và học tập đều tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("0cf9bc42-467d-4ae6-9d91-91a547248a88"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c11a1bbf-fde4-426d-bab7-44482531c316.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("9a44eb62-9913-42ab-9b6f-cd0000c104b3"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Với độ sáng 500 nits, người dùng hoàn toàn tự tin khi sử dụng máy ở những điều kiện môi trường bên ngoài, dưới ánh đèn hay cạnh cửa sổ mà vẫn quan sát tốt nội dung. Dải màu rộng P3 cùng công nghệ True Tone cho các khung ảnh được tái tạo chuyên nghiệp và chân thực nhất so với điều kiện ánh sáng xung quanh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("f8d33c9f-9590-4235-8765-d69596976cd5"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "Hệ thống âm thanh vòm đa chiều Dolby Atmos, Spatial Audio kết hợp cùng 4 loa trên laptop mang đến trọn vẹn những trải nghiệm giải trí chân thực với chất âm sống động, âm bass to rõ, độ khuếch đại lớn để bạn hòa mình vào những bản tình ca du dương hay các tập phim hành động kịch tính.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("fdf42dba-2684-4c01-a0e2-78c0278a9053"), GadgetId = Guid.Parse("bb1bf14f-4124-4a34-9007-ba499305d64b"), Value = "MacBook Air 13 inch M3 2024 phiên bản lần này với hiệu năng được cải tiến tối ưu, hơn nữa cho chất lượng công việc, tiện nghi sử dụng cùng ngoại hình mỏng nhẹ đầy thu hút vẫn được giữ nguyên và còn có phần nâng cấp hơn trước, chắc chắn sẽ không làm các tín đồ nhà Táo thất vọng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("79870784-1c5e-40b2-924c-b2944edd2fec"), GadgetId = Guid.Parse("cbb9f5b2-c0ca-4d8e-bc42-5f7e71281361"), Value = "MacBook Air 15 inch M2 2023 đã có phiên bản cải tiến vừa được nhà Apple cho ra mắt, thêm không gian cho những điều bạn yêu với màn hình Liquid Retina 15 inch ấn tượng. Với người dùng yêu thích hiệu năng \"nhanh như chớp\" trong một thiết kế siêu gọn nhẹ của dòng Air thì đây chắc chắn là một sản phẩm bạn không nên bỏ qua.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
