new GadgetDescription { Id = Guid.Parse("2b7b0279-7324-4e1e-941f-3bb393015bff"), GadgetId = Guid.Parse("cbb9f5b2-c0ca-4d8e-bc42-5f7e71281361"), Value = "Khả năng xử lý đồ họa, chinh phục các tác vụ thiết kế nhanh chóng và hiệu quả card tích hợp 10 nhân GPU. Chiếc Macbook Apple này cho phép bạn thao tác mượt mà các ứng dụng đồ họa như Adobe Illustrator, Premiere,... thực hiện thiết kế, sáng tạo cùng với trình phát video nâng cao đều không thành vấn đề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("aa4e0b64-55e3-4d8c-92ea-0c1e6cc17962"), GadgetId = Guid.Parse("cbb9f5b2-c0ca-4d8e-bc42-5f7e71281361"), Value = "Bộ nhớ RAM 8 GB mang đến khả năng đa nhiệm mượt mà, thực hiện xử lý công việc với nhiều layer đồ họa cùng lúc ứng dụng khác như Spotify hay YouTube mà không gặp hiện tượng giật lag. Với ổ cứng SSD 256 GB cung cấp một không gian lưu trữ vừa đủ cho các tệp tin, ứng dụng và trò chơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
