new GadgetDescription { Id = Guid.Parse("aa0b1420-1c9d-4e54-8199-0a0eb6776ef8"), GadgetId = Guid.Parse("92cb2844-5aa4-41d7-814f-8fe28c84e5ff"), Value = "Dòng MacBook Air M3 luôn là biểu tượng cho những mẫu laptop cao cấp - sang trọng bởi tính mỏng gọn chỉ 11.5 mm và khối lượng 1.51 kg chỉ như bỏ thêm một cuốn sách. Hơn nữa, lớp vỏ nhôm tái chế 100% không chỉ tăng độ bền bỉ thêm đáng kể mà còn nâng cao nét thẩm mỹ vốn đặc trưng của dòng máy.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("aca54823-10c5-4831-a419-cdd5b4e5b3af"), GadgetId = Guid.Parse("92cb2844-5aa4-41d7-814f-8fe28c84e5ff"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/340e617c-0124-47a2-a0b0-7adf9e457f65.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("0d9f2bcf-cb84-45e5-b5c4-0926f1337178"), GadgetId = Guid.Parse("92cb2844-5aa4-41d7-814f-8fe28c84e5ff"), Value = "Mang đến nhiều sự lựa chọn theo thẩm mỹ của người dùng với 4 phối màu cực sang trọng: xám, xanh đen, vàng, bạc. Với mẫu thiết kế MacBook năm nay, vẫn sẽ sở hữu những đường nét quen thuộc, hệ thống cổng kết nối Thunderbolt 3 / USB 4 cùng Jack tai nghe 3.5 mm, cổng sạc nam châm MagSafe 3 được bố trí gọn ở 2 cạnh bên, khung màn hình tràn viền với tai thỏ là cụm camera bên trên màn hình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("b33e3b90-38d2-454a-93ff-3cf4d4714a15"), GadgetId = Guid.Parse("92cb2844-5aa4-41d7-814f-8fe28c84e5ff"), Value = "Magic Keyboard với hàng phím chức năng có chiều cao tiêu chuẩn, tạo cảm giác êm ái có độ tĩnh lặng khi thao tác gõ, phù hợp với đối tượng người dùng làm văn phòng, yêu cầu có sự yên lặng để tập trung cao độ khi làm việc. Hệ thống phím tắt cực thuận lợi cho mọi thao tác, chỉ cần bỏ chút thời gian làm quen và hiệu suất công việc của bạn sẽ được tăng lên rất nhiều. Bên cạnh đó, đèn nền bàn phím còn giúp ích rất nhiều khi làm việc trong môi trường tối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("7bea5ccf-9ac9-4760-83b4-8ec89b5a8db7"), GadgetId = Guid.Parse("92cb2844-5aa4-41d7-814f-8fe28c84e5ff"), Value = "Touch ID hỗ trợ các thao tác truy cập vào máy hay mở khóa các ứng dụng và trang web nhanh chóng, dễ dàng với một chạm đơn giản.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("e3cb7527-40d9-451d-b559-4c6c132a7154"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Siêu phẩm Apple MacBook Air 13 inch M2 16GB/256GB đã chính thức trình làng, mang đến một bước tiến vượt bậc về hiệu năng, hứa hẹn khuấy đảo thị trường máy tính xách tay.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("253cb3a2-31c7-41cf-b6c3-27a59d4ff012"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Hiệu năng mạnh mẽ từ chip Apple M2", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("99f4171b-558c-4f8f-a063-2e2c2a9ef102"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "MacBook Air được trang bị chip Apple M2, mang đến hiệu suất CPU mạnh mẽ hơn 18% so với thế hệ M1 trước đây. Nhờ vậy, bạn có thể hoàn thành mọi tác vụ học tập và làm việc từ cơ bản đến nâng cao một cách mượt mà và nhanh chóng hơn bao giờ hết. Dù là xử lý văn bản, chỉnh sửa ảnh, thiết kế đồ họa, lập trình hay chơi game, máy đều có thể đáp ứng một cách hoàn hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0ca92e2d-0593-4dda-8d51-bfb755932534"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/79ada5b5-ebea-4a3b-9649-a315f01c4272.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("993237b0-95ef-4e5c-ae58-667ac8917e2a"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Với 20 tỷ bóng bán dẫn được sản xuất trên tiến trình 5 nm tiên tiến, MacBook không chỉ mang đến hiệu năng vượt trội mà còn tiết kiệm điện năng đáng kể. Nhờ vậy, bạn có thể thoải mái sử dụng máy trong thời gian dài mà không lo bị gián đoạn bởi tình trạng hết pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("c2f36611-c5b7-4095-aaca-b85bec92bae2"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Sở hữu 10 nhân GPU mạnh mẽ, giúp bạn xử lý mượt mà mọi tác vụ đồ họa, kỹ thuật chuyên sâu như chỉnh ảnh, thiết kế, render video,... Hiệu suất đồ họa của máy cao hơn 35% so với chip M1, cho phép bạn sáng tạo không ngừng nghỉ mà không lo bị giật lag hay gián đoạn. Dù bạn là nhiếp ảnh gia, nhà thiết kế đồ họa hay chuyên gia chỉnh sửa video đều sẽ là người bạn đồng hành hoàn hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("95da0363-4df7-4f8e-a6f1-30cc74e05b31"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Ổ cứng SSD 256 GB cung cấp không gian lưu trữ đủ dùng cho mọi nhu cầu công việc và giải trí. Ngoài ra, ổ cứng còn giúp tăng tốc độ khởi động máy và truy cập ứng dụng chỉ trong vài giây, tiết kiệm thời gian cho bạn. Nhờ vậy, bạn có thể tập trung hoàn toàn vào công việc mà không lo bị ảnh hưởng bởi tốc độ khởi động máy chậm chạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("f88093c6-9600-4644-9e5e-494d5bc8466b"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Với bộ nhớ RAM 16 GB cho phép bạn làm việc cùng lúc trên nhiều cửa sổ Safari khác nhau mà không lo bị giật lag hay treo máy. Bạn có thể thoải mái mở nhiều ứng dụng cùng lúc để tra cứu thông tin, soạn thảo văn bản, chỉnh sửa ảnh,... mà vẫn đảm bảo hiệu quả công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("e356db9a-b1f5-42b0-aa17-d9ba01b1b28f"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Thiết kế của tương lai, đẳng cấp", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("4cdf26ce-d091-4f36-8d83-cfb59f4ea8df"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "MacBook Air 13 inch đánh dấu bước ngoặt mới trong ngôn ngữ thiết kế của Apple, mang đến một diện mạo hoàn toàn mới mẻ và đẳng cấp hơn. Vẫn giữ nguyên nét sang trọng, tinh tế với lớp vỏ kim loại nguyên khối được gia công tỉ mỉ, MacBook Air M2 giờ đây sở hữu thiết kế vuông vức tinh tế, toát lên phong thái thời thượng pha lẫn lịch lãm, kiêu kỳ, đại diện cho sự hiện đại và đẳng cấp, khẳng định vị thế dẫn đầu của Apple trong lĩnh vực thiết kế laptop.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("16864cd2-9e90-492a-baca-1faa6ffc8c45"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Với khối lượng siêu nhẹ chỉ 1.24 kg trở thành chiếc laptop cao cấp - sang trọng hàng đầu trong phân khúc, mang đến cho bạn sự linh hoạt tối đa trong mọi nhu cầu sử dụng. Dễ dàng cầm nắm bằng một tay hoặc cất gọn vào balo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("6acf2f82-0438-4c35-9dc4-053d3840a4cc"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/7a2edf6e-7615-4c45-b6cd-d1162b886c64.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("962ce253-25ca-437e-903a-598a268dbb33"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Tính năng bảo mật vân tay tích hợp với phím nguồn của laptop không chỉ giúp bạn mở máy nhanh chóng chỉ bằng một chạm, mà còn củng cố thêm hệ thống bảo mật và quyền riêng tư. Điều này giúp hạn chế tối đa việc bị mất thông tin cá nhân của người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("847949a8-5122-4d6b-b3a4-f0ad9a85f645"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Nếu bạn thường xuyên tham gia các cuộc họp trực tuyến, 1080p FaceTime HD camera cùng 3 microphones sẽ hỗ trợ tối đa cho quá trình học tập và làm việc của bạn với chất lượng hình ảnh sắc nét cùng giọng nói được thu âm rõ ràng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("0a04fc46-0bfa-49d3-b867-31a585270100"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Ngoài ra, máy còn được trang bị cổng Thunderbolt 3 cho phép bạn truyền tải dữ liệu, hình ảnh và video đến các thiết bị ngoại vi khác một cách nhanh chóng và dễ dàng, jack tai nghe 3.5 mm và sạc nam châm MagSafe đảm bảo sự an toàn khi sạc pin và giúp bạn dễ dàng kết nối sạc mà không cần tìm đúng chiều.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("8ffc2e99-fb28-4173-ae2b-c2cd831c6c48"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Hình ảnh sống động, sắc nét", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("b3a5b794-a03f-472f-8d30-2a34016c545e"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Kích thước màn hình 13.6 inch và độ phân giải Liquid Retina (2560 x 1664) cùng 1 tỷ màu cho khung ảnh siêu sắc nét, sống động và màu sắc chuẩn xác, bạn có thể thực hiện các tác vụ chỉnh sửa hình ảnh và video một cách chính xác và chuyên nghiệp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("7de16aef-a81c-4455-8251-0fec0c576fff"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/005b295a-d061-4de2-a688-b82cc24fbab0.jpgg", Type = ParseGadgetDescriptionType("Image"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("d46bfe4c-863e-48e7-8705-ce90b00b181b"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Với độ sáng 500 nits cho phép bạn sử dụng máy ở điều kiện môi trường nắng gắt mà vẫn nhìn rõ được nội dung hiển thị. Điều này đảm bảo bạn hoàn toàn tự tin khi làm việc hoặc giải trí ngoài trời. Các khung ảnh trên màn hình được tái tạo chân thực nhất dựa trên điều kiện ánh sáng xung quanh nhờ Wide color (P3) và công nghệ True Tone.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("e265fdb9-d687-4207-bbc0-c1afac5e8eea"), GadgetId = Guid.Parse("01df67c2-976f-4d36-a17e-fdbc18dd0832"), Value = "Hệ thống âm thanh vòm Dolby Atmos, Spatial Audio và 4 loa, MacBook Air M2 mang đến chất âm sống động, âm bass to rõ và độ khuếch đại âm thanh lớn. Bạn có thể hòa mình vào những bản tình ca du dương hoặc thưởng thức các tập phim hành động kịch tính một cách tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("65407024-1590-4c60-96f3-468448f9443a"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Bạn muốn tìm một mẫu laptop cho sinh viên trong học tập, nhân viên văn phòng trong mọi việc hàng ngày có cấu hình ổn định nhưng lại không có mức giá thành quá cao. Laptop Samsung Galaxy Chromebook Go XE310XDA N4500 chắc chắn là sự lựa chọn đáng cân nhắc nhất trong phân khúc.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("2c6327bb-1cc5-4764-81ab-bd20985ab1b6"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Chromebook là dòng máy tính xách tay nhỏ gọn, bền bỉ, an toàn với khả năng bảo mật cao, hoạt động trên nền tảng hệ điều hành Google Chrome. Được thiết kế để tối ưu hóa việc sử dụng các dịch vụ đám mây, Chromebook cho phép mọi hoạt động từ lưu trữ tệp tin đến sử dụng ứng dụng đều được thực hiện trực tuyến. Điều này giúp đồng bộ hóa dữ liệu dễ dàng trên mọi thiết bị mà bạn đăng nhập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("54d9e266-a7b3-48f6-9caf-6d30548d2b6b"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/0757d668-74b2-4300-8edc-24932530fa66.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("0a012bde-71f6-4d67-b82e-0e21724d4063"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Cho trẻ tham gia thế giới trực tuyến một cách an toàn với hệ điều hành Chrome OS sự quản lý của phụ huynh, định vị được trẻ khi di chuyển. Ngoài ra, nó còn hỗ trợ một số tính năng thông minh như chỉnh sửa video, viết code, vẽ,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("50a760fa-fa69-468f-b0f7-14bc9c2bd137"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Thiết kế siêu nhẹ, siêu bền với cân nặng chỉ 1.06 kg và vỏ nhựa màu bạc thanh lịch, Galaxy Chromebook Go không chỉ dễ dàng mang theo mà còn thể hiện phong cách hiện đại. Độ bền cao và khả năng chống va đập và cấn móp giúp máy luôn bền bỉ trong khi bỏ trong các không gian hẹp, phù hợp cho cả môi trường văn phòng và những chuyến công tác xa.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e421764a-827a-439a-8d98-b493a724199c"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9c9725b7-d45e-4bf9-9947-b57ca49523cf.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("7b5f33f3-4e78-4def-8131-c7ad2d505a6b"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "HD webcam tích hợp sẵn giúp bạn tham gia các cuộc họp trực tuyến qua Google Meet hay học online trên Microsoft Teams mà không cần thêm thiết bị phụ trợ. Galaxy Chromebook Go được trang bị các cổng kết nối thông dụng như: USB Type-C, Jack tai nghe 3.5 mm và USB 3.2 giúp bạn dễ dàng kết nối với các thiết bị ngoại vi và phụ kiện, nâng cao khả năng làm việc và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b822da7c-ccaf-4c88-98f3-362728cc8d15"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Laptop Samsung cũng được trang bị chip Intel Celeron N4500, Galaxy Chromebook Go mang đến hiệu suất đáng tin cậy, cho phép bạn xử lý mượt mà các tác vụ văn phòng như soạn thảo văn bản bằng Microsoft Word, tạo bảng tính trên Excel và trình chiếu PowerPoint. Không chỉ dừng lại ở đó, máy còn đảm bảo trải nghiệm duyệt web bằng Google Chrome, xem phim HD trên Netflix và chỉnh sửa hình ảnh cơ bản với ứng dụng Canva một cách trơn tru.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("71b5cd52-256a-4c8f-9416-6ca3d91cab04"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/f0f87809-b375-4cad-a204-c426016d7d2f.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("b2b16d7d-5f75-4219-bfe9-56f86b1251e4"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Với dung lượng RAM 4 GB LPDDR4X Onboard, laptop học tập - văn phòng cho phép bạn mở nhiều ứng dụng cùng lúc mà không lo bị đơ hay lag. Điều này giúp tăng cường hiệu suất làm việc và tiết kiệm thời gian đáng kể. Bạn có thể dễ dàng chuyển đổi giữa các tab trên Chrome, làm việc trên Google Docs và kiểm tra email trên Outlook mà không gặp trở ngại nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("7e0a80e7-1e3a-479b-9254-351a79727972"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Ổ cứng eMMC 32 GB giúp bạn dễ dàng lưu trữ các tệp tin cần thiết. Khi cần thêm dung lượng, bạn có thể mở rộng qua khe cắm micro SD, đảm bảo đủ không gian cho mọi dữ liệu quan trọng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("260100f0-f895-460f-9349-3c39972720c6"), GadgetId = Guid.Parse("a4dc2147-a30f-43fe-98eb-95c7a4b50744"), Value = "Màn hình laptop kích thước 11.6 inch hiển thị được độ phân giải HD (1366 x 768) được phủ với tấm nền chống chói Anti Glare mang lại hình ảnh rõ nét, sắc sảo từ mọi góc nhìn. Viền màn hình mỏng giúp tối ưu hóa không gian hiển thị, tạo cảm giác rộng rãi và thoải mái khi sử dụng. Màn hình có kích thước nhỏ hơn tạo độ linh động cao trong di chuyển, chỉ cần bật máy và bạn đã có thể sử dụng thật hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("27ac7d5a-5037-4b04-9e37-484450430071"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Bạn đang tìm kiếm một chiếc laptop học tập và văn phòng giúp xử lý hiệu quả mọi tác vụ công việc và giải trí hàng ngày? Hãy cân nhắc đến laptop Samsung Galaxy Chromebook Go XE340XDA N4500, một thiết bị mang lại hiệu năng ổn định, thiết kế tinh tế và mức giá lý tưởng, chắc chắn sẽ đáp ứng đầy đủ nhu cầu của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("a93ff536-a975-4a83-8a94-8cd416e7bdaa"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Chromebook là chiếc máy tính xách tay nhẹ, bền, an toàn, bảo mật tốt, chạy với hệ điều hành Google Chrome - được thiết kế nhằm hoạt động chính trên đám mây. Điều này nghĩa là mọi tính năng từ file, ứng dụng,... đều hoạt động online (không hoạt động cục bộ trong máy tính xách tay của bạn) và nó sẽ đồng bộ hoá ở tất cả thiết bị mà bạn đăng nhập.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("af1bc50e-9f50-4926-9856-f1543b4a35dc"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/89105a2f-e02f-468a-bd76-38d326c7c16c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("c3425203-21de-4c84-b919-e0dc6653119e"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Với hệ điều hành Chrome OS cho bạn nhiều tính năng thú vị như chỉnh sửa video, học cách viết code hay vẽ nhiều tác phẩm nghệ thuật. Ngoài ra, nó cũng hỗ trợ tạo tài khoản giám sát cho trẻ, định vị trẻ em dù đang ở bất cứ đâu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4edd4fee-8381-4256-a230-e3475c5948cc"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Galaxy Chromebook Go được trang bị bộ vi xử lý Celeron - N4500, kết hợp cùng đồ họa Intel UHD Graphics, giúp bạn thực hiện trơn tru các tác vụ văn phòng như soạn thảo văn bản, trình chiếu PowerPoint hay tạo bảng tính trên Excel. Ngoài ra, cấu hình cũng cho khả năng xử lý đa nhiệm và các công việc hàng ngày như duyệt web, xem phim và thậm chí chỉnh sửa hình ảnh cơ bản cũng đều được đáp ứng tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("d1bef2a0-0976-4be0-8dbd-ddb39fc4f3af"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6a89c156-d428-4753-893c-806aa645ae8a.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("6a74df27-880d-44db-bb20-7af2d245e506"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Với RAM 4 GB LPDDR4X của laptop, bạn có thể mở nhiều tác vụ cùng lúc mà ít gặp hiện tượng đơ hay lag, bổ trợ cho tính làm việc đa nhiệm. Ổ cứng eMMC 32 GB cung cấp không gian lưu trữ ổn định, giúp khởi động máy và truy xuất ứng dụng nhanh hơn. Ngoài ra, bạn có thể mở rộng dung lượng lưu trữ thông qua khe cắm micro SD, đáp ứng nhu cầu lưu trữ cao hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("99d28e74-9807-4be9-b21a-128df877e8dc"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Màn hình 14 inch HD (1366 x 768) của Galaxy Chromebook Go sử dụng tấm nền chống chói Anti Glare, mang lại trải nghiệm rõ nét và chi tiết ở nhiều tình huống hiển thị văn bản, xem phim. Kích thước mỏng gọn của các viền màn hình cũng giúp người dùng dễ dàng quan sát và tập trung hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("59915991-147b-4b82-99e2-a8ee9d453508"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6a46c302-68ff-4afd-8a26-d916983ba8b0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d45360f0-cd49-4d58-9d8c-e8be2d5847ec"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Laptop học tập - văn phòng có thiết kế mỏng nhẹ với vỏ nhựa màu bạc thanh lịch, khối lượng chỉ 1.145 kg cực kỳ dễ dàng mang theo đến mọi nơi. Độ bền cao, chống va đập tốt giúp bạn yên tâm sử dụng trong nhiều tình huống khác nhau, từ công việc đến giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("eb95bdac-8cbf-4a6d-ab28-2d6002fe1ef1"), GadgetId = Guid.Parse("26d411d3-d636-4c7b-a8a7-352c1aa639d1"), Value = "Được trang bị HD webcam, Galaxy Chromebook Go giúp bạn dễ dàng tham gia các cuộc họp trực tuyến hay học online mà không cần phải kết nối thêm thiết bị ngoài. Laptop Samsung cũng tích hợp với các cổng kết nối thông dụng như: Jack tai nghe 3.5 mm, USB 3.2, USB Type-C đảm bảo bạn có thể kết nối với nhiều thiết bị cần thiết một cách thuận tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("a4d0f3d1-0dfa-40a3-9b7d-4adb2cfabb0c"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Laptop HP 15 fd0015TU i7 A19C5PA là sự lựa chọn lý tưởng cho những ai cần một chiếc máy tính xách tay mạnh mẽ và đa năng. Với hiệu năng mạnh mẽ, thiết kế trang nhã và màn hình 15.6 inch sắc nét máy đáp ứng mọi nhu cầu sử dụng hằng ngày của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("f8350d0c-682d-4d35-b593-2fa55d04cb8c"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Laptop HP nổi bật với thiết kế màu vàng sang trọng và tinh tế. Vỏ máy được làm từ chất liệu nhựa cao cấp, vừa nhẹ nhàng vừa bền bỉ, tạo cảm giác chắc chắn khi cầm nắm. Logo HP nổi bật ở mặt lưng, tạo điểm nhấn đặc trưng và mang lại vẻ ngoài chuyên nghiệp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("6ebcdae6-be1f-4a62-9914-4d54e31e2d38"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b9932eb8-41d2-42cf-9b86-345d8d8ce3d3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("931a6d22-b68d-4a3c-b9b6-a2645fcb0f42"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Bàn phím được thiết kế khoa học với hành trình phím vừa phải, mang lại cảm giác gõ thoải mái và chính xác, hỗ trợ tốt cho công việc văn phòng và học tập. Touchpad rộng, nhạy và mượt mà, cho phép người dùng thao tác dễ dàng và chính xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("cbde353a-49f6-42e0-9af9-947cccb593c3"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Ngoài ra, máy còn được trang bị đầy đủ các cổng kết nối phổ biến như: USB, HDMI,.., đáp ứng tốt nhu cầu kết nối với các thiết bị ngoại vi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("76fcac5b-5818-4de6-bb22-731313dbd71a"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ddc5c60d-f750-4086-ac44-81bbd638facf.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("b0d90cb6-9c75-4b24-bcb6-39742d7f513e"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Laptop HP cơ bản mang đến hiệu năng mạnh mẽ nhờ vào bộ vi xử lý Intel Core i7 Raptor Lake - 1355U với 10 nhân và 12 luồng. Máy hỗ trợ tốt các công việc văn phòng cũng như giúp chỉnh sửa ảnh và video, lập trình ở mức cấu hình trung bình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("201ecf98-1811-4b03-a994-1f8ee6f8832d"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Laptop RAM 16 GB DDR4 với hai khe cắm (mỗi khe 8 GB), tốc độ bus RAM đạt 3200 MHz, mang lại khả năng xử lý đa nhiệm mượt mà và nhanh chóng. RAM tối đa hỗ trợ là 16 GB, đủ sức đáp ứng các nhu cầu làm việc và giải trí phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("6c3e3919-5ede-4955-a4da-4a2cf04d73c6"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Ổ cứng SSD NVMe PCIe dung lượng 512 GB không chỉ cung cấp không gian lưu trữ rộng rãi mà còn cho tốc độ đọc/ghi dữ liệu vượt trội, giúp khởi động hệ điều hành và các ứng dụng nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("c7b1031a-2833-420b-a002-0828d40d24ff"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/792e9443-c2d7-4000-ab54-96f522e2681a.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("97f2eb51-8a0d-4cc1-baae-8b9557ec17ae"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Mẫu laptop học tập văn phòng này đi kèm với card đồ họa tích hợp Intel Iris Xe Graphics, mang lại khả năng xử lý đồ họa mạnh mẽ trong nhiều tác vụ. Card đồ họa này hỗ trợ tốt cho các công việc văn phòng, lướt web, và giải trí trực tuyến như xem video độ phân giải cao và chơi các tựa game phổ biến với yêu cầu cấu hình trung bình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f8a488b4-f849-4b91-a55a-c8a0eb5a4a20"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Laptop sở hữu màn hình 15.6 inch với độ phân giải Full HD (1920 x 1080), mang lại chất lượng hình ảnh sắc nét và chi tiết. Màn hình này có tần số quét 60 Hz, đáp ứng tốt các nhu cầu xem video, làm việc và chơi game cơ bản.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("3736e506-f28d-4bef-9b02-ad6b53bd89f0"), GadgetId = Guid.Parse("2181ef25-20b4-468b-9d8e-6c8c67ab6d04"), Value = "Độ phủ màu đạt 62.5% sRGB, giúp hiển thị màu sắc trung thực và khá phù hợp cho các công việc như chỉnh sửa ảnh và thiết kế đồ họa cơ bản. Công nghệ chống chói Anti Glare cùng độ sáng 250 nits giúp giảm thiểu tình trạng chói lóa khi sử dụng máy dưới ánh sáng mạnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("0ff19bf2-178e-48fa-833d-3e53346ad6b1"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Bạn đang muốn tìm kiếm một mẫu laptop mang hiệu năng ổn định, thiết kế hiện đại lại còn sở hữu một mức giá phù hợp kinh tế thì hãy tham khảo ngay laptop HP 240 G10 i5 1335U (9H2E4PT) từ thương hiệu HP nổi tiếng, mang đến cho người dùng những trải nghiệm sử dụng đầy đủ từ làm việc đến giải trí.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("775b35be-ba3a-4740-880e-514ab6cb8786"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Laptop học tập - văn phòng sử dụng bộ vi xử lý Intel Core i5 1335U tích hợp cùng card Intel UHD Graphics giúp mọi công việc soạn thảo, tính toán hay trình chiếu của bạn ở cơ quan hay trường học đều được hoàn thành hiệu quả nhất. Hơn nữa, người dùng cũng có thể gắn thêm 1 thanh RAM để kích hoạt card Iris Xe Graphics tối ưu hơn cho các tác vụ chỉnh sửa hình ảnh hay edit video ngắn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("2ad0644b-e675-461d-a537-d578baa2180b"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/21336686-d062-4211-9ff4-a30cdfcd056c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("6a3f87ec-1935-49aa-846a-5a206c73556b"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Bộ nhớ RAM 16 GB có khả năng nâng cấp cực kì ấn tượng lên đến 64 GB giúp mọi thao tác công việc trở nên thuận tiện hơn, mở nhiều tab cùng lúc mà không sợ bị đứng máy. Ổ cứng SSD 512 GB NVMe PCIe mang đến dung lượng lưu trữ vừa đủ cho nhiều tài liệu và thư mục của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("0d96aa89-0647-4d84-8ae6-430c925734db"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Màn hình laptop HP có kích thước 14 inch cùng độ phân giải Full HD (1920 x 1080), hỗ trợ hiển thị chi tiết và bắt mắt nhiều nội dung khác nhau, khung hình đủ rộng để bạn chia đôi cửa sổ cho quá trình làm việc thuận tiện hơn. Sử dụng thoải mái ở nhiều môi trường khác nhau từ ngoài trời, trong nhà mà không lo bị bóng gương gây chói mắt với công nghệ Anti Glare.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("898f0eb5-a281-4d97-bd78-bc49f49a4141"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c0f8271c-0d6c-4ea9-8cdc-4efa9068c1a5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("ec4ca8c1-3285-4f4f-8ea0-9fc1ac972a05"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Công nghệ âm thanh Realtek High Definition Audio tái tạo các dải âm trầm sống động, bắt tai, đắm chìm bạn sâu hơn trong những bản nhạc du dương hay những bộ phim yêu thích khi thư giãn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("89641c2d-7dc0-40a3-960c-068896996447"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Lớp vỏ nhựa sang trọng cùng tông màu bạc sáng sẽ khiến laptop của bạn luôn giữ được nét thẩm mỹ và chuyên nghiệp khi sử dụng ở bất kỳ không gian nào. Khối lượng linh động 1.36 kg, cùng độ nhỏ gọn cả về kích thước, thoải mái cho bạn mang theo suốt ngày dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("fdedfd2a-024d-403d-98c7-25f2658b7659"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/90386086-67e5-4884-a194-3c4b7c5bc633.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("649b4e14-5939-4078-8a27-81692334a719"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Linh động trong công việc với HD webcam, cho phép người dùng tham gia, làm việc trực tuyến mọi lúc, mọi nơi mà không cần có thiết bị hỗ trợ, bạn cũng có thể đóng mở thủ công dễ dàng với công tắc khoá camera để bảo vệ tốt cho hình ảnh và thông tin cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("1a335f42-0903-4fde-95b5-77a66dd524ec"), GadgetId = Guid.Parse("841aee0f-f32d-48f8-afbc-9452499d01e3"), Value = "Mẫu laptop HP cơ bản này cũng có đầy đủ các cổng kết nối thông dụng như: HDMI, Jack tai nghe 3.5 mm, USB 3.2 và USB Type-C để dễ dàng giao tiếp với các thiết bị bên ngoài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("b90ddfb0-1215-4dbf-a8f7-387ca78d86ce"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Asus cho ra mắt sản phẩm laptop Asus Vivobook S 16 OLED M5606WA R9 AI HX 370 (MX072WS) - mở ra một kỷ nguyên AI hoàn toàn mới, nâng tầm hiệu suất làm việc của bạn với chip AMD Ryzen AI 9 300 series cùng thiết kế linh hoạt, thanh lịch và màn hình 3.2K OLED ấn tượng. Đây chính là người bạn đồng hành hoàn hảo cho cuộc sống hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("5de83610-8ae6-4a44-9d34-833e328ecdac"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Cỗ máy laptop Asus Vivobook S 16 mang đến cho bạn sức mạnh tuyệt vời để chinh phục các tựa game hot nhất hiện nay, sáng tạo nội dung, render video, và thực hiện các dự án thiết kế 2D, 3D một cách hiệu quả. Điều này có được nhờ vi xử lý AMD Ryzen AI 9 HX 370 với 12 nhân 24 luồng, tốc độ tối đa lên đến 5.1 GHz. Ngoài ra, con chip này còn giúp tối ưu hóa năng lượng, kéo dài thời gian sử dụng pin cho bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("4911b989-bcde-4430-b511-c8f418954827"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Trải nghiệm của bạn sẽ được nâng lên một tầm cao mới với công nghệ ASUS Memory Allocation Management, giúp quản lý phân bổ bộ nhớ thông minh hơn. Điều này không chỉ giúp game chạy mượt mà hơn mà còn hỗ trợ tối ưu hóa quá trình biên tập đồ họa, cũng như cải thiện rõ rệt tốc độ xử lý hình ảnh AI. Ngoài ra, máy còn trang bị một phím copilot chuyên dụng giúp bạn truy cập các tính năng AI chỉ với một lượt nhấn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("81864b6d-c0e8-4f4e-861e-ec2e03a6d642"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/53ee956b-3270-4e06-95bd-334f9dbc9d0d.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("3969eeb0-eeaf-42b9-a68d-078212940b00"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Cùng với đó, card đồ họa AMD Radeon Graphics cho phép bạn trải nghiệm gaming sống động và hiệu suất AI vượt trội, đặc biệt là trong các tác vụ như kết xuất đồ họa, xử lý hình ảnh, mã hóa và giải mã video, game 3D và thậm chí cả quá trình đào tạo máy học. Những cải tiến này mang lại hiệu năng đột phá mà bạn có thể cảm nhận rõ rệt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("52dc1dd6-65c1-4c7c-b226-4e6a72bb48d4"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = " RAM 32 GB loại LPDDR5X với tốc độ bus ấn tượng 7500 MHz, mang đến hiệu năng xử lý vượt trội, cho phép bạn chạy mượt mà các ứng dụng nặng, đa nhiệm nhiều tác vụ cùng lúc mà không gặp phải tình trạng giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("ab4ffc20-6987-4f1e-8a0d-53414e03069c"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/20194f30-fdbb-4c4c-8257-590d5f5010a4.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("6b4769f4-b718-4f61-a254-fbe488dbca00"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Về lưu trữ, ổ cứng 1 TB SSD Gen 4 mang lại tốc độ truy xuất dữ liệu siêu nhanh, giúp khởi động máy và các ứng dụng chỉ trong tích tắc. Với dung lượng lưu trữ lớn, bạn có thể thoải mái lưu trữ các tệp tin, dự án, video hoặc dữ liệu quan trọng mà không lo thiếu không gian.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("12dc8da2-672a-4295-a139-658f4d350a0c"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Laptop Asus Vivobook sở hữu phong cách thời trang hiện đại, với logo tinh tế được khắc nổi bật trên mặt lưng, thể hiện sự tối giản và thanh lịch. Thiết kế mỏng nhẹ của máy với độ dày chỉ 13.9 mm và khối lượng 1.5 kg khiến cho việc mang theo khi di chuyển hoặc thay đổi môi trường làm việc trở nên vô cùng thuận tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("ac60764e-ec0f-4f27-8ed2-aed12e1c6c1a"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Về bảo mật, laptop được trang bị tính năng mở khóa bằng gương mặt, đảm bảo an toàn cho mọi thông tin cá nhân. Ngoài ra, công tắc khóa camera vật lý giúp bạn yên tâm ngăn chặn việc đánh cắp hình ảnh riêng tư và camera IR tích hợp còn cho phép chụp hình trong bóng tối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("2e2f19fa-c4c7-448f-97b4-c74f63fc3802"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a4fb5eea-bd24-4ae0-82b3-597d92bf0ee6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("c4e24445-4075-4904-a4ee-ce3e3c4aaccf"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Bàn phím của Asus Vivobook có đèn nền RGB, cho phép tùy chỉnh màu sắc theo tâm trạng và sở thích cá nhân, tạo nên những hiệu ứng ánh sáng ấn tượng. Touchpad thông minh với diện tích lớn hơn 40% so với phiên bản trước, không chỉ hỗ trợ điều khiển âm thanh, điều chỉnh độ sáng màn hình mà còn nhận diện cử chỉ một cách chính xác, mang đến trải nghiệm mượt mà và thú vị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("ccd435d3-df48-4221-b8cd-17d423152c96"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Về kết nối, máy được trang bị đầy đủ các cổng cần thiết như Jack tai nghe 3.5 mm, USB 3.2, HDMI, USB Type-C USB 4 và USB Type-C 3.2, cho phép bạn dễ dàng kết nối với màn hình ngoài, chuột, bàn phím, loa và nhiều thiết bị khác mà không gặp trở ngại nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("c93d8cf8-d42f-479c-89ec-6ffe8eab5465"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Trải nghiệm hình ảnh đỉnh cao trên chiếc laptop cao cấp với màn hình 16 inch độ phân giải 3.2K (3200 x 2000), mang đến hình ảnh sống động và sắc nét như thật. Công nghệ màn hình OLED kết hợp với chuẩn màu điện ảnh 100% DCI-P3 đảm bảo màu sắc chính xác và chân thực, đặc biệt là màu đen sâu thẳm, giúp bạn thấy rõ từng chi tiết ngay cả trong các cảnh tối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("32242da7-20d2-4aef-819b-620904e6d5f1"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Công nghệ Low Blue Light giảm thiểu ánh sáng xanh gây hại, giúp bảo vệ đôi mắt và cải thiện giấc ngủ, ngay cả khi bạn phải sử dụng máy tính trong thời gian dài. Tần số quét 120 Hz và thời gian phản hồi chỉ 0.2 ms giúp hiển thị hình ảnh chuyển động nhanh rõ ràng, mượt mà, loại bỏ hiện tượng bóng mờ, mang đến trải nghiệm thị giác hoàn hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("e7f12118-f71f-44b5-a836-50b64de9868b"), GadgetId = Guid.Parse("b2c97534-b915-479d-8dff-22e1eec14bba"), Value = "Âm thanh vượt trội nhờ công nghệ Smart AMP, Audio by Harman/Kardon và Dolby Atmos, cho âm thanh vang dội và sống động. Dù bạn đang nghe nhạc, xem phim hay tham gia các cuộc gọi trực tuyến, âm thanh luôn trong trẻo và rõ nét, mang đến trải nghiệm nghe nhìn tuyệt hảo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("26a59f5e-27a5-4ef4-b819-2c3014d3f6bd"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Bạn đang tìm kiếm cho mình một mẫu laptop học tập - văn phòng mang hiệu năng xử lý mạnh mẽ, khung hình hiển thị sắc nét cùng đa dạng các tính năng hiện đại. Laptop Asus Vivobook 15 OLED A1505VA i5 13500H (L1341W) là một trong những lựa chọn hàng đầu cho việc đáp ứng hoàn hảo nhu cầu công việc, học tập cũng như giải trí thường ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ce9bbc19-c67d-40c7-82f9-aa249a2e0e49"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Cấu hình vượt trội, đáp ứng đa dạng nhu cầu", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("c707d66d-e8c8-4eaa-937b-d445ed04d5e2"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Mặc dù là chiếc laptop văn phòng nhưng lại sở hữu con chip dòng H siêu mạnh mẽ, vượt trội hơn những sản phẩm cùng giá, với bộ vi xử lý Intel Core i5 13500H cùng card đồ họa Intel Iris Xe Graphics tạo thành “tổ hợp” giúp bạn vận hành trơn tru từ các tác vụ văn phòng, giải trí đến các công việc đồ họa, thiết kế hình ảnh trên Photoshop, Lightroom, SolidWorks,... hay chơi những tựa game thịnh hành trên thị trường hiện nay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("b4515563-5eae-4805-82fe-4dc6d7b32061"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a53d5134-b52e-4e1a-a726-befd1b50d3f1.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("18f6c09b-d766-47f2-b754-ad6a661478e7"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Điểm hiệu năng thu được khi sử dụng công cụ Cinebench R23 là 1507 điểm đơn nhân và 11098 điểm đa nhân. Đây chắc hẳn là một thông số tốt, có điểm đa nhân vượt trội hơn các con chip của laptop Asus dòng P hiện nay, thích hợp cho những anh em creator, xử lý hình ảnh, chơi game đều ổn định.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e6b9b8be-f5e1-42c1-9e35-ee7f7ecf65ca"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "RAM 16 GB là con số lớn, mang đến khả năng đa nhiệm cao, giải quyết công việc, render video, thiết kế đồ họa, cùng chơi game không gây giật lag, đơ màn hình. Mình có thử mở hơn 10 tab Chrome cùng lúc, vừa nghe nhạc vừa viết content, chuyển tab liên tục nhưng vẫn tạo cảm giác mượt mà, không xuất hiện tình trạng lag máy. Ngoài ra, bạn có thể nâng cấp RAM 24 GB để nâng cao tối đa các công việc sử dụng nhiều tài nguyên nặng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("412bb41f-7a72-466f-a4df-0280e3d78405"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Ổ cứng SSD 512 GB NVMe PCIe đem lại khả năng khởi động máy nhanh cùng mở các ứng dụng mượt mà. Với một người sinh viên, mình cảm thấy khá hài lòng vì dung lượng này thích hợp lưu trữ các tài liệu, bài luận, thuyết trình, Excel,...", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("e4271e21-fbbf-4c0b-a536-ce378c012f0d"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Về phần tản nhiệt, khi sử dụng các tác vụ thông thường như lướt web, viết content, xem video,... mình không nghe thấy tiếng quạt và máy cũng không nóng lên. Khi thiết kế hình ảnh, render video hay chơi game thì phần bàn phím có hơi nóng và xuất hiện tiếng ồn. Nhưng với một dòng laptop văn phòng chúng ta không thể đòi hỏi quá cao về khoản này, bạn hãy mua thêm đế tản nhiệt để hạn chế được tình trạng nóng máy khi sử dụng các tác vụ nặng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("aa9a7905-1240-4e35-af9c-d9f0ba0c5533"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Màn hình OLED mở ra chân trời thiết kế rực rỡ, sáng tạo", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("8e86ea2b-fff0-4daf-a7b8-f64d2d3adb33"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Một điểm cộng sáng giá mà mình muốn giới thiệu đến các bạn chính là chiếc màn hình OLED đạt chuẩn DisplayHDR True Black 600, sở hữu nhiều tính năng cao cấp. Khi xem thông số của chiếc máy này, mình đã rất ngạc nhiên vì với một mức giá “học sinh” như vậy nhưng màn hình lại có kích thước 15.6 inch OLED, độ phân giải Full HD (1920 x 1080) cùng độ sáng tối đa lên đến 600 nits cho ra hình ảnh sống động, đẹp mắt. Mình có thử dùng tính năng chia màn hình để vừa viết content vừa xem tài liệu tham khảo nhưng chữ vẫn nhìn rõ ràng, hình ảnh vẫn sắc nét, không bị quá nhỏ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
