new GadgetDescription { Id = Guid.Parse("ebf33449-f06b-4788-b7cb-9c9ff1e27c3a"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Thông qua bài test trên Spyder 5 Pro mình cũng không quá bất ngờ khi con máy này cho ra kết quả 100% sRGB, 100% Adobe RGB, 100% DCI-P3. Những thông số trên kết hợp với mức hiển thị 1.07 tỷ màu chắc chắn sẽ mang lại lợi thế cho các bạn thiết kế, chỉnh sửa đồ họa, xuất ra sản phẩm có độ chính xác cao về màu sắc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("dd2eed79-8624-4086-966e-5cb86425d80e"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Đa dạng nhiều tính năng bảo vệ mắt như EYE CARE và Low Blue Light chắc hẳn sẽ hỗ trợ hạn chế mỏi mắt trong những buổi deadline khuya hoặc mải mê chiến game với bạn bè.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("8e5cf0f3-7c06-40c9-a393-87510d227ad1"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Mang đến cho mình khả năng giải trí cực đã nhờ giữ độ mượt và trong cho âm thanh, loại bỏ hiệu quả các tạp âm, tăng cường trải nghiệm chơi game, nghe nhạc với công nghệ SonicMaster audio.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("12936257-9d01-47c4-9305-077e2c9c850b"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Ngoại hình gọn gàng, dễ dàng chinh phục mọi đối tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("1d1f6d1e-3dba-405d-9a17-8739d721d2ac"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Ngoại hình của laptop Asus mang đến cảm giác vừa hiện đại nhưng rất đơn giản. Lớp vỏ nhựa sở hữu màu đen thanh lịch, đạt chuẩn quân đội MIL STD 810H nên mình rất yên tâm nếu có vô tình làm máy va chạm nhẹ. Mang tính di động cao khi có khối lượng 1.7 kg và độ mỏng 19.9 mm, chắc hẳn sẽ thuận tiện bỏ vào balo đồng hành cùng bạn đi đến trường học, quán cafe để giải quyết công việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("18138d4f-7763-4cc1-ac53-ae3ffe25d685"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/24bd4a4d-809a-44d9-904c-5319d6c9c646.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 16 },
new GadgetDescription { Id = Guid.Parse("30470c03-06fa-47a5-92c3-9a61060cfc1a"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Do máy làm từ lớp vỏ nhựa nên dấu vân tay, mồ hôi sẽ dễ bám lên bề mặt, vì thế bạn hãy thủ sẵn một chiếc khăn để thường xuyên vệ sinh cho máy sạch sẽ. Nhằm khắc phục tình trạng trên, nhà Asus cũng cho thêm lớp phủ ASUS Antimicrobial Guard để kháng khuẩn các khu vực bạn thường xuyên tiếp xúc, hạn chế tình trạng ảnh hưởng sức khỏe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 17 },
new GadgetDescription { Id = Guid.Parse("b04cb82f-2330-48a8-b386-0a8ab00354b3"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Phần bản lề theo mình đánh giá là khá chắc chắn, khi gõ nhanh các phím thì bản lề cũng không rung lắc quá nhiều. Kết hợp cùng khả năng gập 180 độ không chỉ thuận tiện chia sẻ hình ảnh, nội dung đến người đối diện mà còn hạn chế việc gãy bản lề.", Type = ParseGadgetDescriptionType("NormalText"), Index = 18 },
new GadgetDescription { Id = Guid.Parse("c763f0b8-198b-4c47-834a-e85c31e7f1fe"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Bàn phím Full size có hành trình phím đủ sâu, mang đến cảm giác thoải mái khi gõ văn bản, không sợ mỏi tay. Trên mặt phím còn được phủ một lớp nhám nhằm mục đích hạn chế việc bám dấu vân tay. Một điểm đáng tiếc khi máy không trang bị đèn nền nên các bạn hãy sắm cho mình một chiếc đèn bàn hoặc làm việc ở những nơi có ánh sáng tốt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 19 },
new GadgetDescription { Id = Guid.Parse("4e2a1c73-5d4f-4c51-8590-7f20c5e7085a"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Phần TouchPad theo mình cảm nhận là khá to và sử dụng mượt mà, thích hợp sử dụng để mở web, nghe nhạc,... đều không cảm thấy khó chịu. Nhưng khi chỉnh sửa hình ảnh hoặc chơi game, mình nghĩ các bạn nên sắm một con chuột để thao tác được mượt mà, nhanh chóng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 20 },
new GadgetDescription { Id = Guid.Parse("d0194b4d-0c2f-466f-b804-e0093164f28a"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Ngoài ra, sở hữu tính năng bảo mật vân tay và công tắc khóa camera mang lại việc lưu trữ dữ liệu được an toàn và khả năng mở máy nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 21 },
new GadgetDescription { Id = Guid.Parse("85a89825-fa50-42f6-9ed9-8968930800aa"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Trang bị các cổng giao tiếp cho khả năng kết nối nhanh chóng, đáp ứng đủ nhu cầu sử dụng của mình: HDMI, USB 2.0, Jack tai nghe 3.5 mm, USB 3.2, USB Type-C.", Type = ParseGadgetDescriptionType("NormalText"), Index = 22 },
new GadgetDescription { Id = Guid.Parse("df3487d8-c595-46ce-a26e-cd7e1d48b5fa"), GadgetId = Guid.Parse("b5505553-0a95-453b-a685-6d098ddb20c6"), Value = "Phần pin theo mình đánh giá là chưa quá tốt khi có dung lượng 50 Wh. Khi sử dụng độ sáng tối đa cùng mở loa ngoài thì máy chỉ hoạt động khoảng 4 - 5 tiếng. Có thể dễ hiểu khi chiếc laptop Asus Vivobook mang chip dòng H sẽ “ngốn” nhiều pin, bù lại mang đến hiệu năng vượt trội để bạn xử lý tốt được các tác vụ nặng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 23 },
