new GadgetDescription { Id = Guid.Parse("da80d5dc-9749-4aea-bcb1-cf1f1a0fc66f"), GadgetId = Guid.Parse("08cfc997-083e-4be3-917b-9a1f04f1886a"), Value = "Trong những tháng cuối năm 2020, Apple đã chính thức giới thiệu đến người dùng cũng như iFan thế hệ iPhone 12 series mới với hàng loạt tính năng bứt phá, thiết kế được lột xác hoàn toàn, hiệu năng đầy mạnh mẽ và một trong số đó chính là iPhone 12 64GB.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
