new GadgetDescription { Id = Guid.Parse("3b4c64fc-def8-4f9e-9600-1d10409056af"), GadgetId = Guid.Parse("08cfc997-083e-4be3-917b-9a1f04f1886a"), Value = "Chưa hết, Apple còn gây bất ngờ đến người dùng với hệ thống 5G lần đầu tiên được trang bị trên những chiếc iPhone, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
