new GadgetDescription { Id = Guid.Parse("b1da7a14-61be-4c4a-9ca8-a4f3f85a695b"), GadgetId = Guid.Parse("22c5c3c3-3629-4c5c-9f29-346cffab76d9"), Value = "Cuối cùng thì iPhone 15 Pro cũng đã chính thức ra mắt vào tháng 09/2023 sau nhiều đồn đoán trong thời gian qua. Ở lần ra mắt này Apple mang đến một chiếc máy với vẻ ngoài cứng cáp đẹp mắt, một khả năng quay video chuyên nghiệp đi cùng bộ cấu hình cực khủng - hàng đầu phân khúc điện thoại hiện nay.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
