new GadgetDescription { Id = Guid.Parse("b5e4c012-681c-4774-b374-9966873b02ea"), GadgetId = Guid.Parse("4accc410-e79e-4f97-9e42-05ce82216810"), Value = "Sự quay trở lại đầy ấn tượng của siêu phẩm Samsung Galaxy Z Fold6, tiếp tục là biểu tượng cho sự đổi mới và sáng tạo của Samsung trong lĩnh vực điện thoại thông minh. Với những cải tiến vượt trội về mặt thiết kế, cấu hình cao hơn, tính năng 'xịn' hơn và vẫn giữ nguyên phong cách gập mở đầy ấn tượng cho việc đa nhiệm và xử lý công việc của người dùng hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
