new GadgetDescription { Id = Guid.Parse("9bcb94f0-21a2-4a2c-b706-b980b17541eb"), GadgetId = Guid.Parse("4accc410-e79e-4f97-9e42-05ce82216810"), Value = "Galaxy Z Fold6 không chỉ kế thừa thiết kế gập độc đáo từ các phiên bản trước mà còn được cải tiến đáng kể về độ mỏng và khối lượng tổng thể. Với độ mỏng 12.1 mm khi gập lại và 5.6 mm khi mở ra, cùng cân nặng 239g, Z Fold6 trở nên nhẹ nhàng và dễ dàng mang theo hơn bao giờ hết. Hơn nữa, kiểu dáng đổi mới này cũng khiến bề mặt trở nên gọn gàng, dễ dàng cầm nắm hơn rất nhiều.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
