new GadgetDescription { Id = Guid.Parse("0ed1558e-b79f-49ed-9dd9-f1d07427e99e"), GadgetId = Guid.Parse("4accc410-e79e-4f97-9e42-05ce82216810"), Value = "Với các tính năng AI này, Galaxy Z Fold6 không chỉ mạnh mẽ về phần cứng mà còn mang lại trải nghiệm tiện lợi và thông minh, giúp người dùng thực hiện các tác vụ hằng ngày hiệu quả với ít công sức được bỏ ra nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
