new GadgetDescription { Id = Guid.Parse("b1f244db-afb1-49b1-b2c0-465dea9025cb"), GadgetId = Guid.Parse("4accc410-e79e-4f97-9e42-05ce82216810"), Value = "Một điểm nổi bật dễ nhận thấy ở chiếc điện thoại Android của Samsung là màn hình Dynamic AMOLED 2X, có độ tương phản màu sắc cao và tái tạo hình ảnh chính xác, chân thực. Điều này mang đến trải nghiệm sử dụng và giải trí vô cùng hấp dẫn và thú vị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
