new GadgetDescription { Id = Guid.Parse("029f1842-716c-4a42-9c1f-3252d5f365e4"), GadgetId = Guid.Parse("3a8376e8-9520-4682-8983-80bfddaffa56"), Value = "Nhìn chung, lối thiết kế của Z Flip6 được “kế thừa” hầu như từ thế hệ trước với cơ chế màn hình gập độc đáo. Kích thước nhỏ gọn, đường nét máy được gia công phẳng tạo độ sang trọng, đồng thời mẫu sản phẩm còn sở hữu 4 tone màu cá tính mang lại nhiều lựa chọn cho người dùng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
