new GadgetDescription { Id = Guid.Parse("12dd0d0a-1635-4cab-b558-5be97dfbd4f4"), GadgetId = Guid.Parse("090a68a8-62ec-4c42-be55-de4cc7bb45c1"), Value = "Lần này Samsung đã mang viên pin 6000 mAh trang bị cho Galaxy M54 5G, đây là một con số khá lớn khi so sánh với các dòng điện thoại Android có mặt trên thị trường. Việc trang bị dung lượng pin lớn ở chiếc điện thoại này mang lại nhiều ưu điểm đáng kể cho trải nghiệm người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
