new GadgetDescription { Id = Guid.Parse("ca095b25-b8e1-4f3c-b274-fb709165fda6"), GadgetId = Guid.Parse("090a68a8-62ec-4c42-be55-de4cc7bb45c1"), Value = "Về phần hiển thị, điện thoại Samsung này trang bị tấm nền Super AMOLED Plus, tạo ra trải nghiệm xem vô cùng ấn tượng. Màu sắc trở nên rực rỡ và bắt mắt, độ tương phản cao giúp nội dung trở nên hoàn hảo và sống động hơn. Điều đặc biệt là ánh sáng xanh được giảm thiểu, giúp mắt không cảm thấy mệt mỏi khi sử dụng trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
