new GadgetDescription { Id = Guid.Parse("ca08d011-f7c8-4bf1-94c6-ce9f7bf8d39a"), GadgetId = Guid.Parse("090a68a8-62ec-4c42-be55-de4cc7bb45c1"), Value = "Ấn tượng của mình đối với Galaxy M54 5G là ngoại hình cực kỳ sang trọng, máy được gia công hoàn chỉnh như được làm bằng kính. Tuy nhiên, thực tế mặt lưng của máy được làm từ nhựa, tạo ra một sự cuốn hút đặc biệt. Khi cầm trên tay, chiếc điện thoại không chỉ giúp người sử dụng nổi bật hơn trong đám đông, mà còn mang lại trải nghiệm thú vị và đẳng cấp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("81683e48-f7a0-4475-b3e9-6aa1ac446485"), GadgetId = Guid.Parse("b5efbecd-5cc3-4dce-86f7-60c8f44226f8"), Value = "Samsung tiếp tục chiếm lĩnh thị trường điện thoại thông minh với sự xuất hiện của Samsung Galaxy S23 FE 5G. Điện thoại này được trang bị camera 50 MP có khả năng quay video 8K, màn hình 120 Hz. Với những tính năng này, hứa hẹn sẽ mang đến trải nghiệm tuyệt vời, đáp ứng mọi nhu cầu của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
