new GadgetDescription { Id = Guid.Parse("e777a912-ea17-4acf-b74a-d096dd69f031"), GadgetId = Guid.Parse("b5efbecd-5cc3-4dce-86f7-60c8f44226f8"), Value = "Các tính năng đáng chú ý bao gồm Trợ lý Note thông minh cho phép người dùng ghi nhớ mọi điều một cách dễ dàng và hiệu quả. Đồng thời, có Trợ lý chỉnh ảnh cung cấp sự cải tiến trong việc chỉnh sửa và tối ưu hóa hình ảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
