new GadgetDescription { Id = Guid.Parse("0f7f365d-8967-43c3-ba12-2cf72af93ce9"), GadgetId = Guid.Parse("b5efbecd-5cc3-4dce-86f7-60c8f44226f8"), Value = "Màn hình lớn hỗ trợ tấm nền Dynamic AMOLED 2X", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("e517c684-bffc-424a-90f4-207e600ce03b"), GadgetId = Guid.Parse("b5efbecd-5cc3-4dce-86f7-60c8f44226f8"), Value = "Điểm đặc biệt mà mình ưa thích trên màn hình là tấm nền Dynamic AMOLED 2X với khả năng hiển thị màu sắc và độ sáng. Màn hình này sử dụng điểm ảnh tự phát sáng độc lập, cho phép tắt hoàn toàn khi không cần sáng, làm cho màu đen trở nên sâu hơn. Công nghệ này không chỉ tăng cường độ tương phản mà còn tiết kiệm năng lượng, điều mà mình thấy rất ấn tượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
