new GadgetDescription { Id = Guid.Parse("c2b3c344-df94-4bc2-b8f1-bd42d9b5484f"), GadgetId = Guid.Parse("695943b6-eae5-4b33-940c-d832a471f93c"), Value = "Về mặt cấu hình, chiếc điện thoại cấu hình cao này tích hợp con chip Snapdragon 8 Gen 3 for Galaxy đến từ nhà Qualcomm. Không chỉ mạnh mẽ để phục vụ chơi game cấu hình cao như trước đây, năm nay Samsung còn khai thác nó để xử lý các tác vụ liên quan đến AI trở nên nhanh chóng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
