new GadgetDescription { Id = Guid.Parse("7b9ddd63-3150-40fd-8b0c-f64aff58686c"), GadgetId = Guid.Parse("695943b6-eae5-4b33-940c-d832a471f93c"), Value = "Galaxy S24 Ultra được hỗ trợ nhiều tính năng có tích hợp sẵn AI, các công nghệ này được đề cập khá nhiều tại sự kiện ra mắt sản phẩm mới vừa qua của Samsung. Theo sau đó, cụm từ AI bỗng trở nên sốt hơn bao giờ hết và vô tình tạo làn sóng công nghệ trên các thiết bị di động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("7d840f96-9291-4508-a1cb-54daec21c71b"), GadgetId = Guid.Parse("695943b6-eae5-4b33-940c-d832a471f93c"), Value = "Với sự giúp sức từ AI, giờ đây mình có thể thực hiện việc xóa các chủ thể không cần thiết hay thậm chí là di chuyển đối tượng một cách nhanh chóng và chính xác. Đặc biệt, tốc độ xử lý của điện thoại cho ra khá nhanh, những vùng trống sau khi xử lý cũng được lấp đầy bằng những chi tiết khá giống với vùng lân cận. Bởi vậy mà kết quả làm mình rất ưng và cực kỳ thích.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("bae4138a-8359-4eba-802a-2cf89496f98a"), GadgetId = Guid.Parse("695943b6-eae5-4b33-940c-d832a471f93c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/57313d3c-c178-4491-b42f-9719098e29f3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 14 },
new GadgetDescription { Id = Guid.Parse("e945f436-4d25-4367-a9bd-538309b920f7"), GadgetId = Guid.Parse("d8c425c7-6b76-4519-b8c9-bb12e1bd216c"), Value = "Giữa thời đại công nghệ, việc sở hữu một chiếc điện thoại thông minh không chỉ là phương tiện liên lạc mà còn là công cụ hỗ trợ công việc, giải trí và ghi lại khoảnh khắc đáng nhớ. Với thiết kế tinh tế, hiệu năng vượt trội, Samsung Galaxy M55 5G 12GB là sự lựa chọn lý tưởng cho những ai tìm kiếm một thiết bị đa năng, hiện đại.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
