new GadgetDescription { Id = Guid.Parse("5485701e-5b68-464e-8d6d-ac26bbe37240"), GadgetId = Guid.Parse("d8c425c7-6b76-4519-b8c9-bb12e1bd216c"), Value = "Với hệ thống camera ấn tượng, Galaxy M55 5G giúp bạn bắt trọn mọi khoảnh khắc. Camera chính 50 MP đi kèm công nghệ chống rung quang học OIS, cùng với camera góc siêu rộng 8 MP, camera cận cảnh 2 MP và camera selfie 50 MP. Công nghệ Nightography và Super HDR giúp bạn chụp ảnh và quay video sắc nét, sống động trong mọi điều kiện ánh sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
