new GadgetDescription { Id = Guid.Parse("3a09de7b-670b-4953-81ad-f524ba929b0e"), GadgetId = Guid.Parse("d8c425c7-6b76-4519-b8c9-bb12e1bd216c"), Value = "Chiếc điện thoại Samsung này sở hữu màn hình Super AMOLED Plus 6.7 inch, đem lại trải nghiệm thị giác tuyệt vời với độ phân giải Full HD+. Màn hình này hiển thị màu sắc rực rỡ và chi tiết sắc nét, rất phù hợp cho việc xem phim, chơi game và lướt web.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("07d75872-1a4c-4016-a688-d7469d8bf90c"), GadgetId = Guid.Parse("d8c425c7-6b76-4519-b8c9-bb12e1bd216c"), Value = "Thời lượng pin bền bỉ cùng sạc nhanh 45W", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("13b5576d-1032-4b54-8fb7-a1fb2dc7cf02"), GadgetId = Guid.Parse("d8c425c7-6b76-4519-b8c9-bb12e1bd216c"), Value = "Galaxy M55 5G được trang bị viên pin 5000 mAh, đủ dùng cả ngày mà không lo hết pin. Với sạc siêu nhanh 45W, bạn có thể nhanh chóng nạp lại năng lượng, tiết kiệm thời gian đáng kể. Sự kết hợp giữa pin dung lượng lớn và công nghệ sạc nhanh giúp bạn làm việc và giải trí không gián đoạn, đảm bảo thiết bị luôn hoạt động ở trạng thái tốt nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("0514c85b-a618-4d4a-b1d7-cb5509ddfd87"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Samsung Galaxy A06 4GB/64GB mang đến sự kết hợp hoàn hảo giữa thiết kế tinh tế, hiệu năng mạnh mẽ và bảo mật cao cấp. Với màn hình 6.7 inch, camera 50 MP, pin bền bỉ cùng nhiều cải tiến vượt trội, Galaxy A06 là lựa chọn lý tưởng cho mọi nhu cầu sử dụng hằng ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
