new GadgetDescription { Id = Guid.Parse("7a707010-c9c7-4aeb-86fb-9a9996308bd8"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Họa tiết sọc dọc trên mặt lưng Galaxy A06 vừa tăng tính thẩm mỹ vừa hạn chế bám vân tay hiệu quả, giữ cho điện thoại trông luôn sạch sẽ, bóng bẩy, không bị dính dấu tay khó chịu sau khi sử dụng lâu. Galaxy A06 mang đến nhiều tùy chọn màu sắc khác nhau, điều này phù hợp với sở thích và cá tính của nhiều đối tượng người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
