new GadgetDescription { Id = Guid.Parse("90b5453a-2f27-4042-9054-be98711efff1"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Về camera, Galaxy A06 trang bị camera chính 50 MP cho phép chụp ảnh với độ phân giải cao, chi tiết rõ nét ngay cả khi phóng to hoặc cắt ảnh. Camera 2 MP hỗ trợ xoá phông, tạo hiệu ứng nền mờ, giúp hình ảnh chân dung trở nên ấn tượng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("ed0171fa-959f-46cd-96b1-0f40d810fd63"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Hiệu năng mượt mà, thao tác nhanh chóng", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("593994ed-26eb-48cf-8967-a038dd35ea4d"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Samsung Galaxy A06 trang bị chip MediaTek G85, một vi xử lý tầm trung phù hợp với các nhu cầu sử dụng phổ thông và thậm chí là chơi game nhẹ. Bộ xử lý này giúp các ứng dụng vận hành trơn tru và ổn định. Với dung lượng RAM 4 GB, Galaxy A06 có khả năng đa nhiệm tốt, cho phép người dùng dễ dàng chuyển đổi giữa nhiều ứng dụng mà không gặp hiện tượng giật lag.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
