new GadgetDescription { Id = Guid.Parse("56587942-9d40-4b01-aadd-41710dae3ad8"), GadgetId = Guid.Parse("fb19b8ec-a4ef-4075-bd14-d465530a32c0"), Value = "Trang bị viên pin 5000 mAh, chiếc điện thoại Android này cho phép người dùng sử dụng thoải mái trong suốt ngày dài mà không phải lo lắng về việc hết pin. Pin dung lượng lớn này đặc biệt hữu ích khi bạn liên tục sử dụng các ứng dụng nặng như chơi game, xem video. Đặc biệt, với khả năng sạc nhanh 25W cũng là một lợi thế lớn của Galaxy A06, giúp bạn tiết kiệm thời gian sạc và nhanh chóng trở lại với các hoạt động giải trí yêu thích.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
