new GadgetDescription { Id = Guid.Parse("ade74abe-eac1-4218-a8d8-60454c91e0a2"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Trong sự kiện Unpacked 2024 diễn ra vào ngày 18/01, Samsung đã chính thức ra mắt chiếc điện thoại Samsung Galaxy S24. Sản phẩm này mang đến nhiều cải tiến độc đáo, bao gồm việc sử dụng chip mới của Samsung, tích hợp nhiều tính năng thông minh sử dụng trí tuệ nhân tạo và cải thiện đáng kể hiệu suất chụp ảnh từ hệ thống camera.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4d13e6ba-8cce-4760-8476-7582f7a16e34"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Thiết kế vuông hơn, thời thượng hơn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8b357b2e-50f3-42bb-8be7-75432c3a924d"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Về phần thiết kế, Samsung vẫn tiếp tục sử dụng kiểu dáng vuông vức và cách bố trí cụm camera quen thuộc so với Samsung Galaxy S23. Đáng khen là hãng có ghi nhận những góp ý từ đời trước nên cũng đã tối ưu nhẹ ở một vài điểm như: Các góc làm vuông hơn, viền màn hình mỏng hơn và cuối cùng là dải loa được làm theo dạng rảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
