new GadgetDescription { Id = Guid.Parse("c351f18d-6098-4850-bc16-ffc42fe967c9"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Về thông số máy ảnh, chiếc điện thoại Galaxy dòng S này được trang bị bộ 3 camera với thông số như sau: Camera chính 50 MP, camera góc siêu rộng 12 MP và camera tele 10 MP. Năm nay, hãng không có sự nâng cấp gì về độ phân giải, nhưng đổi lại Samsung tích hợp khá nhiều tính năng có liên quan đến AI cho điện thoại nhằm hỗ trợ cho việc chụp và hậu kỳ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
