new GadgetDescription { Id = Guid.Parse("22e2705b-267b-4edd-b2d2-37fc59687f1d"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Nâng cao trải nghiệm với tấm nền Dynamic AMOLED 2X", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("e9cd1766-8f01-42d4-847b-8637c3e9141c"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Màn hình của điện thoại được hỗ trợ tấm nền Dynamic AMOLED 2X, có độ phân giải Full HD+ (1080 x 2340 Pixels) và vẫn duy trì tần số quét màn hình 120 Hz. Có thể nói những điểm này không khác biệt gì so với Galaxy S23, vì thế mọi trải nghiệm vẫn tốt như ở trên phiên bản cũ và nó làm mình khá hài lòng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("b899f954-a422-4fd2-90c6-db670de7f111"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Cấu hình mạnh mẽ với chip nhà Exynos 2400", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("10ea1097-2b54-4739-87f5-6057833c9585"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Nói về sự nâng cấp và thay đổi, có lẽ hiệu năng là thứ làm mình ấn tượng nhất trên chiếc Galaxy S24. Năm nay, hãng quyết định đưa vào điện thoại một con chip tự sản xuất mang tên gọi Exynos 2400, thay vì sử dụng chip của Qualcomm như truyền thống. Ấn tượng hơn nữa là chip được trang bị 10 nhân chứ không phải 8 nhân như nhiều dòng chip trên thị trường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("2b60fece-3235-40db-82fc-6c6d9e74f4f0"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Tích hợp pin 4000 mAh đi cùng sạc nhanh 25 W", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("8a2816a4-c4a7-4ac5-b4dd-bc3498222e85"), GadgetId = Guid.Parse("4dec5a84-f235-454e-8ce0-a8e486654fe7"), Value = "Galaxy S24 là nguồn năng lượng di động đáng tin cậy với viên pin lớn 4000 mAh. Điều này đảm bảo người dùng có đủ năng lượng để sử dụng điện thoại một cách thoải mái suốt cả ngày, từ việc gọi điện thoại, xem video, đến sử dụng các ứng dụng và trò chơi đòi hỏi nhiều năng lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("34ffa08b-9600-48eb-902c-f9e066a52e7a"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Samsung đã cho ra mắt Samsung Galaxy S24+ 5G 256GB, chiếc điện thoại đẳng cấp của họ tại sự kiện hàng năm diễn ra vào ngày 18/01 tại Mỹ. Điểm độc đáo của sản phẩm nằm ở chip mới của Samsung, đi kèm với sự phát triển trong việc bổ sung nhiều tính năng thông minh có tích hợp AI và tăng cường khả năng chụp ảnh ở phần camera.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("6e3b7cd6-5737-46e4-b3bc-4315d121f3e1"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Thiết kế vuông hơn, màn hình mỏng hơn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("f0317e07-54ca-472a-8ce0-28bfd1dcdd79"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Đầu tiên, mình thích cách thiết kế của máy với vẻ ngoại hình vuông vức và mặt lưng phẳng, giữ nguyên phong cách từ các phiên bản trước đó. Tuy nhiên, năm nay hãng có một thay đổi nhỏ đó là làm bốn góc có hơi vuông hơn Samsung Galaxy S23+, tạo nên một tổng thể vuông vắn cho ra cái nhìn sang trọng và cực kỳ bắt trend.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("d2281c00-f02d-400a-8626-c9cf5f2e2bb6"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ddd7db12-59f3-4931-b12c-c34a4002e2b5.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d491b20e-9386-4530-bf34-51a029c50489"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Tăng cường độ chi tiết với camera 50 MP", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("60ecaf47-97f3-4640-b9dc-2a130b7ea12c"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Sau khi trải nghiệm 3 - 4 ngày sử dụng chiếc Galaxy S24+, mình thực sự ấn tượng với khả năng chụp ảnh của máy. Camera chính lên đến 50 MP với khẩu độ f/1.8 không chỉ mang lại độ phân giải cao mà còn tạo ra những bức ảnh sắc nét và chi tiết. Đây thực sự là thông số có thể làm mê mẩn bất kỳ người đam mê nhiếp ảnh nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("3988dd25-e363-433c-9bab-e095d834ae8a"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c169a2f2-a5e0-4a25-9eb2-616c6e4a95bb.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("0bae454f-d187-49f1-a92c-bd9ff7322c61"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Màn hình Dynamic AMOLED 2X cho trải nghiệm sống động", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("17dbfe75-770e-426b-a77a-28794daf5b4c"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Khi sử dụng Galaxy S24+, mình rất ấn tượng với màn hình Dynamic AMOLED 2X của máy. Hiển thị màu sắc sắc nét và độ tương phản cao tạo nên trải nghiệm sống động và chân thực. Việc xem nội dung đa phương tiện và chơi game trên điện thoại trở nên khác biệt và độc đáo, đặc biệt so với các sản phẩm khác trong phân khúc giá tương đương.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("d47a25d6-73ef-4ad9-8674-e51cbe8cee55"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Sử dụng chip nhà làm mang tên Exynos 2400", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9f6c9084-025a-4b09-a4a9-70d696817a45"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Chiếc điện thoại Samsung này đã thực sự tạo được ấn tượng với những thay đổi mà họ mang lại. Lần này, họ quyết định sử dụng chip sản xuất in-house của mình, Exynos 2400, thay vì lựa chọn chip Snapdragon như trước đây. Điều này là một bước tiến lớn, khiến chiếc điện thoại trở thành biểu tượng của sự đổi mới và sáng tạo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e62bc3f2-ab10-4389-891d-e49c7e429bfc"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Pin 4900 mAh đi cùng khả năng sạc 45 W", Type = ParseGadgetDescriptionType("BoldText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e1a3c5c3-d5ce-4224-a66e-2d5a5301b11f"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "Chiếc điện thoại Samsung này sở hữu viên pin có dung lượng lên đến 4900 mAh, đây là một bước tiến đột phá về khả năng giữ năng lượng so với những mô hình trước đó. Đặc biệt, với sự tối ưu hóa từ chip Exynos 2400, viên pin này hứa hẹn mang lại hiệu suất vô cùng hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("99e7ddfa-aa8e-47f2-84ea-1c51319dca04"), GadgetId = Guid.Parse("e43b31b1-d536-41cc-84bd-d9a9a19549e9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/73aede4d-ee56-41e5-b2b6-57e2ac5ded5e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("d2d3e170-6910-40c9-8bae-ca5851effe3d"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Samsung Galaxy A05s 6GB được cho ra mắt tại thị trường Việt Nam vào hồi tháng 09/2023. Máy gây chú ý nhờ sở hữu mức giá bán hợp lý, cấu hình ổn với chip Snapdragon 680 8 nhân và hệ thống camera chất lượng đến 50 MP.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
