new GadgetDescription { Id = Guid.Parse("b9ab5309-56de-406b-acf6-a48ef5fe2042"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Galaxy A05s 6GB lấy cảm hứng thiết kế từ các phiên bản flagship như Galaxy S23, với một diện mạo vuông vắn và tinh tế. Cụm camera được đặt theo kiểu của các phiên bản cao cấp, tạo nên sự gọn gàng và bố cục hài hòa ở mặt lưng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
