new GadgetDescription { Id = Guid.Parse("fea63c63-c949-4353-ad39-f12d087172d1"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Trang bị màn hình kích thước lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("169496e5-b8b9-4baa-889b-65bb2b131476"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Galaxy A05s 6GB trang bị một màn hình PLS LCD, đạt độ phân giải Full HD+ (1080 x 2400 Pixels), mang đến chất lượng hình ảnh tuyệt vời. Mọi nội dung hiển thị trên màn hình được tái hiện rõ nét, màu sắc chân thực và có màu đen sâu, tạo ra trải nghiệm sống động và thư giãn khi xem phim, video và hình ảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("9da76a23-2384-40a7-85af-16668600648a"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Hiệu năng tốt trong phân khúc", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("a0575e0b-0ab8-47c9-b101-d8e456977918"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Galaxy A05s 6GB trang bị vi xử lý Snapdragon 680, đây là dòng chip giá rẻ có hiệu năng tốt được sản xuất từ nhà Qualcomm. Điều này đồng nghĩa với việc nó sẽ xử lý tốt các tác vụ cơ bản và hơn thế nữa. Từ việc duyệt web, gửi email, xem video đến cả việc sử dụng ứng dụng cho công việc và giải trí, Snapdragon 680 đảm bảo hiệu suất mượt mà và nhanh chóng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("c02097f3-c0a2-4d24-87c7-cd079247a05b"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/ae2c84b5-c037-4771-9fbe-65244113612b.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("2f57ef46-5f93-40b0-8e58-aca5f0f01f9e"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Nhiếp ảnh đỉnh cao với camera 50 MP", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("4045ad97-5e49-44fc-9698-f2fff4092402"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Ở phần mặt sau, chiếc điện thoại trang bị hệ thống camera bao gồm camera chính 50 MP và hai camera phụ 2 MP. Điều này cùng với việc tích hợp nhiều tính năng tiên tiến, giúp tối ưu hóa hình ảnh chụp và mang lại sự hấp dẫn cho những bức ảnh.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
