new GadgetDescription { Id = Guid.Parse("059fbf2a-bb65-4558-911e-29b2d7586c64"), GadgetId = Guid.Parse("7c60db06-7257-473c-99bd-afe5b61e602c"), Value = "Galaxy A05s 6GB sở hữu viên pin có dung lượng lớn lên đến 5000 mAh. Nhờ mức dung lượng lớn này mà bạn có thể sử dụng điện thoại suốt cả ngày và không cần phải lo lắng về việc sạc pin thường xuyên. Viên pin khủng đảm bảo rằng bạn có đủ năng lượng để thực hiện mọi tác vụ, từ lướt web đến chơi game một cách dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("96ff0c51-64be-4bdd-a348-f15c2bb58b6a"), GadgetId = Guid.Parse("4f5d3758-9160-4c12-94e8-a4bc7d39290d"), Value = "OPPO chính thức giới thiệu sản phẩm OPPO Find N3 5G, một chiếc điện thoại thông minh với thiết kế gập ngang hoàn toàn hiện đại và đẳng cấp, nổi bật với camera và hiệu năng đứng đầu. Đây là một bước đột phá của OPPO trong việc kết hợp sự tiện ích và thẩm mỹ tối ưu, đem lại trải nghiệm độc đáo cho người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("70cb8fb1-1f84-44ac-a092-5c7c2309ede5"), GadgetId = Guid.Parse("4f5d3758-9160-4c12-94e8-a4bc7d39290d"), Value = "Thiết kế với cơ chế gấp gọn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("8314409a-8bff-4970-b34d-86231970ff4d"), GadgetId = Guid.Parse("4f5d3758-9160-4c12-94e8-a4bc7d39290d"), Value = "Một trong những điểm đáng chú ý nhất của OPPO Find N3 chính là thiết kế gập ngang. Điều này không chỉ mang lại vẻ đẹp thời thượng và sang trọng, mà còn giúp thay đổi kích thước màn hình linh hoạt. Với khả năng gập màn hình từ một thiết bị di động thành một máy tính bảng mini, OPPO Find N3 cho phép bạn tận hưởng nhiều trải nghiệm sáng tạo khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
