new GadgetDescription { Id = Guid.Parse("5f8f4164-a52f-4c0e-a109-48f28e4993b3"), GadgetId = Guid.Parse("4f5d3758-9160-4c12-94e8-a4bc7d39290d"), Value = "OPPO Find N3 đã đánh dấu một bước đột phá đầy ấn tượng trong ngành công nghệ di động với thiết kế độc đáo và một cặp màn hình vượt trội. Chiếc điện thoại này không chỉ thu hút người dùng bởi thiết kế đẹp mắt mà còn bởi màn hình nổi bật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
