new GadgetDescription { Id = Guid.Parse("36857391-065e-4de5-802d-e198bddaaddb"), GadgetId = Guid.Parse("2c3f30b3-316b-4460-a834-69844ce63f2d"), Value = "Camera sau 50 MP với những thuật toán thông minh giúp cho ảnh sắc nét, kể cả khi thiếu sáng. Máy hỗ trợ chế độ làm đẹp tự nhiên, giúp điều chỉnh nhẹ nhàng khuôn mặt, tăng sự tự tin cho bạn. Bên cạnh đó, tính năng quay video hiển thị kép mở ra góc nhìn đa chiều và sáng tạo mới.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("326e21c8-5b10-4453-bc47-a2b216546478"), GadgetId = Guid.Parse("2c3f30b3-316b-4460-a834-69844ce63f2d"), Value = "Pin bền bỉ và sạc nhanh giữ cho bạn hoạt động suốt ngày dài", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
