new GadgetDescription { Id = Guid.Parse("50366acb-6e9f-4981-ab27-79f1d0fba946"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "OPPO A18 - một trong những sản phẩm điện thoại giá rẻ được OPPO giới thiệu tại thị trường Việt Nam trong những tháng cuối năm 2023. Thiết kế của máy vẫn giữ nguyên phong cách quen thuộc như các sản phẩm điện thoại OPPO A, đi kèm với đó là một màn hình sắc nét cùng một hiệu năng ổn định.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
