new GadgetDescription { Id = Guid.Parse("a7867816-9116-43e8-8e8e-904cd921edc0"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "Điện thoại OPPO này sử dụng chip Helio G85 thực sự là một sự kết hợp mạnh mẽ giữa hiệu năng và tiết kiệm điện. Chipset này cung cấp sức mạnh xử lý đủ mạnh mẽ để đáp ứng mọi nhu cầu của mình, từ việc xem video, chơi game đến làm việc với các ứng dụng đa nhiệm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
