new GadgetDescription { Id = Guid.Parse("cad37814-cc79-419b-ac50-ee84b84c8e05"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "Camera trên OPPO A18 mang lại cho mình một trải nghiệm chụp ảnh đa dạng và thú vị. Với camera chính 8 MP, mình đã có cơ hội ghi lại những khoảnh khắc đáng nhớ trong cuộc sống hằng ngày. Dù là cảnh quan tự nhiên hay chụp ảnh trong nhà, máy đều cho ra những bức ảnh khá rõ nét, với màu sắc tự nhiên và cân đối.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("6c1b8a6c-d3f0-4dd6-9a8a-7801109c4ed0"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "Dùng lâu với viên pin lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("563ce22d-b96f-4aae-b1fa-326cdf4f506c"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "Không chỉ tạo ấn tượng về hiệu năng và thiết kế, một ưu điểm khác của máy là thời lượng pin. Với dung lượng pin lên đến 5000 mAh và khả năng hỗ trợ sạc 10 W, giúp bạn có thể sử dụng máy liên tục cả ngày mà không phải bận tâm nhiều về việc sạc pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("f223d731-0cd1-46bc-98ce-d77c7d9bf5b4"), GadgetId = Guid.Parse("e1fa7b98-9c96-4e44-bcf3-2f5c0bdd373d"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/381e3f58-5885-4c32-b80b-5f2962985dd3.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("30ca1318-45d9-4268-86f2-1cc14f53ac84"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "OPPO Reno12 là chiếc điện thoại mang đến sự kết hợp hoàn hảo giữa thiết kế trẻ trung, hiện đại có tích hợp AI thông minh và hiệu năng mạnh mẽ. Reno12 chắc chắn sẽ là lựa chọn hấp dẫn cho người dùng yêu thích sự mới mẻ và trải nghiệm công nghệ tiên tiến.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("0f9ff905-9e45-4b3b-ba8e-4ac8d754405f"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "Màn hình hiển thị sống động, mượt mà", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("0318073f-b6d9-4a08-8e7b-b66bdd98fa2e"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "OPPO Reno12 được trang bị màn hình AMOLED tiên tiến, mang lại chất lượng hiển thị xuất sắc. Màn hình này cho phép hiển thị màu đen sâu và tạo nên màu sắc sống động. Màu sắc trên màn hình AMOLED rất rực rỡ và chính xác, giúp hình ảnh trở nên sắc nét hơn bao giờ hết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("be2d3abd-b10e-4fab-bf97-4681450e4e7c"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "Camera AI, nâng cao khả năng chụp ảnh", Type = ParseGadgetDescriptionType("BoldText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("b929e441-455e-4184-adfd-32f2757396f1"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "Chiếc điện thoại Android này có hệ thống camera siêu rõ nét với công nghệ AI tiên tiến. Camera chính 50 MP với công nghệ ổn định hình ảnh quang học (OIS) giúp giảm rung lắc, mang lại ảnh và video rõ nét, mượt mà ngay cả trong điều kiện ánh sáng yếu. Độ phân giải cao cho phép chụp ảnh chi tiết, sắc nét và cắt xén mà không giảm chất lượng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("532d7a55-8c5f-4351-a4c4-69e17c25296c"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/4cdb8593-78d3-48d2-b969-646042d7c5e6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("95cec658-31a9-4d16-9469-b7afd593d05c"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "Tính năng tách nền AI giúp nhanh chóng cắt người hoặc thú cưng khỏi ảnh, sau đó thêm biểu tượng cảm xúc, nhãn dán, hình nền và hơn thế nữa ngay trong ứng dụng. AI Studio cho phép tạo ra những hình ảnh ấn tượng bằng cách thêm nền, thay đổi mái tóc hoặc mời bạn bè chụp ảnh nhóm, mang lại sự sáng tạo và cá nhân hóa cho bức ảnh của bạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("1748894c-5434-4e25-ba33-93a1a14e4689"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "Hiệu năng mượt mà, tiết kiệm điện năng", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
