new GadgetDescription { Id = Guid.Parse("789f0fdd-4fda-4191-9ff1-e04c049493ed"), GadgetId = Guid.Parse("e73ef35e-fe25-47bc-8954-6c921aeea4e9"), Value = "OPPO Reno12 trang bị pin dung lượng lớn 5000 mAh, mang lại trải nghiệm sử dụng liên tục và tiện lợi cho người dùng. Pin 5000 mAh đảm bảo rằng người dùng có thể sử dụng điện thoại suốt cả ngày mà không cần phải lo lắng về việc sạc pin thường xuyên. Điều này đặc biệt quan trọng đối với những người có nhu cầu sử dụng điện thoại nhiều, từ việc gọi điện, nhắn tin, đến xem video, chơi game và làm việc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
