new GadgetDescription { Id = Guid.Parse("2a3114a8-8246-4186-b42a-fd7fd093901f"), GadgetId = Guid.Parse("1e74ab92-da32-4683-b893-3f7cac0eebea"), Value = "Ngoài ra, trí tuệ nhân tạo trên máy còn hỗ trợ người dùng tách nền như người, vật thể hay thú cưng ra khỏi khung để tạo nhãn dán hoặc biểu tượng cảm xúc, mang đến trải nghiệm sáng tạo và thú vị. Thêm nữa, tính năng AI Studio sẽ biến trí tưởng tượng của bạn thành hiện thực, với khả năng linh hoạt biến đổi các tấm hình thành các chủ đề khác nhau, tạo nên những bức ảnh độc đáo cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
