new GadgetDescription { Id = Guid.Parse("0bbbf27c-19ed-4cbf-803b-2787573aa52e"), GadgetId = Guid.Parse("1e74ab92-da32-4683-b893-3f7cac0eebea"), Value = "Với khả năng sạc siêu nhanh SuperVOOC 80W, thiết bị không chỉ sạc pin nhanh chóng mà còn giữ nhiệt độ bên trong máy ở mức thấp, ngay cả khi bạn đang chơi game hoặc xem video. Điều này mang đến hiệu suất bền bỉ, giúp bạn tận hưởng những giây phút giải trí và làm việc mượt mà, không bị gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("b6c2633e-d180-4c7d-81bd-7276abd01041"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Vào tháng 01/2024, Xiaomi Redmi Note 13 chính thức ra mắt tại Việt Nam, đánh dấu sự tiếp nối của dòng sản phẩm Redmi Note nổi tiếng. Điện thoại được trang bị cấu hình mạnh mẽ, thiết kế hiện đại, màn hình sắc nét và camera ấn tượng, hứa hẹn sẽ thu hút được sự quan tâm của người dùng Việt.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("87bf8a86-a5e8-4717-82be-747a0402d7fb"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Màn hình AMOLED cho chất lượng hiển thị sắc nét", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("354fec33-fe72-4451-a142-fa132436f0ca"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Màn hình AMOLED là một điểm độc đáo của Redmi Note 13 mà mình thực sự rất thích. Nó có khả năng hiển thị màu sắc sống động và đặc biệt là khả năng tạo ra độ tương phản cao và màu đen sâu, làm cho mọi hình ảnh trở nên sắc nét và chân thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
